                  <DesktopDatePicker
                    inputFormat="dd/MM/yyyy"
                    // value={form?.ngay_cap}
                    onChange={selectedNCCMND}
                    label=" "
                    maxDate={new Date()}
                    renderInput={(params) => (
                      <TextField size="small" {...params} />
                    )}
                  />
                </div>

                <div className="form-group mt-2">
                  <label className="required fw-bold fs-6 mb-2">
                    Văn bằng chuyên môn
                  </label>
                  <Select
                    value={selectedVBCN}
                    key={"id"}
                    options={listVanBangChuyenMon}
                    placeholder="Chọn văn bằng chuyên môn"
                    formatGroupLabel={formatGroupLabel}
                    onChange={(e) => {
                      setSelectedVBCN(e);
                    }}
                  />
                </div>

                <div className="form-group mb-5 mt-2">
                  <label className="required fw-bold fs-6 mb-2">
                    Năm tốt nghiệp
                  </label>
                  <Form.Control
                    placeholder="Nhập năm tốt nghiệp"
                    style={{ height: 38 }}
                  />
                </div>
              </div>
            </div>
          </div>
        </Modal.Body>
        <Modal.Footer>
          <Button
            variant="secondary"
            onClick={() => {
              props?.hide();
            }}
          >
            <i className="fas fa-times"></i> Hủy
          </Button>
          <Button variant="btn text-white bg-blue2">
            {edit ? (
              <i className="fa fa-pencil-alt text-white"></i>
            ) : (
              <i className="fas fa-save text-white"></i>
            )}{" "}
            {edit ? "Cập nhật" : "Thêm"}
          </Button>
        </Modal.Footer>
      </LocalizationProvider>
    </Modal>
  );
}
