import { enAU } from "date-fns/locale";
import { DesktopDatePicker, LocalizationProvider } from "@mui/x-date-pickers";
import { AdapterDateFns } from "@mui/x-date-pickers/AdapterDateFns";

import { Form, Modal } from "react-bootstrap";
import { useState } from "react";
import LoadingIndicator from "../../../components/LoadingIndicator";
import { Checkbox, FormControlLabel, Select, TextField } from "@mui/material";
import { GioiTinh, HinhThuc } from "../../../data";
import { useDispatch, useSelector } from "react-redux";
import { commonActions, selectListIdCheckedPVCM, selectListIdCheckedVTHN } from "../../../redux-module/common/commonSlice";

export function PopupCapNhatYeuCauLienKetTC (props) {
    const dispatch = useDispatch();


    const [isLoading, setIsLoading] = useState(false);

    const [listVanBangChuyenMon, setListVanBangChuyenMon] = useState([]);
    const [listNoiCapCMND, setListNoiCapCMND] = useState([]);

    const [ListPVCM, setListPVCM] = useState([]);
    const [ListVTHN, setListVTHN] = useState([]);

    const ListIdCheckedPVCM = useSelector(selectListIdCheckedPVCM);
    const listIdCheckedVTHN = useSelector(selectListIdCheckedVTHN);

    const [FileAnhChanDung, setFileAnhChanDung] = useState([]);
    const [FileCMNDCCCD, setFileCMNDCCCD] = useState([]);
    const [FileGiayToKhac, setFileGiayToKhac] = useState([]);
    const [FileHinhChupCCHND, setFileHinhChupCCHND] = useState([]);




    const renderCheckbox = (e) => {
        return (
            <div className="row col-12" key={e?.id}>
                <span key={e?.id} className="font-weight-bold mb-1">
                    {e?.ten}
                </span>
                {e.value && e.value.length > 0
                    ? e.value.map((a, i) => {
                        return (
                            <div key={a.value} className="col-6">
                                <FormControlLabel
                                    control={
                                        <Checkbox
                                            key={i}
                                            checked={listIdCheckedVTHN.includes(String(a.value))}
                                            onChange={() => {
                                                let checkExist = listIdCheckedVTHN.includes(
                                                    String(a.value)
                                                );
                                                let listTemp = listIdCheckedVTHN;
                                                if (checkExist) {
                                                    let x1 = listTemp.filter(
                                                        (x) => x !== String(a.value)
                                                    );
                                                    dispatch(commonActions.setListIdCheckedVTHN(x1));
                                                } else {
                                                    let x2 = [...listTemp, a.value];
                                                    dispatch(commonActions.setListIdCheckedVTHN(x2));
                                                }
                                            }}
                                            value={a?.value || ""}
                                        />
                                    }
                                    label={a?.label}
                                />
                            </div>
                        );
                    })
                    : null}
            </div>
        );
    };

    const renderChooseFile = ({
        title,
        name,
        arrayFileMap,
        setFile,
        arrayDelFile,
        setFileDelete,
    }) => {
        return (
            <div className="col-6 pb-2">
                <div className="row">
                    <div className="col-10">{title}</div>
                    <div className="col-2">
                        <button className="btn btn-primary btn-sm font-weight-bold">
                            <UploadControl name={name}>CHá»N</UploadControl>
                        </button>
                    </div>
                    <div className="col-12">
                        {arrayFileMap?.length > 0
                            ? arrayFileMap.map((e, i) => {
                                return (
                                    <button
                                        key={i}
                                        className="btn ml-2 mb-2"
                                        style={{
                                            backgroundColor: "#e8e8e8",
                                            padding: "12px 10px 12px 16px",
                                            marginRight: "12px",
                                        }}
                                    >
                                        <span>{e?.name}</span>
                                        <i
                                            style={{
                                                display: "inline",
                                                color: "red",
                                                marginLeft: "8px",
                                            }}
                                            onClick={() => {
                                                let arrFile = arrayFileMap;
                                                arrFile = arrFile.filter(
                                                    (item) => item.name !== e?.name
                                                );
                                                setFile(arrFile);
                                                //set del file
                                                if (e.url) {
                                                    setFileDelete(arrayDelFile.concat(e));
                                                    console.log("log e cÃ³ url", e);
                                                }
                                            }}
                                            className="fas fa-times-circle"
                                        ></i>
                                    </button>
                                );
                            })
                            : null}
                    </div>
                </div>
            </div>
        );
    };

    const UploadControl = ({
        children,
        value,
        onChange,
        disabled,
        accept,
        name,
    }) => {
        return (
            <label htmlFor={`contained-button-${name}`} className="m-0 w-100">
                <input
                    disabled={disabled}
                    style={{ display: "none" }}
                    id={`contained-button-${name}`}
                    multiple
                    type="file"
                    accept="audio/*|video/*|image/*|MIME_type"
                    onChange={(event) => {
                        switch (name) {
                            case "hinhchupcchnd":
                                setFileHinhChupCCHND(
                                    FileHinhChupCCHND.concat(Object.values(event.target.files))
                                );
                                break;
                            case "filecmndcccd":
                                setFileCMNDCCCD(
                                    FileCMNDCCCD.concat(Object.values(event.target.files))
                                );
                                break;
                            case "anhchandung":
                                setFileAnhChanDung(
                                    FileAnhChanDung.concat(Object.values(event.target.files))
                                );
                                break;
                            case "giaytokhac":
                                setFileGiayToKhac(
                                    FileGiayToKhac.concat(Object.values(event.target.files))
                                );
                                break;
                            default:
                                break;
                        }
                    }}
                />
                {children}
            </label>
        );
    };




    return (
        <Modal
            backdrop="static"
            keyboard={false}
            size="xl"
            show={props?.show}
            onHide={() => {
                props?.hide();
            }}
            aria-labelledby="contained-modal-title-vcenter"
            centered
        >
            <LocalizationProvider dateAdapter={AdapterDateFns} locale={enAU}>
                <Modal.Header className="px-4" closeButton>
                    <Modal.Title className=" ms-auto ">
                        <h2 className=" text-uppercase text-center text-primary">
                            YÃU Cáº¦U LIÃN Káº¾T DÃNH CHO DÆ¯á»¢C SÄ¨ ÄÃ CÃ CCHND
                        </h2>
                    </Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    {/* THÃNG TIN HÃNH CHÃNH */}
                    <>
                        {isLoading && <LoadingIndicator />}
                        {!isLoading && (
                            <>
                                <div className="row card-box" style={{ pointerEvents: "none" }}>
                                    <div className="col-12 text-title pb-3 fw-bolder">
                                        THÃNG TIN CÆ  Sá»/Äá»A ÄIá»M KINH DOANH
                                    </div>
                                    <div className="col-4 mb-4">
                                        <span className="required fw-bolder text-primary">
                                            Há» Äá»m
                                        </span>
                                        <Form.Control
                                            type="text"
                                            className="mt-2"
                                            style={{ fontSize: 14, fontWeight: 400, height: "38px" }}
                                            onChange={(e) => {
                                                // setField("ho", e.target.value);
                                            }}
                                            // value={formTTHC?.ho != null ? formTTHC?.ho : ""}
                                            // isInvalid={!!errors?.ho}
                                        />
                                        <Form.Control.Feedback type="invalid">
                                            {/* {errors?.ho} */}
                                        </Form.Control.Feedback>
                                    </div>
                                    <div className="col-2 mb-4">
                                        <span className="required fw-bolder text-primary">TÃªn</span>
                                        <Form.Control
                                            type="text"
                                            className="mt-2"
                                            style={{ fontSize: 14, fontWeight: 400, height: "38px" }}
                                            onChange={(e) => {
                                                // setField("ten", e.target.value);
                                            }}
                                            // value={formTTHC?.ten != null ? formTTHC?.ten : ""}
                                            // isInvalid={!!errors?.ten}
                                        />
                                        <Form.Control.Feedback type="invalid">
                                            {/* {errors?.ten} */}
                                        </Form.Control.Feedback>
                                    </div>
                                    <div className="col-3 mb-4">
                                        <span className="required fw-bolder d-block mb-2 text-primary">
                                            NgÃ y sinh
                                        </span>
                                        <DesktopDatePicker
                                            label=" "
                                            inputFormat="dd/MM/yyyy"
                                            // value={formTTHC?.ngay_sinh}
                                            onChange={(e) => {
                                                // setField("ngay_sinh", e);
                                            }}
                                            renderInput={(params) => <TextField {...params} />}
                                            // isInvalid={!!errors?.ngay_sinh}
                                        />
                                        <Form.Control.Feedback type="invalid">
                                            {/* {errors?.ngay_sinh} */}
                                        </Form.Control.Feedback>
                                    </div>
                                    <div className="col-3 mb-4">
                                        <span className="required fw-bolder text-primary">
                                            Giá»i tÃ­nh
                                        </span>
                                        <Select
                                            key={"id"}
                                            className="mt-2"
                                            options={GioiTinh}
                                            style={{ height: "44px" }}
                                            onChange={(e) => {
                                                // setSelectedGioiTinh(e);
                                            }}
                                            // value={selectedGioiTinh}
                                        />
                                        <span className="text-danger">
                                            {/* {!selectedGioiTinh ? " khÃ´ng ÄÆ°á»£c bá» trá»ng!" : ""} */}
                                        </span>
                                    </div>

                                    <div className="col-lg-3 col-sm-6 col-md-6 mb-4">
                                        <span className="required fw-bolder text-primary">
                                            CMND/CCCD
                                        </span>
                                        <Form.Control
                                            type="text"
                                            className="mt-2"
                                            style={{ fontSize: 14, fontWeight: 400, height: "38px" }}
                                            onChange={(e) => {
                                                // setField("ma_cong_dan", e.target.value);
                                            }}
                                            // value={
                                            //     formTTHC?.ma_cong_dan != null
                                            //         ? formTTHC?.ma_cong_dan
                                            //         : ""
                                            // }
                                            // isInvalid={!!errors?.so_hieu}
                                        />
                                        <Form.Control.Feedback type="invalid">
                                            {/* {errors?.so_hieu} */}
                                        </Form.Control.Feedback>
                                    </div>
                                    <div className="col-lg-3 col-sm-6 col-md-6 mb-4">
                                        <span className="required fw-bolder d-block mb-2 text-primary">
                                            NgÃ y cáº¥p CMND/CCCD
                                        </span>
                                        <DesktopDatePicker
                                            label=" "
                                            inputFormat="dd/MM/yyyy"
                                            // value={formTTHC?.ngay_cap}
                                            onChange={(e) => {
                                                // setField("ngay_cap", e.target.value);
                                            }}
                                            renderInput={(params) => <TextField {...params} />}
                                        // isInvalid={!!errors?.ngay_cap}
                                        />
                                        <Form.Control.Feedback type="invalid">
                                            {/* {errors?.ngay_cap} */}
                                        </Form.Control.Feedback>
                                    </div>
                                    <div className="col-lg-6 col-sm-12 col-md-12 mb-4">
                                        <span className="required fw-bolder d-block mb-2 text-primary">
                                            NÆ¡i cáº¥p CMND/CCCD
                                        </span>
                                        <Select
                                            key={"id"}
                                            className="mt-2"
                                            // options={listNoiCapCMND}
                                            style={{ height: "44px" }}
                                            onChange={(e) => {
                                                // setSelectedNoiCapCMND(e);
                                            }}
                                            // value={selectedNoiCapCMND}
                                        />
                                        <span className="text-danger">
                                            {/* {errors?.noi_tot_nghiep_id
                                                ? "NÆ¡i tá»t nghiá»p khÃ´ng ÄÆ°á»£c bá» trá»ng!"
