import { DesktopDatePicker } from "@mui/x-date-pickers";
import { TextField } from "@mui/material";
import axios from "axios";
import { useEffect, useState } from "react";
import { Form } from "react-bootstrap";
import Select from "react-select";
import { toast } from "react-toastify";
import api from "../../../../../configs/api";
import { GioiTinh } from "../../../../../data";

export function ThongTinHanhChinhCapNhat() {

    const [SelectedGioiTinh, setSelectedGioiTinh] = useState({});

    useEffect(() => {
        getTinhThanh();
    }, []);

    //validate thêm vị trí hành nghề
    const [form, setForm] = useState({});
    const [errors, setErrors] = useState({});
    // location
    const [ListTinhThanh, setListTinhThanh] = useState([]);
    const [ListQuanHuyen, setListQuanHuyen] = useState([]);
    const [ListXaPhuong, setListXaPhuong] = useState([]);

    const [disQuanHuyen, setDisQuanHuyen] = useState(true);
    const [disXaPhuong, setDisXaPhuong] = useState(true);

    const [quanHuyenValue, setQuanHuyenValue] = useState(null);
    const [xaPhuongValue, setXaPhuongValue] = useState(null);

    const setField = (field, value) => {
        setForm({
            ...form,
            [field]: value,
        });
        if (!!errors[field])
            setErrors({
                ...errors,
                [field]: null,
            });
    };

    const findFormErrors = () => {
        const { ly_do_tu_choi } = form;
        const newErrors = {};
        // name errors
        if (!ly_do_tu_choi || ly_do_tu_choi === "")
            newErrors.ly_do_tu_choi = "Lý do từ chối không được bỏ trống!";
        return newErrors;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const newErrors = findFormErrors();
        // Conditional logic:
        if (Object.keys(newErrors).length > 0) {
            console.log(newErrors);
            // We got errors!
            setErrors(newErrors);
            for (const property in newErrors) {
                toast.error(newErrors[property], {
                    position: "top-right",
                    autoClose: 2000,
                    hideProgressBar: false,
                    closeOnClick: true,
                    pauseOnHover: true,
                    draggable: true,
                    progress: undefined,
                    toastId: "error",
                });
                return;
            }
        } else {
            // No errors! Put any logic here for the form submission!
            updateHoSo();
        }
    };

    const onChangeTinhThanh = async (values) => {
        setField("tinh_thanh_id", values.id);
        getQuanHuyen(values.id);
        setDisQuanHuyen(false);
        setDisXaPhuong(true);
        setQuanHuyenValue(null);
        setXaPhuongValue(null);
    };

    const getTinhThanh = () => {
        axios
            .get(api.API_TINH_THANH)
            .then(({ data }) => {
                let resultsTinhThanh = data?.results;
                resultsTinhThanh.forEach((e) => {
                    e.label = e.ten;
                    e.value = e.id;
                });
                setListTinhThanh(resultsTinhThanh);
            })
            .catch((error) => {
                if (error.response.data) {
                    toast.error(error.response.data.msg, {
                        position: "top-right",
                        autoClose: 2000,
                        hideProgressBar: false,
                        closeOnClick: true,
                        pauseOnHover: true,
                        draggable: true,
                        progress: undefined,
                        toastId: "success",
                    });
                }
            });
    };
    const getQuanHuyen = (value) => {
        axios
            .get(`${api.API_QUAN_HUYEN}/${value}?per_page=100`)
            .then(({ data }) => {
                let resultsQuanHuyen = data?.results;
                resultsQuanHuyen.forEach((e) => {
                    e.label = e.ten;
                    e.value = e.id;
                });
                setListQuanHuyen(resultsQuanHuyen);
            })
            .catch((error) => {
                if (error.response.data) {
                    toast.error(error.response.data.msg, {
                        position: "top-right",
                        autoClose: 2000,
                        hideProgressBar: false,
                        closeOnClick: true,
                        pauseOnHover: true,
                        draggable: true,
                        progress: undefined,
                        toastId: "success",
                    });
                }
            });
    };
    const getXaPhuong = (value) => {
        axios
            .get(`${api.API_XA_PHUONG}/${value}?per_page=100`)
            .then(({ data }) => {
                let resultsXaPhuong = data?.results;
                resultsXaPhuong.forEach((e) => {
                    e.label = e.ten;
                    e.value = e.id;
                });
                setListXaPhuong(resultsXaPhuong);
            })
            .catch((error) => {
                if (error.response.data) {
                    toast.error(error.response.data.msg, {
                        position: "top-right",
                        autoClose: 2000,
                        hideProgressBar: false,
                        closeOnClick: true,
                        pauseOnHover: true,
                        draggable: true,
                        progress: undefined,
                        toastId: "success",
                    });
                }
            });
    };

    const onChangeQuanHuyen = (values) => {
        setField("quan_huyen_id", values.id);
        getXaPhuong(values.id);
        setDisXaPhuong(false);
        setQuanHuyenValue(values);
        setXaPhuongValue(null);
    };

    const onChangeXaPhuong = (values) => {
        setField("phuong_xa_id", values.id);
        setXaPhuongValue(values);
    };

    const updateHoSo = () => {
        const { ly_do_tu_choi } = form;
        const jsonUpdate = {
            ly_do_tu_choi,
        };
        axios
            .post(api.API_YEU_CAU_CHUNG_CHI_HANH_NGHE + "/thu_ly", jsonUpdate)
            .then(async ({ data }) => {
                toast.success("Cập nhật thành công", {
                    position: "top-right",
                    autoClose: 2000,
                    hideProgressBar: false,
                    closeOnClick: true,
                    pauseOnHover: true,
                    draggable: true,
                    progress: undefined,
                    toastId: "success",
                });
            })
            .catch((error) => {
                toast.error(error?.data?.errors, {
                    position: "top-right",
                    autoClose: 2000,
                    hideProgressBar: false,
                    closeOnClick: true,
                    pauseOnHover: true,
                    draggable: true,
                    progress: undefined,
                    toastId: "error",
                });
            })
            .finally(() => { });
    };


    return (
        <div className="card py-6 px-6 mx-3 my-3">
            <div className="col-12 text-title pb-3 fw-bolder">
                Thông tin hành chính
            </div>
            <div className="row">
                {/* Họ tên */}
                <div className="row col-12">
                    <div className="col-4 mb-4">
                        <span className="required fw-bolder text-primary">Họ và tên</span>
                        <Form.Control
                            type="text"
                            className="mt-2"
                            placeholder="Nhập họ tên"
                            style={{ fontSize: 14, fontWeight: 400, height: "38px" }}
                            onChange={(e) => {
                                setField("ho", e.target.value);
                            }}
                            value={form?.ho != null ? form?.ho : ""}
                            isInvalid={!!errors?.ho}
                        />
                        <Form.Control.Feedback type="invalid">
                            {/* {errors?.so_hieu} */}
                        </Form.Control.Feedback>
                    </div>
                    <div className="col-4 mb-4">
                        <span className="required fw-bolder d-block mb-2 text-primary">
                            Ngày sinh
                        </span>
                        <DesktopDatePicker
                            label=" "
                            inputFormat="dd/MM/yyyy"
                            // value={ngayCap}
                            onChange={() => { }}
                            renderInput={(params) => <TextField {...params} />}
                        // isInvalid={!!errors?.ngay_cap}
                        />
                        <Form.Control.Feedback type="invalid">
                            {/* {errors?.ngay_cap} */}
                        </Form.Control.Feedback>
                    </div>
                    <div className="col-4 mb-4">
                        <span className="required fw-bolder text-primary">Giới tính</span>
                        <Select
                            key={"id"}
                            className="mt-2"
                            placeholder="Chọn giới tính"
                            options={GioiTinh}
                            style={{ height: "44px" }}
                            onChange={(e) => {
                                setSelectedGioiTinh(e);
                            }}
                        // value={SelectedGioiTinh}
                        />
                    </div>
                </div>



                {/* CMND/SDT */}
                <div className="row col-12">
                    <div className="col-4 mb-4">
                        <span className="required fw-bolder text-primary">CMND/CCCD</span>
                        <Form.Control
                            type="text"
                            className="mt-2"
                            placeholder="Nhập số CMND/CCCD"
                            style={{ fontSize: 14, fontWeight: 400, height: "38px" }}
                            onChange={(e) => {
                                //   setField("so_hieu", e.target.value);
                            }}
                        // value={form?.so_hieu != null ? form?.so_hieu : ""}
                        // isInvalid={!!errors?.so_hieu}
                        />
                        <Form.Control.Feedback type="invalid">
                            {/* {errors?.so_hieu} */}
                        </Form.Control.Feedback>
                    </div>
                    <div className="col-4 mb-4">
                        <span className="required fw-bolder d-block mb-2 text-primary">
                            Ngày cấp CMND/CCCD
                        </span>
                        <DesktopDatePicker
                            label=" "
                            inputFormat="dd/MM/yyyy"
                            // value={ngayCap}
                            onChange={() => { }}
                            renderInput={(params) => <TextField {...params} />}
                        // isInvalid={!!errors?.ngay_cap}
                        />
                        <Form.Control.Feedback type="invalid">
                            {/* {errors?.ngay_cap} */}
                        </Form.Control.Feedback>
                    </div>
                    <div className="col-4 mb-4">
                        <span className="required fw-bolder d-block mb-2 text-primary">
                            Nơi cấp CMND/CCCD
                        </span>
                        <Select
                            key={"id"}
                            labelField={"ten"}
                            placeholder="Chọn nơi cấp CMND/CCCD"
                            searchBy={"tenkhongdau"}
                            options={ListTinhThanh}
                            onChange={() => { }}
                            closeOnSelect={true}
                        />
                        <Form.Control.Feedback type="invalid">
                            {/* {errors?.so_hieu} */}
                        </Form.Control.Feedback>
                    </div>

                </div>

                {/* Hộ khẩu thường trú */}
                <div className="row col-12">
                    <div className="col-4 mb-4">
                        <span className="required fw-bolder text-primary">Tỉnh/TP</span>
                        <Select
                            key={"id"}
                            labelField={"ten"}
                            placeholder="Chọn tỉnh thành"
                            searchBy={"tenkhongdau"}
                            options={ListTinhThanh}
                            onChange={(values) => onChangeTinhThanh(values)}
                            closeOnSelect={true}
                        />
                        <Form.Control.Feedback type="invalid">
                            {/* {errors?.so_hieu} */}
                        </Form.Control.Feedback>
                    </div>
                    <div className="col-4 mb-4">
                        <span className="required fw-bolder text-primary">Quận huyện</span>
                        <Select
                            key={"id"}
                            className={"quan_huyen"}
                            labelField={"ten"}
                            placeholder="Chọn quận huyện"
                            options={ListQuanHuyen}
                            onChange={(values) => onChangeQuanHuyen(values)}
                            closeOnSelect={true}
                            multi={false}
                            isDisabled={disQuanHuyen}
                            value={quanHuyenValue}
                        />
                        <Form.Control.Feedback type="invalid">
                            {/* {errors?.so_hieu} */}
                        </Form.Control.Feedback>
                    </div>
                    <div className="col-4 mb-4">
                        <span className="required fw-bolder d-block text-primary">
                            Phường/Xã
                        </span>
                        <Select
                            key={"id"}
                            labelField={"ten"}
                            placeholder="Chọn xã phường"
                            options={ListXaPhuong}
                            onChange={(values) => onChangeXaPhuong(values)}
                            closeOnSelect={true}
                            isDisabled={disXaPhuong}
                            value={xaPhuongValue}
                        />
                        <Form.Control.Feedback type="invalid">
                            {/* {errors?.ngay_cap} */}
                        </Form.Control.Feedback>
                    </div>

                </div>

                <div className="row col-12">
                    <div className="col-4 mb-4">
                        <span className="required fw-bolder text-primary">Địa chỉ</span>
                        <Form.Control
                            type="text"
                            className="mt-2"
                            placeholder="Nhập địa chỉ"
                            style={{ fontSize: 14, fontWeight: 400, height: "38px" }}
                            onChange={(e) => {
                                setField("ho", e.target.value);
                            }}
                            value={form?.ho != null ? form?.ho : ""}
                            isInvalid={!!errors?.ho}
                        />
                        <Form.Control.Feedback type="invalid">
                            {/* {errors?.so_hieu} */}
                        </Form.Control.Feedback>
                    </div>
                    <div className="col-4 mb-4">
                        <span className="fw-bolder d-block mb-2 text-primary">
                            Số điện thoại
                        </span>
                        <Form.Control
                            type="text"
                            className="mt-2"
                            placeholder="Nhập số điện thoại"
                            style={{ fontSize: 14, fontWeight: 400, height: "38px" }}
                            onChange={(e) => {
                                //   setField("so_hieu", e.target.value);
                            }}
                        // value={form?.so_hieu != null ? form?.so_hieu : ""}

                        // isInvalid={!!errors?.so_hieu}
                        />
                    </div>
                    <div className="col-4 mb-4">
                        <span className="fw-bolder d-block mb-2 text-primary">Email</span>
                        <Form.Control
                            type="text"
                            className="mt-2"
                            placeholder="Nhập email"
                            style={{ fontSize: 14, fontWeight: 400, height: "38px" }}
                            onChange={(e) => {
                                //   setField("so_hieu", e.target.value);
                            }}
                        // value={form?.so_hieu != null ? form?.so_hieu : ""}

                        // isInvalid={!!errors?.so_hieu}
                        />
                    </div>
                </div>

                <div className="row col-12">
                    <div className="col-4 mb-4">
                        <span className="required fw-bolder d-block mb-2 text-primary">
                            Nơi tốt nghiệp
                        </span>
                        <Select
                            key={"id"}
                            labelField={"ten"}
                            placeholder="Chọn nơi tốt nghiệp"
                            options={ListXaPhuong}
                            onChange={() => { }}
                            closeOnSelect={true}
                            value={xaPhuongValue}
                        />
                        <Form.Control.Feedback type="invalid">
                            {/* {errors?.ngay_cap} */}
                        </Form.Control.Feedback>
                    </div>
                    <div className="col-4 mb-4">
                        <span className="fw-bolder d-block mb-2 text-primary">Năm tốt nghiệp</span>
                        <Form.Control
                            type="text"
                            className="mt-2"
                            placeholder="Nhập năm tốt nghiệp"
                            style={{ fontSize: 14, fontWeight: 400, height: "38px" }}
                            onChange={(e) => {
                                //   setField("so_hieu", e.target.value);
                            }}
                        // value={form?.so_hieu != null ? form?.so_hieu : ""}

                        // isInvalid={!!errors?.so_hieu}
                        />
                    </div>
                </div>
            </div>
        </div>
    )
}
