import axios from "axios";
import { useEffect, useState } from "react";
import { Form, Modal } from "react-bootstrap";
import DataTable from "react-data-table-component";
import { Oval } from "react-loader-spinner";
import { conditionalRowStyles, customStyles, paginationOptions } from "../../../../../_metronic/assets/custom/table";
import { useDebounce } from "../../../../../_metronic/helpers";
import api from "../../../../configs/api";

import { CKEditor } from "@ckeditor/ckeditor5-react";
import ClassicEditor from '@ckeditor/ckeditor5-build-classic';

export function QuanLyTinTuc() {


    const [page, setPage] = useState(1);
    const [perPage, setPerPage] = useState(1);
    const [totalRows, setTotalRows] = useState(0);
    const [isLoading, setIsLoading] = useState(false);

    const [show, setShow] = useState(false);
    const [searchKey, setSearchKey] = useState("");
    const [tinTucList, setTinTucList] = useState([]);

    const debouncedSearchKey = useDebounce(searchKey, 1000);

    useEffect(() => {
        if (debouncedSearchKey !== undefined && searchKey !== undefined) {
            getList(1, perPage, debouncedSearchKey);
        }
    }, [debouncedSearchKey]);


    const getList = (page_number = page, size = perPage, search_key = searchKey) => {
        axios
            .post(api.API_QUAN_LY_NGUOI_DUNG + `?page=${page_number}&per_page=${size}`, { search_key: search_key })
            .then(({ data }) => {
                if (data) {
                    setTinTucList(data?.results)
                }
            })
    }

    const handlePageChange = (page) => {
        setPage(page);
        getList({ page_number: page });
    };

    const handlePerRowsChange = async (newPerPage, page) => {
        axios
            .post(api.API_QUAN_LY_NGUOI_DUNG, {})
            .then(({ data }) => {
                if (data) {
                    setTinTucList(data?.results);
                    setPerPage(newPerPage);
                }
            })
            .catch(() => { })
            .finally(() => {
            });
    };

    const columns = [
        {
            name: "Tên người dùng",
            selector: (row) => row.ho_ten ? <span>{row?.ho_ten}</span> : <span className="text-danger"> N/A</span>,
            grow: 8,
            style: {
                cursor: "pointer",
                color: "#202124",
            },
        },
        {
            name: "Tên người dùng",
            selector: (row) => row.ho_ten ? <span>{row?.ho_ten}</span> : <span className="text-danger"> N/A</span>,
            grow: 8,
            style: {
                cursor: "pointer",
                color: "#202124",
            },
        },
        {
            name: "Tên người dùng",
            selector: (row) => row.ho_ten ? <span>{row?.ho_ten}</span> : <span className="text-danger"> N/A</span>,
            grow: 8,
            style: {
                cursor: "pointer",
                color: "#202124",
            },
        },
        {
            name: "Tên người dùng",
            selector: (row) => row.ho_ten ? <span>{row?.ho_ten}</span> : <span className="text-danger"> N/A</span>,
            grow: 8,
            style: {
                cursor: "pointer",
                color: "#202124",
            },
        },
        {
            name: "Tên người dùng",
            selector: (row) => row.ho_ten ? <span>{row?.ho_ten}</span> : <span className="text-danger"> N/A</span>,
            grow: 8,
            style: {
                cursor: "pointer",
                color: "#202124",
            },
        },
        {
            name: "Tên người dùng",
            selector: (row) => row.ho_ten ? <span>{row?.ho_ten}</span> : <span className="text-danger"> N/A</span>,
            grow: 8,
            style: {
                cursor: "pointer",
                color: "#202124",
            },
        },
    ]





    return (
        <div className="py-3">
            <div className="card-box" style={{ padding: "32px" }}>
                <div className="row">
                    <div className="col-12 mb-4">
                        <h4 className="fw-bold">QUẢN LÝ TIN TỨC</h4>
                    </div>

                    <div className="col-8 mt-4 mb-6">
                        <Form.Control
                            type="text"
                            placeholder="Nhập từ khóa tìm kiếm..."
                            style={{ fontSize: 14, fontWeight: 400, height: "38px", width: "320px" }}
                            onChange={(e) => setSearchKey(e.target.value)}
                        />
                    </div>
                    <div className="col-4 text-end">
                        <button className="btn btn-primary" style={{fontSize: "13px"}} onClick={() => {setShow(true)}}><i className="fas fa-plus me-1"></i>Tạo mới</button>
                    </div>

                    <div className="col-12">
                        <DataTable
                            noDataComponent={"Không có dữ liệu ..."}
                            sortServer
                            progressPending={isLoading}
                            columns={columns}
                            data={tinTucList}
                            customStyles={customStyles}
                            pagination
                            highlightOnHover
                            pointerOnHover
                            paginationServer
                            paginationTotalRows={totalRows}
                            onChangeRowsPerPage={handlePerRowsChange}
                            onChangePage={handlePageChange}
                            paginationComponentOptions={paginationOptions}
                            conditionalRowStyles={conditionalRowStyles}
                            // onRowClicked={(data) => {
                            //   detail(data);
                            //   handleRowClicked(data);
                            // }}
                            progressComponent={
                                <div style={{ padding: "24px" }}>
                                    <Oval
                                        arialLabel="loading-indicator"
                                        color="#007bff"
                                        height={40}
                                    />
                                </div>
                            }
                        />
                    </div>
                </div>
            </div>

            <Modal show={show} size="lg">
                <Modal.Header>
                    <h4 className="font-weight-bold">Thêm mới tin tức</h4>
                </Modal.Header>
                <Modal.Body>
                    <label className="mb-2 required" style={{fontWeight: 600}}>Tiêu đề</label>
                    <Form.Control 
                        type="text"
                        placeholder="Nhập tiêu đề"
                        style={{ fontWeight: 500 , fontSize: "13px", height: "38px"}}
                    />
                    
                    <label className="mt-4 mb-2 required" style={{ fontWeight: 600 }}>Nội dung</label>
                    <CKEditor
                        editor={ClassicEditor}
                        config={{ placeholder: "Nhập yêu cầu bổ sung ..." }}
                        onReady={(editor) => {
                            editor.ui.view.editable.element.style.height = "3000px";
                        }}
                        onChange={(event, editor) => {}}
                    />
                </Modal.Body>
            </Modal>

        </div>
    )
}
