import axios from "axios";
import api from "../../configs/api";
import { toast } from "react-toastify";

export const getListTinhThanh = () => {
    return new Promise((resolve, reject) => {
        axios
            .get(api.API_TINH_THANH)
            .then(({ data }) => {
                const resultsTinhThanh = data?.results;
                resultsTinhThanh.forEach((e) => {
                    e.label = e.ten;
                    e.value = e.id;
                })
                resolve(resultsTinhThanh);
            })
            .catch((error) => {
                reject(error)
            })
            .finally(() => { });
    });
};


const getListQuanHuyen = (id) => {
    return new Promise((resolve, reject) => {
        axios
            .get(`${api.API_QUAN_HUYEN}/${id}?per_page=100`)
            .then(({ data }) => {
                const resultsQuanHuyen = data?.results;
                resultsQuanHuyen.forEach((e) => {
                    e.label = e.ten;
                    e.value = e.id;
                });
                resolve(resultsQuanHuyen)
            })
            .catch((error) => {
                reject(error);
            });
    })
}

const getListXaPhuong = (id) => {
    return new Promise((resolve, reject) => {
        axios
            .get(`${api.API_XA_PHUONG}/${id}?per_page=100`)
            .then(({ data }) => {
                const resultsXaPhuong = data?.results;
                resultsXaPhuong.forEach((e) => {
                    e.label = e.ten;
                    e.value = e.id;
                });
                resolve(resultsXaPhuong);
            })
            .catch((error) => {
                if (error.response.data) {
                    toast.error(error.response.data.msg, {
                        position: "top-right",
                        autoClose: 2000,
                        hideProgressBar: false,
                        closeOnClick: true,
                        pauseOnHover: true,
                        draggable: true,
                        progress: undefined,
                        toastId: "success",
                    });
                }
            });
    })
}
