import axios from "axios";
import { useEffect, useState } from "react";
import { Form } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { NavLink, useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import api from "../../../configs/api";
import {
  authActions,
  selectCurrentUser
} from "../../../redux-module/auth/authSlice";

export function Login() {
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const [data, setData] = useState();
  const [form, setForm] = useState({});
  const [errors, setErrors] = useState({});
  const [isDaCap, setIsDaCap] = useState(false);
  const [currentRole, setCurrentRole] = useState({});
  const [isLoading, setIsLoading] = useState(false);
  const currentUser = useSelector(selectCurrentUser);

  const [type, setType] = useState("password");
  const [icon, setIcon] = useState("fas fa-eye text-primary mx-auto");

  const spanStyle = { fontSize: "15px", fontWeight: 600 }

  function showOrHide(e) {
    e.preventDefault();
    type === "password" ? setType("text") : setType("password");

    if (type === "password") {
      setType("text");
      setIcon("fas fa-eye-slash text-primary mx-auto");
    } else if (type === "text") {
      setType("password");
      setIcon("fas fa-eye text-primary mx-auto");
    }
  }

  const setField = (field, value) => {
    setForm({
      ...form,
      [field]: value,
    });
    if (!!errors[field])
      setErrors({
        ...errors,
        [field]: null,
      });
  };

  useEffect(() => {
    if (currentUser) {
      navigate("/dashboard");
    }
  }, []);

  const findFormErrors = () => {
    const { username, password } = form;
    const newErrors = {};
    // name errors
    if (!username || username === "")
      newErrors.username = "Tên đăng nhập không được bỏ trống!";
    if (!password || password === "")
      newErrors.password = "Mật khẩu không được bỏ trống!";
    return newErrors;
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    const newErrors = findFormErrors();
    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      setIsLoading(false);
    } else {
      onChoosen();
    }
  };


  const onChoosen = () => {
    const { username, password } = form;
    const json = { username, password };
    axios
      .post(api.API_LOGIN, json)
      .then(async ({ data }) => {
        setData(data);
        dispatch(authActions.loginSuccess(data));
        setCurrentRole(data?.userInfo?.assigned_role[0]?.ten_en);

        if (data?.userInfo?.last_login == null) {
          if (
            data?.userInfo?.assigned_role[0]?.ten_en === "duocsi" ||
            data?.userInfo?.assigned_role[0]?.ten_en === "tochuc"
          ) {
            setIsDaCap(true);
          } else {
            navigate("/dashboard");
          }
        } else {
          switch (data?.userInfo?.assigned_role[0]?.ten_en) {
            case "duocsi":
              navigate("/admin/chung-chi-hanh-nghe");
              break;
            case "tochuc":
              navigate("/admin/giay-phep-kinh-doanh");
              break;
            default:
              navigate("/dashboard");
              break;
          }
        }
        setIsLoading(false);
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  return (
    <div className="px-5 mx-auto" style={{ width: "72%", marginTop: "20vh" }}>
      <div className="box-login-wrapper">
        <Form>
          <Form.Group>
            <div className="row mb-3">
              <span className="mb-2" style={spanStyle}>
                Tên đăng nhập <span className="text-danger">*</span>
              </span>
              <Form.Control
                type="text"
                placeholder="Tên đăng nhập"
                style={{ fontSize: 14, fontWeight: 400 }}
                value={form?.username ? form?.username : ""}
                onChange={(e) => setField("username", e.target.value)}
              />
              {errors?.username ? (<span className="text-danger">{errors?.username}</span>) : ("")}

            </div>
            <div className="row mb-3">
              <span className="required mb-2" style={spanStyle}>
                Mật khẩu <span className="text-danger">*</span>
              </span>
              <div className="input-group px-0" id="show_hide_password">
                <Form.Control
                  type={type}
                  placeholder="Mật khẩu"
                  style={{ fontSize: 14, fontWeight: 400 }}
                  value={form?.password ? form?.password : ""}
                  onChange={(e) => setField("password", e.target.value)}
                />
                <div className="input-group-addon">
                  <button type="button" className="btn ps-3 pe-3" style={{ backgroundColor: "#fff", border: "1px solid #ced4da", borderTopLeftRadius: 0, borderBottomLeftRadius: 0 }} onClick={showOrHide}>
                    <i className={icon}></i>
                  </button>
                </div>
              </div>
              {errors?.password ? (<span className="text-danger">{errors?.password}</span>) : ("")}

            </div>
          </Form.Group>

          <div className="form-group mb-1 text-center">
            <button
              className="btn btn-primary btn-block btn-login"
              style={{ width: "100%", height: "46px" }}
              onClick={(e) => handleSubmit(e, "login")}
            >
              {loading ? (
                <span
                  className="indicator-progress"
                  style={{ display: "block" }}
                >
                  Vui lòng chờ...
                  <span className="spinner-border spinner-border-sm align-middle ms-2"></span>
                </span>
              ) : (
                <span>
                  <i className="fas fa-chevron-circle-right"></i>
                  &nbsp;&nbsp; Đăng nhập
                </span>
              )}
            </button>
          </div>
        </Form>
        <div className="form-group text-center mt-2 mb-3">
          <NavLink onClick={() => {navigate("/register")}}>Bạn chưa có tài khoản ?</NavLink>
        </div>

        <div className="form-group float-start">
          <div className="form-check">
            <input
              type="checkbox"
              id="flexCheckDefault"
              className="form-check-input"
            />
            <label
              htmlFor="flexCheckDefault"
              className="form-check-label"
            >
              Lưu mật khẩu
            </label>
          </div>
        </div>

        <div className="form-group float-end">
          <NavLink to="/forgot-password" className="forgot-password">
            Quên mật khẩu
          </NavLink>
        </div>
      </div>
    </div>
  );
}
