                                        <DesktopDatePicker
                                            label=" "
                                            inputFormat="dd/MM/yyyy"
                                            value={form?.ngay_cap ? form?.ngay_cap : null}
                                            onChange={selectedNgayCapGCN}
                                            maxDate={new Date()}
                                            renderInput={(params) => (
                                                <TextField size="small" {...params} />
                                            )}
                                        />
                                    </div>
                                    <div className="form-group mt-2">
                                        <label className="fw-bold required fs-6 mb-1"> Email </label>
                                        <Form.Control
                                            type="text"
                                            placeholder="Email"
                                            style={{ fontSize: 14, fontWeight: 400, height: 38 }}
                                            onChange={(e) => { setField("email_coso", e.target.value) }}
                                            value={ thongTinToChuc?.email_coso != null ? thongTinToChuc?.email_coso : "" }
                                        />
                                    </div>
                                    <div className="form-group mt-2">
                                        <label className="fw-bold required fs-6 mb-1"> Sá» Äiá»n thoáº¡i </label>
                                        <Form.Control
