                                            value={ thongTinToChuc?.fax_coso != null ? thongTinToChuc?.fax_coso : "" }
                                        />
                                    </div>
                                    <div className="mt-5">
                                        <label className="font-weight-bold text-uppercase"> 
                                            Äá»a chá» kinh doanh: 
                                        </label>
                                    </div>
                                    <div className="form-group mt-2">
                                        <label className="fw-bold required fs-6 mb-2"> Tá»nh/ThÃ nh phá» </label>
                                        <Select
                                            key={"id"}
                                            labelField={"ten"}
                                            placeholder="Chá»n tá»nh thÃ nh"
                                            searchBy={"tenkhongdau"}
                                            options={ListTinhThanh}
                                            onChange={(values) => onChangeTinhThanh(values)}
                                            closeOnSelect={true}
                                        />
                                        {errors?.tinh_thanh !== null && !form.tinh_thanh_id ? (
                                            <span className="text-danger"> {errors?.tinh_thanh}</span>
                                        ) : null}
                                    </div>
                                    <div className="form-group mt-2">
                                        <label className="fw-bold required fs-6 mb-2"> Quáº­n/Huyá»n </label>
                                        <Select
                                            key={"id"}
                                            className={"quan_huyen"}
                                            labelField={"ten"}
                                            placeholder="Chá»n quáº­n huyá»n"
