                                            onChange={(values) => onChangeQuanHuyen(values)}
                                            closeOnSelect={true}
                                            multi={false}
                                            isDisabled={disQuanHuyen}
                                            value={quanHuyenValue}
                                        />
                                    </div>
                                    <div className="form-group mt-2">
                                        <label className="fw-bold required fs-6 mb-2"> XÃ£/PhÆ°á»ng </label>
                                        <Select
                                            key={"id"}
                                            labelField={"ten"}
                                            placeholder="Chá»n xÃ£ phÆ°á»ng"
                                            options={ListXaPhuong}
                                            onChange={(values) => onChangeXaPhuong(values)}
                                            closeOnSelect={true}
                                            isDisabled={disXaPhuong}
                                            value={xaPhuongValue}
                                        />
                                    </div>
                                    <div className="form-group mt-2">
                                        <label className="fw-bold fs-6 mb-2"> Sá» nhÃ , tÃªn ÄÆ°á»ng </label>
                                        <Form.Control
                                            type="text"
                                            placeholder="Nháº­p sá» nhÃ  tÃªn ÄÆ°á»ng"
                                            style={{ fontSize: 14, fontWeight: 400, height: 38 }}
                                            onChange={(e) => { setField("diachi_coso", e.target.value) }}
                                            value={form.diachi_coso === null ? "" : form.diachi_coso}
                                        />
                                    </div>
                                </div>
                            </div>

                            <div className="col-md-6">
                                <div style={cardBox}>

                                    <div className="mt-7">
                                        <label className="font-weight-bold text-uppercase"> 
                                            ThÃ´ng tin ngÆ°á»i Äá»©ng Äáº§u: 
                                        </label>
                                    </div>

                                    <div className="form-group mt-5">
                                        <label className="fw-bold required fs-6 mb-2"> TÃªn ngÆ°á»i liÃªn há» </label>
                                        <Form.Control
                                            type="text"
                                            placeholder="Nháº­p tÃªn ngÆ°á»i liÃªn há»"
                                            style={{ fontSize: 14, fontWeight: 400, height: 38 }}
                                            onChange={(e) => { setField("ten_nguoi_lien_he", e.target.value) }}
                                            value={ thongTinToChuc?.ten_nguoi_lien_he != null ? thongTinToChuc?.ten_nguoi_lien_he : "" }
                                        />
                                    </div>
                                    <div className="form-group mt-5">
                                        <label className="fw-bold required fs-6 mb-2"> Chá»©c danh </label>
                                        <Form.Control
                                            type="text"
                                            placeholder="Chá»©c danh"
                                            style={{ fontSize: 14, fontWeight: 400, height: 38 }}
                                            onChange={(e) => { setField("chuc_danh_nguoi_lienhe", e.target.value) }}
                                            value={ thongTinToChuc?.chuc_danh_nguoi_lienhe != null ? thongTinToChuc?.chuc_danh_nguoi_lienhe : "" }
                                        />
                                    </div>
                                    <div className="form-group mt-5">
                                        <label className="fw-bold required fs-6 mb-2"> Email </label>
                                        <Form.Control
                                            type="text"
                                            placeholder="Email"
                                            style={{ fontSize: 14, fontWeight: 400, height: 38 }}
                                            onChange={(e) => { setField("email_nguoi_lienhe", e.target.value) }}
                                            value={ thongTinToChuc?.email_nguoi_lienhe != null ? thongTinToChuc?.email_nguoi_lienhe : "" }
