import { AdapterDateFns } from "@mui/x-date-pickers/AdapterDateFns";
import { LocalizationProvider, DesktopDatePicker } from "@mui/x-date-pickers";
import { TextField } from "@mui/material";
import axios from "axios";
// import { vi } from "date-fns/locale";
import { enAU } from "date-fns/locale";
import { useEffect, useState } from "react";
import { Button, Form, Modal } from "react-bootstrap";
import Select from "react-select";
import { toast } from "react-toastify";
import api from "../../../configs/api";
import { async } from "@firebase/util";
// import { GioiTinh, HinhThuc } from "../../../data";
// import moment from "moment";

export default function PopupQuanLyNguoiDung(props) {
  const [edit, setEdit] = useState(false);
  const [showUpdate, setShowUpdate] = useState(false);
  const [form, setForm] = useState({});
  const [display_chuc_vu, setDisplay_chuc_vu] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [listRoles, setListRoles] = useState([]);
  const [selectedgioitinh, setSelectedgioitinh] = useState({
    label: "Nam",
    value: 1,
    id: 1,
  });

 



  useEffect(() => {
    if (props?.show) {
      // resetState();
      console.log("props.edit", props.edit);
      if (props.edit == false) {
        setForm({ ho_ten: "", tai_khoan: "",nhap_lai_mat_khau: "", mat_khau: "" });
        setSelectedDoiTuong(null)
      } else {
        setForm({
          ...props?.data,
        });
        selectVaiTro(props?.data?.chuc_vu)

      }
      console.log(props?.data);
      //   setForm({ ...props?.data, ngay_cap: props?.data.ngay_cap * 1 });
    }
    console.log("propsssss", props);
  }, [props?.show, props?.edit]);

  const selectVaiTro = (x) =>{
    console.log(x);
    const vaitro = optionsDoiTuong.find((e)=> e.value === x)
    if (vaitro) {
      setSelectedDoiTuong(vaitro)
    } else {
      setSelectedDoiTuong(null)
    }

  }

  const [selectedDoiTuong, setSelectedDoiTuong] = useState({});
  const [errors, setErrors] = useState({});
  const setField = (field, value) => {
    setForm({
      ...form,
      [field]: value,
    });
    if (!!errors[field])
      setErrors({
        ...errors,
        [field]: null,
      });
  };

  const formatGroupLabel = (listTemp) => (
    <div style={groupStyles}>
      <span>{listTemp.ten}</span>
    </div>
  );
  const findFormErrors = () => {
    const { mat_khau, nhap_lai_mat_khau } = form;
    const newErrors = {};
