import { ca, vi } from "date-fns/locale";
import { AdapterDateFns } from "@mui/x-date-pickers/AdapterDateFns";
import { LocalizationProvider } from "@mui/x-date-pickers";

import axios from "axios";
import { useDispatch, useSelector } from "react-redux";
import { useEffect, useState } from "react";
import { Form } from "react-bootstrap";
import { toast } from "react-toastify";
import api from "../../../configs/api";
import LoadingOverlay from "react-loading-overlay";
import { BangCap } from "../duoc-si/bang-cap/BangCap";
import { useNavigate, useParams } from "react-router-dom";
import { PageTitle } from "../../../../_metronic/layout/core";
import { ThongTinCapCCHN } from "./components/ThongTinCapCCHN";
import {
  commonActions,
  doiTuong,
  selectDataCVHD,
  selectDataVT,
  selectListIdCheckedPVCM,
  selectListIdCheckedVTHN,
} from "../../../redux-module/common/commonSlice";
import { PhamViVaViTriChuyenMon } from "./components/PhamViChuyenMon";
import { LichSuDaoTao } from "../duoc-si/lich-su-dao-tao/LichSuDaoTao";
import { LichSuThucHanh } from "../duoc-si/lich-su-thuc-hanh/LichSuThucHanh";
import ThongTinCaNhanCapNhat from "./components/thong-tin-ca-nhan/ThongTinCaNhanCapNhat";
import { selectRoleUser } from "../../../redux-module/auth/authSlice";
import moment from "moment";
import LyDoHeader from "./components/LyDoHeader";
import PopupCancel from "../popup/PopupCancel";

LoadingOverlay.propTypes = undefined;
export function ChiTietHoSoThuLyDuThaoCapPhep(props) {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const { id } = useParams();
  const roleUser = useSelector(selectRoleUser);
  const hsDoiTuong = useSelector(doiTuong);
  const DataCVHD = useSelector(selectDataCVHD);
  const DataVT = useSelector(selectDataVT);
  const ListIdCheckedPVCM = useSelector(selectListIdCheckedPVCM);
  const listIdCheckedVTHN = useSelector(selectListIdCheckedVTHN);

  const [isLoading, setIsLoading] = useState(false);
  const [validateCapNhatTT, setValidateCapNhatTT] = useState(false);
  const [validateVTHNvsPVCM, setValidateVTHNvsPVCM] = useState(false);
  const [jsonUpdate, setJsonUpdate] = useState();
  const [thongTinHoSo, setThongTinHoSo] = useState({});
  const [TypeHoSo, setTypeHoSo] = useState("");
  const [DeNghi, setDeNghi] = useState("");
  const [yKienLanhDao, setYKienLanhDao] = useState([]);
  const [ListLoaiChungChi, setListLoaiChungChi] = useState([]);
  //open modal Cancel
  const [isvisibleCancel, setIsvisibleCancel] = useState(false);
  const closeModalCancel = () => setIsvisibleCancel(false);
  const showModalCancel = () => setIsvisibleCancel(true);
  //validate thêm vị trí hành nghề
  const [form, setForm] = useState({});
  const [errors, setErrors] = useState({});

  useEffect(() => {
    if (id) {
      getThongTinHoSo(id);
    }
  }, []);

  useEffect(() => {
    setJsonUpdate({ ...hsDoiTuong, trang_thai_ho_so: 4 });
  }, [hsDoiTuong]);

  const setField = (field, value) => {
    setForm({
      ...form,
      [field]: value,
    });
    dispatch(
      commonActions.setDoiTuong({
        key: field,
        value,
      })
    );
    if (!!errors[field])
      setErrors({
        ...errors,
        [field]: null,
      });
  };

  const getThongTinHoSo = async (id) => {
    await setIsLoading(true);
    axios
      .get(api.API_DETAIL_HO_SO_THU_LY_DUOC_SI + "/" + id)
      .then(async ({ data }) => {
        const { result } = data;
        if (result) {
          setThongTinHoSo(result);
          setTypeHoSo(result?.trang_thai_ho_so);
          setDeNghi(result?.de_nghi);
          setField("noi_dung_de_nghi", result?.yeu_cau_cchnd?.noi_dung_de_nghi);
          setField(
            "noi_dung_tham_xet",
            result?.yeu_cau_cchnd?.noi_dung_tham_xet
          );
          setYKienLanhDao(result?.yeu_cau_cchnd?.y_kien_lanh_dao || []);
          getLoaiChungChi(result?.thu_tuc?.doi_tuong);
        }
      })
      .catch((error) => {
        toast.error("Lấy thông tin chứng chỉ không thành công", {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
        navigate("/error/404");
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  const getLoaiChungChi = (value) => {
    axios
      .get(api.API_GET_LOAI_CHUNG_CHI + "/" + value)
      .then(async ({ data }) => {
        setListLoaiChungChi(data?.results);
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {});
  };

  const updateHoSo = (value) => {
    const { noi_dung_tham_xet, noi_dung_de_nghi } = form;
    setIsLoading(true);
    const jsonUpdateHoSo = {
      ...hsDoiTuong,
      vi_tri_hanh_nghe_ids: listIdCheckedVTHN,
      pham_vi_hoat_dong_chuyen_mon_ids: ListIdCheckedPVCM,
      trang_thai_ho_so: value,
      noi_dung_tham_xet,
      noi_dung_de_nghi,
      y_kien_lanh_dao: yKienLanhDao,
    };
    axios
      .put(
        api.API_YEU_CAU_CHUNG_CHI_HANH_NGHE + "/update_du_thao/" + id,
        jsonUpdateHoSo
      )
      .then(async ({ data }) => {
        toast.success(data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "success",
        });
        navigate(0);
      })
      .catch((error) => {
        toast.error(error?.data?.errors, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  const updateHoSoCVHD = (value) => {
    const { noi_dung_tham_xet, noi_dung_de_nghi } = form;
    setIsLoading(true);
    const jsonUpdateHoSoCVHD = {
      ...DataCVHD,
      ngay_hieu_luc: new Date(DataCVHD.ngay_hieu_luc).getTime(),
      ngay_het_han: new Date(DataCVHD.ngay_het_han).getTime(),
      ngay_het_han_cchnd_cu: new Date(DataCVHD.ngay_het_han_cchnd_cu).getTime(),
      so_cchnd: String(DataCVHD.so_cchnd),
      trang_thai_ho_so: value,
      noi_dung_tham_xet,
      noi_dung_de_nghi,
      yeu_cau_phien_dich: true,
    };
    axios
      .put(
        api.API_YEU_CAU_CHUNG_CHI_HANH_NGHE + "/trinh_hoi_dong/" + id,
        jsonUpdateHoSoCVHD
      )
      .then(async ({ data }) => {
        toast.success(data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "success",
        });
        navigate(0);
      })
      .catch((error) => {
        toast.error(error?.data?.errors, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  const updateHoSoVT = (value) => {
    const { so_quyet_dinh, ngay_quyet_dinh } = form;
    setIsLoading(true);
    const json = {
      ...DataVT,
      ids: [`${id}`],
      trang_thai_ho_so: value,
      ngay_quyet_dinh: new Date(DataVT.ngay_quyet_dinh).getTime(),
    };
    axios
      .put(api.API_DUYET_MULTI_YEU_CAU_CHUNG_CHI, json)
      .then(async ({ data }) => {
        await toast.success(data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "success",
        });
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  const usersBreadcrumbs = [
    {
      title: "Trang chủ",
      path: "/",
      isSeparator: false,
      isActive: false,
    },
    {
      title: "",
      path: "",
      isSeparator: true,
      isActive: false,
    },
  ];
  const renderBtnBottom = () => {
    switch (roleUser[0]?.ten_en) {
      case "chuyenvien":
        return (
          <div className="col-12 mt-4 mb-5 text-center">
            {TypeHoSo === "4" || TypeHoSo === "5" ? (
              <>
                <div className="col-12 mt-4 mb-5 text-center">
                  <div
                    onClick={() => {
                      navigate(-1);
                    }}
                    className="btn bg-primary2 text-white px-4 py-2 mx-2 font-weight-bold"
                  >
                    QUAY LẠI
                  </div>
                  <div
                    onClick={() => {
                      // setValidateCapNhatTT(!validateCapNhatTT);
                      updateHoSo(thongTinHoSo?.trang_thai_ho_so);
                    }}
                    className="btn btn-success text-white px-4 py-2 mx-2 font-weight-bold "
                  >
                    LƯU
                  </div>
                  <div
                    onClick={() => {
                      updateHoSo("6");
                    }}
                    className="btn btn-primary text-white px-4 py-2 mx-2 font-weight-bold "
                  >
                    TRÌNH LÃNH ĐẠO
                  </div>
                  <div
                    onClick={() => {}}
                    className="btn bg-blue2 text-white px-4 py-2 mx-2 font-weight-bold "
                  >
                    IN PHIẾU DỰ THẢO
                  </div>
                </div>
              </>
            ) : (
              <div
                onClick={() => {
                  navigate(-1);
                }}
                className="btn bg-primary2 text-white px-4 py-2 mx-2 font-weight-bold"
              >
                QUAY LẠI
              </div>
            )}
          </div>
        );
        break;
      case "lanhdao":
        return (
          <div className="col-12 mt-4 mb-5 text-center">
            {TypeHoSo === "6" ? (
              <>
                <div
                  onClick={() => {
                    navigate(-1);
                  }}
                  className="btn bg-primary2 text-white px-4 py-2 mx-2 font-weight-bold"
                >
                  QUAY LẠI
                </div>
                <div
                  onClick={() => {
                    updateHoSo(thongTinHoSo?.trang_thai_ho_so);
                  }}
                  className="btn btn-success text-white px-4 py-2 mx-2 font-weight-bold "
                >
                  LƯU
                </div>
                <div
                  onClick={() => {
                    updateHoSo("9");
                  }}
                  className="btn btn-primary text-white px-4 py-2 mx-2 font-weight-bold "
                >
                  CHUYỂN CHUYÊN VIÊN HỘI ĐỒNG
                </div>
                <div
                  onClick={() => {
                    navigate(
                      `/admin/danhsach-hoso-thuly-duocsi/yeucaubosung/${id}`
                    );
                  }}
                  className="btn btn-warning text-white px-4 py-2 mx-2 font-weight-bold "
                >
                  BỔ SUNG
                </div>
                <div
                  onClick={() => {
                    showModalCancel();
                  }}
                  className="btn btn-danger text-white px-4 py-2 mx-2 font-weight-bold "
                >
                  TỪ CHỐI
                </div>
                <div
                  onClick={() => {}}
                  className="btn bg-blue2 text-white px-4 py-2 mx-2 font-weight-bold "
                >
                  IN PHIẾU DỰ THẢO
                </div>
              </>
            ) : (
              <div
                onClick={() => {
                  navigate(-1);
                }}
                className="btn bg-primary2 text-white px-4 py-2 mx-2 font-weight-bold"
              >
                QUAY LẠI
              </div>
            )}
          </div>
        );
        break;
      case "chuyenvienhoidong":
        return (
          <div className="col-12 mt-4 mb-5 text-center">
            {TypeHoSo === "9" ? (
              <>
                <div
                  onClick={() => {
                    navigate(-1);
                  }}
                  className="btn bg-primary2 text-white px-4 py-2 mx-2 font-weight-bold"
                >
                  QUAY LẠI
                </div>
                <div
                  onClick={() => {
                    updateHoSoCVHD(thongTinHoSo?.trang_thai_ho_so);
                  }}
                  className="btn btn-success text-white px-4 py-2 mx-2 font-weight-bold "
                >
                  LƯU
                </div>
                <div
                  onClick={() => {
                    updateHoSoCVHD("2");
                  }}
                  className="btn btn-primary text-white px-4 py-2 mx-2 font-weight-bold "
                >
                  CHUYỂN CHUYÊN VIÊN THỤ LÝ
                </div>
                <div
                  onClick={() => {
                    updateHoSoCVHD("10");
                  }}
                  className="btn btn-warning text-white px-4 py-2 mx-2 font-weight-bold "
                >
                  CHUYỂN VĂN THƯ CẤP SỐ
                </div>
                <div
                  onClick={() => {}}
                  className="btn bg-blue2 text-white px-4 py-2 mx-2 font-weight-bold "
                >
                  IN PHIẾU DỰ THẢO
                </div>
              </>
            ) : (
              <div
                onClick={() => {
                  navigate(-1);
                }}
                className="btn bg-primary2 text-white px-4 py-2 mx-2 font-weight-bold"
              >
                QUAY LẠI
              </div>
            )}
          </div>
        );
        break;
      case "vanthu":
        return (
          <div className="col-12 mt-4 mb-5 text-center">
            {TypeHoSo === "10" ? (
              <>
                <div
                  onClick={() => {
                    navigate(-1);
                  }}
                  className="btn bg-primary2 text-white px-4 py-2 mx-2 font-weight-bold"
                >
                  QUAY LẠI
                </div>
                <div
                  onClick={() => {
                    updateHoSoVT(thongTinHoSo?.trang_thai_ho_so);
                  }}
                  className="btn btn-success text-white px-4 py-2 mx-2 font-weight-bold "
                >
                  LƯU
                </div>
                <div
                  onClick={() => {
                    updateHoSoVT("11");
                  }}
                  className="btn btn-primary text-white px-4 py-2 mx-2 font-weight-bold "
                >
                  HOÀN THÀNH
                </div>
              </>
            ) : (
              <div
                onClick={() => {
                  navigate(-1);
                }}
                className="btn bg-primary2 text-white px-4 py-2 mx-2 font-weight-bold"
              >
                QUAY LẠI
              </div>
            )}

            {/* <div
              onClick={() => {}}
              className="btn btn-success text-white px-4 py-2 mx-2 font-weight-bold "
            >
              Lưu
            </div>
            <div
              onClick={() => {
                updateHoSo(thongTinHoSo.id);
              }}
              className="btn btn-primary text-white px-4 py-2 mx-2 font-weight-bold "
            >
              Trình lãnh đạo
            </div>
            <div
              onClick={() => {}}
              className="btn bg-blue2 text-white px-4 py-2 mx-2 font-weight-bold "
            >
              In phiếu dự thảo
            </div> */}
          </div>
        );
      default:
        break;
    }
  };

  const callBackValidateTTCN = () => {
    setValidateCapNhatTT(false);
  };

  const callBackValidateVTHNvsPVCM = () => {
    setValidateVTHNvsPVCM(false);
  };

  const addRow = async () => {
    const hoTen = yKienLanhDao[yKienLanhDao?.length - 1]?.ho_ten;
    const noiDung = yKienLanhDao[yKienLanhDao?.length - 1]?.noi_dung;
    console.log("log  ho ten", hoTen);
    console.log("log noi dung", noiDung);

    if ((!noiDung || !hoTen) && yKienLanhDao?.length >= 1) {
      toast.error("Vui lòng nhập đầy đủ ý kiến lãnh đạo.", {
        position: "top-right",
        autoClose: 2000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        toastId: "error",
      });
    } else {
      let newRow = initRow;
      var stt = 0;
      if (yKienLanhDao.length == 0) {
        stt = 1;
      } else {
        stt = yKienLanhDao[yKienLanhDao.length - 1].stt + 1;
      }
      newRow.stt = stt;
      await setYKienLanhDao(yKienLanhDao.concat(newRow));
    }
  };

  const setRowDateValue = async (e, ndth, type) => {
    const dataRow = {
      ...ndth,
      [type]: e,
    };
    const updatedData = yKienLanhDao.map((x) =>
      x.stt === ndth.stt ? { ...x, ...dataRow } : x
    );
    await setYKienLanhDao(updatedData);
  };

  const initRow = {
    stt: 1,
    ho_ten: "",
    noi_dung: "",
    thoi_gian: new Date(),
  };

  const removeRow = (stt) => {
    const formArray = yKienLanhDao.filter((row) => row.stt != stt);
    setYKienLanhDao(formArray);
  };

  const renderTable = (arrayTest) => {
    return (
      <>
        {yKienLanhDao?.length > 0 ? (
          <table className="table mt-4">
            <thead>
              <tr>
                <th className="fw-bolder px-3" style={{ width: "4%" }}>
                  STT
                </th>
                <th className="fw-bolder px-3" style={{ width: "25%" }}>
                  Họ tên
                </th>
                <th className="fw-bolder px-3" style={{ width: "45%" }}>
                  Nội dung ý kiến
                </th>
                <th className="fw-bolder px-3">Thời gian</th>
                <th className="fw-bolder px-3"></th>
              </tr>
            </thead>
            <tbody>
              {arrayTest.map((ykld, index) => (
                <tr key={ykld.stt}>
                  <td
                    className="px-3 py-0 text-center"
                    style={{ verticalAlign: "middle" }}
                  >
                    <span className="fw-bold">{ykld.stt}</span>
                  </td>
                  <td className="px-3 py-0">
                    <Form.Control
                      type="text"
                      placeholder="Nhập họ tên"
                      style={{
                        fontSize: 14,
                        fontWeight: 400,
                        height: "38px",
                        margin: "10px 0 10px 0",
                      }}
                      onChange={(e) => {
                        setRowDateValue(e.target.value, ykld, "ho_ten");
                      }}
                      value={ykld?.ho_ten}
                    />
                  </td>
                  <td className="px-3 py-0">
                    <Form.Control
                      type="text"
                      placeholder="Nhập nội dung ý kiến"
                      style={{
                        fontSize: 14,
                        fontWeight: 400,
                        height: "38px",
                        margin: "10px 0 10px 0",
                      }}
                      onChange={(e) => {
                        setRowDateValue(e.target.value, ykld, "noi_dung");
                      }}
                      value={ykld?.noi_dung}
                    />
                  </td>
                  <td className="px-3 py-0" style={{ verticalAlign: "middle" }}>
                    <span>
                      {moment(new Date(ykld?.thoi_gian)).format(
                        "hh:mm DD/MM/YYYY"
                      )}
                    </span>
                  </td>
                  <td
                    className="px-3 py-0"
                    style={{ verticalAlign: "middle", textAlign: "center" }}
                  >
                    <button
                      className="btn btn-danger px-0 py-0 mb-4"
                      style={{
                        width: "38px",
                        height: "38px",
                        fontSize: "12px",
                        margin: "10px 0 10px 0",
                      }}
                      onClick={() => {
                        removeRow(ykld.stt);
                      }}
                    >
                      X
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : null}
      </>
    );
  };

  return (
    <div>
      <LocalizationProvider dateAdapter={AdapterDateFns} locale={vi}>
        <PageTitle breadcrumbs={usersBreadcrumbs}>Chi tiết hồ sơ</PageTitle>
        <LoadingOverlay spinner active={isLoading} text="Đang tải dữ liệu ...">
          <LyDoHeader thongTinHoSo={thongTinHoSo} />
          {/* Thủ tục */}
          <div className="card py-6 px-6 mx-3 my-3">
            <label className="fw-bold fs-5">THỦ TỤC</label>
            <div className="text py-2">
              {thongTinHoSo?.thu_tuc ? thongTinHoSo?.thu_tuc?.ten : ""}
            </div>
          </div>
          {/* THÔNG TIN CÁ NHÂN */}
          <ThongTinCaNhanCapNhat
            validate={validateCapNhatTT}
            changeValidate={callBackValidateTTCN}
            data={thongTinHoSo?.yeu_cau_cchnd?.doi_tuong}
          />
          {/* BẰNG CẤP  */}
          <BangCap userId={thongTinHoSo?.user?.id} />
          {/* THỰC HÀNH TẠI CƠ SỞ DƯỢC */}
          <LichSuThucHanh userId={thongTinHoSo?.user?.id} />
          {/* ĐÀO TẠO LIÊN TỤC */}
          <LichSuDaoTao userId={thongTinHoSo?.user?.id} />

          <div className="card py-7 px-7 mx-3 my-3">
            <PhamViVaViTriChuyenMon
              validate={validateVTHNvsPVCM}
              changeValidate={callBackValidateVTHNvsPVCM}
              hideGhiChu={true}
              dataDMPVCM={thongTinHoSo?.danh_muc_pham_vi_chuyen_mon}
              dataVTHN={thongTinHoSo?.danh_muc_vi_tri_hanh_nghe}
              listCheckedDMPVCM={
                thongTinHoSo?.yeu_cau_cchnd?.pham_vi_chuyen_mon?.id || []
              }
              listCheckedVTHN={
                thongTinHoSo?.yeu_cau_cchnd?.vi_tri_hanh_nghe_cchn?.id || []
              }
            />
          </div>

          <ThongTinCapCCHN
            loaiThuTuc={thongTinHoSo?.thu_tuc?.loai}
            data={thongTinHoSo?.yeu_cau_cchnd}
            ListLoaiChungChi={ListLoaiChungChi}
          />

          {/* KẾT QUẢ THẨM XÉT */}
          <div className="card-box py-6 px-6 mx-3 my-3">
            <label className="fw-bold fs-5 mb-2">KẾT QUẢ THẨM XÉT</label>
            <div className="col-12 d-flex px-0">
              <div className="col-6 pl-1">
                <label>1. Nội dung thẩm xét, lý do</label>
                <Form.Control
                  as="textarea"
                  rows={3}
                  style={{ fontSize: 14, fontWeight: 500 }}
                  value={
                    form.noi_dung_tham_xet === null
                      ? ""
                      : form.noi_dung_tham_xet
                  }
                  onChange={(e) => {
                    setField("noi_dung_tham_xet", e.target.value);
                  }}
                  isInvalid={!!errors.noi_dung_tham_xet}
                />
              </div>
              <div className="col-6 px-1">
                <label>2. Nội dung đề nghị</label>
                <Form.Control
                  as="textarea"
                  rows={3}
                  style={{ fontSize: 14, fontWeight: 500 }}
                  value={
                    form.noi_dung_de_nghi === null ? "" : form.noi_dung_de_nghi
                  }
                  onChange={(e) => {
                    setField("noi_dung_de_nghi", e.target.value);
                  }}
                  isInvalid={!!errors.noi_dung_de_nghi}
                />
              </div>
            </div>
          </div>
          {/* Ý KIẾN LÃNH ĐẠO */}
          <div className="card py-6 px-6 mx-3 my-3">
            <div className="row pb-2">
              <div className="col-md-6 col-sm-6">
                <label className="fw-bold fs-5">
                  Ý KIẾN CỦA LÃNH ĐẠO PHÒNG
                </label>
              </div>
              <div className="col-md-6 col-sm-6 text-end">
                <button
                  className="btn btn-primary ms-3 px-1 py-2 fw-normal"
                  style={{ width: "120px" }}
                  onClick={() => {
                    addRow();
                  }}
                >
                  Thêm mới
                </button>
              </div>
            </div>
            {renderTable(yKienLanhDao)}
          </div>
          <div className="row w-100">{renderBtnBottom()}</div>
        </LoadingOverlay>
      </LocalizationProvider>
      <PopupCancel
        // data={}
        id={id}
        title={"Xác nhận từ chối"}
        show={isvisibleCancel}
        onHide={closeModalCancel}
      />
    </div>
  );
