import { vi } from "date-fns/locale";
import { LocalizationProvider } from "@mui/x-date-pickers";
import { AdapterDateFns } from "@mui/x-date-pickers/AdapterDateFns";

import DataTable from "react-data-table-component";
import { useEffect, useState } from "react";
import api from "../../../configs/api";
import axios from "axios";


export function DanhSachChungChi() {

    const [chungChiList, setChungChiList] = useState([])

    useEffect(() => {
        getListChungChi();

    })

    const getListChungChi = () => {
        axios
            .post(api.API_CHUNG_CHI_HANH_NGHE_DA_CAP + "/get_list", {})
            .then(({data}) => {
                console.log(data)
                // setChungChiList()
            })
            .catch((error) => {
                if (error.response.data) {
                    toast.error(error.response.data.msg, {
                        position: "top-right",
                        autoClose: 2000,
                        hideProgressBar: false,
                        closeOnClick: true,
                        pauseOnHover: true,
                        draggable: true,
                        progress: undefined,
                        toastId: "success",
                    });
                }
            })
            .finally(() => {

            });
    }

    return (
        <LocalizationProvider dateAdapter={AdapterDateFns} locale={vi}>
            <div className="bg-white py-5 px-5 my-5">
                <div className="row" style={{ padding: "8px 16px 8px 16px" }}>
                    <div className="col-12 text-center">
                        <label className="text-primary fw-bold fs-1">DANH SÁCH CHỨNG CHỈ</label>
                    </div>
                    <div className="col-9 mt-5">
                        <input className="form-control" type="text" placeholder="Tìm kiếm chứng chỉ"></input>
                    </div>
                    <div className="col-3 mt-5 text-end">
                        <button className="btn btn-success ms-3">Import</button>
                        <button className="btn btn-primary ms-3">Export</button>

                    </div>
                </div>

                <DataTable

                />

            </div>
        </LocalizationProvider>
    )
}
