import { DesktopDatePicker, LocalizationProvider } from "@mui/x-date-pickers";
import { AdapterDateFns } from "@mui/x-date-pickers/AdapterDateFns";
import { enAU } from "date-fns/locale";

import {
  Checkbox,
  FormControl,
  FormControlLabel,
  Radio,
  RadioGroup,
  TextField,
} from "@mui/material";
import { useEffect, useState } from "react";
import { Form } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { selectRoleUser } from "../../../../../app/redux-module/auth/authSlice";
import { commonActions } from "../../../../redux-module/common/commonSlice";
import { HinhThuc, LoaiMaCCHND } from "../../../../data";
import Select from "react-select";
import api from "../../../../configs/api";
import axios from "axios";
import { toast } from "react-toastify";
import { set } from "date-fns";

export function ThongTinCapCCHN(props) {
  const dispatch = useDispatch();
  const roleUser = useSelector(selectRoleUser)[0].ten_en;

  const [loaiThuTuc, setLoaiThuTuc] = useState("");
  const [isThayThe, setIsThayThe] = useState(true);
  const [valueThayThe, setValueThayThe] = useState();
  const [_isdisabled, setisDisabled] = useState(true);
  const [ValueLoaiMaCCHND, setValueLoaiMaCCHND] = useState({});
  const [ValueHinhThuc, setValueHinhThuc] = useState("");
  const [listHoiDong, setListHoiDong] = useState([]);
  const [ValueHoiDong, setValueHoiDong] = useState("");
  const [DateNgayQD, setDateNgayQD] = useState(new Date());
  const [ThuTucType, setThuTucType] = useState("");
  const [ListLoaiChungChi, setListLoaiChungChi] = useState([]);

  //validate thêm vị trí hành nghề
  const [form, setForm] = useState();
  const [errors, setErrors] = useState({});
  const verCenter = {
    display: "flex",
    alignItems: "center",
  };

  useEffect(() => {
    if (roleUser === "chuyenvienhoidong" || roleUser === "vanthu") {
      console.log("false");
      setisDisabled(false);
    } else {
      console.log("true");
      setisDisabled(true);
    }
    getListHoiDong();
  }, []);

  //selectLoaiChungChi
  useEffect(() => {
    let tempLoaiMaCCHND = props?.ListLoaiChungChi?.find(
      (e) => e.value === form?.loai_ma_cchnd
    );
    setValueLoaiMaCCHND(tempLoaiMaCCHND);
  }, [form?.loai_ma_cchnd, props?.ListLoaiChungChi]);

  //selectHoiDong
  useEffect(() => {
    let tempHoiDong = listHoiDong?.find((e) => e.id === form?.hoi_dong_id);
    setValueHoiDong(tempHoiDong);
  }, [form?.hoi_dong_id, listHoiDong]);

  //selectHinhThuc
  const selectValueHinhThucCap = (id) => {
    let tempHinhThuc = HinhThuc?.find((e) => e.value === id);
    setValueHinhThuc(tempHinhThuc);
  };

  //getListLoaiChungChi
  useEffect(() => {
    const { ListLoaiChungChi } = props;
    if (ListLoaiChungChi) {
      setListLoaiChungChi(ListLoaiChungChi);
    }
  }, [props?.ListLoaiChungChi]);

  useEffect(() => {
    const { data } = props;
    if (data) {
      let dataForm = {
        loai_ma_cchnd: data?.loai_ma_cchnd || "",
        lan_cap_thu: data?.lan_cap_thu || "",
        hinh_thuc: data?.hinh_thuc || "",
        hoi_dong_id: data?.hoi_dong_id || "",
        ngay_hieu_luc: data?.ngay_hieu_luc || null,
        ngay_het_han: data?.ngay_het_han || null,
        thay_the_cchnd: data?.thay_the_cchnd || "",
        noi_dung_tham_xet: data?.noi_dung_tham_xet || "",
        noi_dung_de_nghi: data?.noi_dung_de_nghi || "",
        ngay_het_han_cchnd_cu: data?.ngay_het_han_cchnd_cu || null,
        so_cchnd: data?.so_cchnd || "",
      };
      setForm({
        ...dataForm,
        ngay_quyet_dinh: data?.ngay_quyet_dinh || null,
        so_quyet_dinh: data?.so_quyet_dinh || "",
      });
      selectValueHinhThucCap(data?.hinh_thuc);
      setLoaiThuTuc(props?.loaiThuTuc);
      dispatch(commonActions.setDataCVHDAll(dataForm));
      dispatch(
        commonActions.setDataVTAll({
          ngay_quyet_dinh: data?.ngay_quyet_dinh || null,
          so_quyet_dinh: data?.so_quyet_dinh || "",
        })
      );
    }
  }, [props?.data]);

  const handleThayThe = () => {
    setIsThayThe(!isThayThe);
  };

  const setField = (field, value) => {
    setForm({
      ...form,
      [field]: value,
    });
    dispatch(
      commonActions.setDataCVHDItem({
        key: field,
        value,
      })
    );
    if (!!errors[field])
      setErrors({
        ...errors,
        [field]: null,
      });
  };

  const setFieldVT = (field, value) => {
    setForm({
      ...form,
      [field]: value,
    });
    dispatch(
      commonActions.setDataVTItem({
        key: field,
        value,
      })
    );
    if (!!errors[field])
      setErrors({
        ...errors,
        [field]: null,
      });
  };

  const getListHoiDong = () => {
    axios
      .post(api.API_GET_HOI_DONG_GET_LIST + `?page=1&per_page=100`, {
        search_ten: "",
      })
      .then(async ({ data }) => {
        let resultsHoiDong = data?.results;
        resultsHoiDong.forEach((e) => {
          e.label = e.ten_hoi_dong;
          e.value = e.id;
        });
        setListHoiDong(data?.results);
      })
      .catch((error) => {})
      .finally(() => {});
  };

  const findFormErrors = () => {
    const { ho } = form;
    const newErrors = {};
    // name errors
    if (!ho || ho === "") newErrors.ho = "Họ không được bỏ trống!";

    return newErrors;
  };

  const handleSubmit = async () => {
    const newErrors = findFormErrors();
    // Conditional logic:
    if (Object.keys(newErrors).length > 0) {
      console.log(newErrors);
      // We got errors!
      setErrors(newErrors);
    } else {
      // No errors! Put any logic here for the form submission!
    }
  };

  const onChangeLoaiChungChi = (values) => {
    setValueLoaiMaCCHND(values);
    setField("loai_ma_cchnd", values.value);
    axios
      .get(api.API_GET_LOAI_CHUNG_CHI_CHI_TIET + "/" + values?.value)
      .then(async ({ data }) => {
        if (data?.so_da_cap) {
          setField("so_cchnd", data?.so_da_cap);
        }
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {});
  };

  return (
    <LocalizationProvider dateAdapter={AdapterDateFns} locale={enAU}>
      <div className="card-box bg-white py-6 px-6 mx-3 my-3">
        <div className="row pb-5">
          <div className="col-md-6 col-sm-6">
            <label className="fw-bold fs-5">THÔNG TIN CẤP CCHN</label>
          </div>
        </div>
        <div className="row px-2 py-2">
          <div className="col-6">
            <div className="row">
              <div className="col-3" style={verCenter}>
                <span className="fw-bold">Số CCHND</span>
              </div>
              <div className="col-9" style={verCenter}>
                <div className="input-group">
                  <Form.Control
                    type="text"
                    style={{ fontSize: 14, fontWeight: 400, height: "38px" }}
                    disabled={true}
                    value={form?.so_cchnd || ""}
                  ></Form.Control>
                  <span className="input-group-text" style={{ height: "38px" }}>
                    /
                  </span>
                  <Select
                    key={"id"}
                    labelField={"label"}
                    options={ListLoaiChungChi}
                    onChange={(values) => {
                      onChangeLoaiChungChi(values);
                    }}
                    closeOnSelect={true}
                    value={ValueLoaiMaCCHND}
                    isDisabled={_isdisabled}
                  />
                </div>
              </div>

              <div className="col-5" style={verCenter}>
                <FormControl>
                  <RadioGroup
                    aria-labelledby="demo-controlled-radio-buttons-group"
                    name="controlled-radio-buttons-group"
                    value={loaiThuTuc}
                    // onChange={(e) => setLoaiThuTuc(e.target.value)}
                  >
                    <FormControlLabel
                      key={"1"}
                      value={"1"}
                      control={<Radio />}
                      label={"Cấp mới"}
                      disabled={true}
                    />
                    <FormControlLabel
                      key={"2"}
                      value={"2" || "3"}
                      control={<Radio />}
                      label={"Cấp lại/Điều chỉnh"}
                      disabled={true}
                    />
                  </RadioGroup>
                </FormControl>
              </div>

              <div className="col-2" style={verCenter}>
                <span className="fw-bold">Lần thứ</span>
              </div>
              <div className="col-5" style={verCenter}>
                <Form.Control
                  type="text"
                  style={{ fontSize: 14, fontWeight: 400, height: "38px" }}
                  disabled={loaiThuTuc === "1" ? true : false}
                  value={form?.lan_cap_thu || ""}
                  onChange={(e) => {
                    setField("lan_cap_thu", e.target.value);
                  }}
                ></Form.Control>
              </div>

              <div className="col-3 mb-3" style={verCenter}>
                <span className="fw-bold">Hình thức cấp</span>
              </div>
              <div className="col-9 mb-3">
                <Select
                  key={"id"}
                  labelField={"label"}
                  placeholder={"Chọn hình thức cấp"}
                  options={HinhThuc}
                  onChange={(values) => {
                    setValueHinhThuc(values);
                    setField("hinh_thuc", values.id);
                  }}
                  closeOnSelect={true}
                  value={ValueHinhThuc}
                  isDisabled={_isdisabled}
                />
              </div>

              <div className="col-3 mb-3" style={verCenter}>
                <span className="fw-bold">Tên hội đồng</span>
              </div>
              <div className="col-9 mb-3">
                <Select
                  key={"id"}
                  labelField={"label"}
                  placeholder={"Chọn hội đồng"}
                  options={listHoiDong}
                  onChange={(values) => {
                    setValueHoiDong(values);
                    setField("hoi_dong_id", values?.id);
                  }}
                  closeOnSelect={true}
                  value={ValueHoiDong}
                  isDisabled={_isdisabled}
                />
              </div>

              <div className="col-3 mb-3" style={verCenter}>
                <span className="fw-bold">Theo QĐ số</span>
              </div>
              <div className="col-9 mb-3" style={verCenter}>
                <Form.Control
                  type="text"
                  style={{ fontSize: 14, fontWeight: 400, height: "38px" }}
                  disabled={
                    !_isdisabled && roleUser === "vanthu" ? false : true
                  }
                  value={form?.so_quyet_dinh || ""}
                  onChange={(e) => {
                    setFieldVT("so_quyet_dinh", e.target.value);
                  }}
                ></Form.Control>
              </div>
            </div>
          </div>
          <div className="col-6">
            <div className="row">
              <div className="col-3 mb-3" style={verCenter}>
                <span className="fw-bold">Hiệu lực từ</span>
              </div>
              <div className="col-9 mb-3" style={verCenter}>
                <DesktopDatePicker
                  inputFormat="dd/MM/yyyy"
                  label=" "
                  value={form?.ngay_hieu_luc || null}
                  onChange={(value) => {
                    setField("ngay_hieu_luc", value);
                  }}
                  renderInput={(params) => (
                    <TextField size="small" {...params} />
                  )}
                  disabled={_isdisabled}
                />
              </div>

              <div className="col-3 mb-3" style={verCenter}>
                <span className="fw-bold">Ngày hết hạn</span>
              </div>
              <div className="col-9 mb-3" style={verCenter}>
                <DesktopDatePicker
                  inputFormat="dd/MM/yyyy"
                  label=" "
                  value={form?.ngay_het_han || null}
                  onChange={(value) => {
                    setField("ngay_het_han", value);
                  }}
                  renderInput={(params) => (
                    <TextField size="small" {...params} />
                  )}
                  disabled={_isdisabled}
                />
              </div>

              <div className="col-3 mb-3" style={verCenter}>
                <FormControl>
                  <FormControlLabel
                    key={""}
                    value={""}
                    control={<Checkbox />}
                    label={"Thay thế số CCHN cũ"}
                    disabled={_isdisabled}
                    onChange={handleThayThe}
                  />
                </FormControl>
              </div>
              <div className="col-3 mb-3" style={verCenter}>
                <span className="fw-bold">Ngày cấp CCHND cũ</span>
              </div>
              <div className="col-6 mb-3" style={verCenter}>
                <DesktopDatePicker
                  inputFormat="dd/MM/yyyy"
                  label=" "
                  value={form?.ngay_het_han_cchnd_cu || null}
                  onChange={(value) => {
                    setField("ngay_het_han_cchnd_cu", value);
                  }}
                  renderInput={(params) => (
                    <TextField size="small" {...params} />
                  )}
                  disabled={isThayThe}
                />
              </div>

              <div className="col-3 mb-3" style={verCenter}>
                <span className="fw-bold">Số CCHN cũ</span>
              </div>
              <div className="col-9 mb-3" style={verCenter}>
                <Form.Control
                  type="text"
                  style={{ fontSize: 14, fontWeight: 400, height: "38px" }}
                  disabled={isThayThe}
                  value={form?.thay_the_cchnd || ""}
                  onChange={(e) => {
                    setField("thay_the_cchnd", e.target.value);
                  }}
                ></Form.Control>
              </div>

              <div className="col-12 mb-3" style={verCenter}>
                <FormControlLabel
                  key={""}
                  value={""}
                  control={<Checkbox />}
                  label={"Yêu cầu phiên dịch nước ngoài"}
                  disabled={_isdisabled}
                  onChange={(e) => {
                    setField("");
                    console.log("e", e);
                  }}
                />
              </div>

              <div className="col-3 mb-3" style={verCenter}>
                <span className="fw-bold">Ngày QĐ</span>
              </div>
              <div className="col-9 mb-3" style={verCenter}>
                <DesktopDatePicker
                  inputFormat="dd/MM/yyyy"
                  label=" "
                  value={form?.ngay_quyet_dinh || null}
                  onChange={(value) => {
                    setFieldVT("ngay_quyet_dinh", value);
                  }}
                  renderInput={(params) => (
                    <TextField size="small" {...params} />
                  )}
                  disabled={
                    !_isdisabled && roleUser === "vanthu" ? false : true
                  }
                />
              </div>
            </div>
          </div>
        </div>
      </div>
    </LocalizationProvider>
  );
}
