import { AdapterDateFns } from "@mui/x-date-pickers/AdapterDateFns";
import { LocalizationProvider, DesktopDatePicker } from "@mui/x-date-pickers";
import { Checkbox, FormControlLabel, TextField } from "@mui/material";
import axios from "axios";
import { vi } from "date-fns/locale";
import { useEffect, useState } from "react";
import { Button, Form, Modal, Spinner } from "react-bootstrap";
import Select from "react-select";
import { toast } from "react-toastify";
import api from "../../../configs/api";
import { GioiTinh, HinhThuc } from "../../../data";
import { useDispatch, useSelector } from "react-redux";
import {
  commonActions,
  selectListIdCheckedPVCM,
  selectListIdCheckedVTHN,
} from "../../../redux-module/common/commonSlice";
import LoadingIndicator from "../../../components/LoadingIndicator";

export default function PopupCapNhatYeuCauLienKetDS(props) {
  const dispatch = useDispatch();
  const [listVanBangChuyenMon, setListVanBangChuyenMon] = useState([]);
  const [listNoiCapCMND, setListNoiCapCMND] = useState([]);
  const [ListPVCM, setListPVCM] = useState([]);
  const [ListVTHN, setListVTHN] = useState([]);
  const [selectedGioiTinh, setSelectedGioiTinh] = useState(GioiTinh[0]);
  const [selectedNoiCapCMND, setSelectedNoiCapCMND] = useState(null);
  const [selectedHinhThuc, setSelectedHinhThuc] = useState({});
  const [listIdPVCM, setListIdPVCM] = useState([]);
  const [listIdVTHN, setListIdVTHN] = useState([]);
  const [FileAnhChanDung, setFileAnhChanDung] = useState([]);
  const [FileCMNDCCCD, setFileCMNDCCCD] = useState([]);
  const [FileGiayToKhac, setFileGiayToKhac] = useState([]);
  const [FileHinhChupCCHND, setFileHinhChupCCHND] = useState([]);
  const [selectedVanBang, setSelectedVanBang] = useState({});
  const ListIdCheckedPVCM = useSelector(selectListIdCheckedPVCM);
  const listIdCheckedVTHN = useSelector(selectListIdCheckedVTHN);
  const [isLoading, setIsLoading] = useState(false);
  const [FileDelAnhChanDung, setFileDelAnhChanDung] = useState([]);
  const [FileDelCMNDCCCD, setFileDelCMNDCCCD] = useState([]);
  const [FileDelGiayToKhac, setFileDelGiayToKhac] = useState([]);
  const [FileDelHinhChupCCHND, setFileDelHinhChupCCHND] = useState([]);
  const [LoadingBtn, setLoadingBtn] = useState(false);

  useEffect(() => {
    if (props?.show) {
      getCATinhThanh();
      getThongTin();
    }
  }, [props?.show]);

  //validate ttcchn
  const [formCCHND, setFormCCHND] = useState({});
  const [errorsCCHND, setErrorsCCHND] = useState({});
  const setFieldCCHND = (field, value) => {
    setFormCCHND({
      ...formCCHND,
      [field]: value,
    });
    if (!!errors[field])
      setErrorsCCHND({
        ...errorsCCHND,
        [field]: null,
      });
  };
  const findFormErrors = () => {
    const { ten } = formTTHC;
    const newErrors = {};
    // name errors
    if (!ten || ten === "") newErrors.ten = "Tên không được bỏ trống!";

    return newErrors;
  };
  //validate tthc
  const [formTTHC, setFormTTHC] = useState({});
  const [errors, setErrors] = useState({});
  const setField = (field, value) => {
    setFormTTHC({
      ...formTTHC,
      [field]: value,
    });
    if (!!errors[field])
      setErrors({
        ...errors,
        [field]: null,
      });
  };
  const findFormErrorsCCHND = () => {
    const { ten } = formTTHC;
    const newErrors = {};
    // name errors
    if (!ten || ten === "") newErrors.ten = "Tên không được bỏ trống!";

    return newErrors;
  };

  const getThongTin = async () => {
    await setIsLoading(true);
    axios
      .get(api.API_THONG_TIN_YEU_CAU_DUOC_SI)
      .then(async ({ data }) => {
        setListVanBangChuyenMon(data?.results?.van_bang?.danh_sach_van_bang);
        setSelectedVanBang(data?.results?.van_bang?.current_van_bang);
        setSelectedNoiCapCMND({
          label: data?.results?.nhan_vien?.noi_cap,
          value: "123",
        });
        setFormTTHC({
          ...data?.results?.nhan_vien,
          ngay_sinh: data?.results?.nhan_vien?.ngay_sinh,
          ngay_cap: data?.results?.nhan_vien?.ngay_cap,
        });
        setFormCCHND({
          ...data?.results?.chung_chi,
          ngay_cap: data?.results?.chung_chi?.ngay_cap,
        });
        setListPVCM(data?.results?.pvhd_chuyen_mon?.danh_sach_pvhd_chuyen_mon);
        setListVTHN(data?.results?.vi_tri_hanh_nghe?.danh_sach_vt_hn);
        dispatch(
          commonActions.setListIdCheckedPVCM(
            data?.results?.pvhd_chuyen_mon?.pvhd_chuyen_mon_checked || []
          )
        );
        dispatch(
          commonActions.setListIdCheckedVTHN(
            data?.results?.vi_tri_hanh_nghe?.vt_hn_checked || []
          )
        );
        let _selectedHinhThuc = HinhThuc.find(
          (e) => e?.id === data?.results?.chung_chi?.hinh_thuc_thi
        );
        setSelectedHinhThuc(_selectedHinhThuc);
        setFileAnhChanDung(data?.results?.dinh_kem?.dinh_kem_chan_dung || []);
        setFileCMNDCCCD(data?.results?.dinh_kem?.dinh_kem_cmnd || []);
        setFileHinhChupCCHND(data?.results?.dinh_kem?.dinh_kem_cchnd || []);
        setFileGiayToKhac(data?.results?.dinh_kem?.dinh_kem_giay_to_khac || []);
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  const getCATinhThanh = () => {
    axios
      .get(api.API_CA_TINH_THANH)
      .then(async ({ data }) => {
        setListNoiCapCMND(data?.results);
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {});
  };

  const renderCheckbox = (e) => {
    return (
      <div className="row col-12" key={e?.id}>
        <span key={e?.id} className="font-weight-bold mb-1">
          {e?.ten}
        </span>
        {e.value && e.value.length > 0
          ? e.value.map((a, i) => {
              return (
                <div key={a.value} className="col-6">
                  <FormControlLabel
                    control={
                      <Checkbox
                        key={i}
                        checked={listIdCheckedVTHN.includes(String(a.value))}
                        onChange={() => {
                          let checkExist = listIdCheckedVTHN.includes(
                            String(a.value)
                          );
                          let listTemp = listIdCheckedVTHN;
                          if (checkExist) {
                            let x1 = listTemp.filter(
                              (x) => x !== String(a.value)
                            );
                            dispatch(commonActions.setListIdCheckedVTHN(x1));
                          } else {
                            let x2 = [...listTemp, a.value];
                            dispatch(commonActions.setListIdCheckedVTHN(x2));
                          }
                        }}
                        value={a?.value || ""}
                      />
                    }
                    label={a?.label}
                  />
                </div>
              );
            })
          : null}
      </div>
    );
  };

  const renderChooseFile = ({
    title,
    name,
    arrayFileMap,
    setFile,
    arrayDelFile,
    setFileDelete,
  }) => {
    return (
      <div className="col-6 pb-2">
        <div className="row">
          <div className="col-10">{title}</div>
          <div className="col-2">
            <button className="btn btn-primary btn-sm font-weight-bold">
              <UploadControl name={name}>CHỌN</UploadControl>
            </button>
          </div>
          <div className="col-12">
            {arrayFileMap?.length > 0
              ? arrayFileMap.map((e, i) => {
                  return (
                    <button
                      key={i}
                      className="btn ml-2 mb-2"
                      style={{
                        backgroundColor: "#e8e8e8",
                        padding: "12px 10px 12px 16px",
                        marginRight: "12px",
                      }}
                    >
                      <span>{e?.name}</span>
                      <i
                        style={{
                          display: "inline",
                          color: "red",
                          marginLeft: "8px",
                        }}
                        onClick={() => {
                          let arrFile = arrayFileMap;
                          arrFile = arrFile.filter(
                            (item) => item.name !== e?.name
                          );
                          setFile(arrFile);
                          //set del file
                          if (e.url) {
                            setFileDelete(arrayDelFile.concat(e));
                            console.log("log e có url", e);
                          }
                        }}
                        className="fas fa-times-circle"
                      ></i>
                    </button>
                  );
                })
              : null}
          </div>
        </div>
      </div>
    );
  };

  const UploadControl = ({
    children,
    value,
    onChange,
    disabled,
    accept,
    name,
  }) => {
    return (
      <label htmlFor={`contained-button-${name}`} className="m-0 w-100">
        <input
          disabled={disabled}
          style={{ display: "none" }}
          id={`contained-button-${name}`}
          multiple
          type="file"
          accept="audio/*|video/*|image/*|MIME_type"
          onChange={(event) => {
            switch (name) {
              case "hinhchupcchnd":
                setFileHinhChupCCHND(
                  FileHinhChupCCHND.concat(Object.values(event.target.files))
                );
                break;
              case "filecmndcccd":
                setFileCMNDCCCD(
                  FileCMNDCCCD.concat(Object.values(event.target.files))
                );
                break;
              case "anhchandung":
                setFileAnhChanDung(
                  FileAnhChanDung.concat(Object.values(event.target.files))
                );
                break;
              case "giaytokhac":
                setFileGiayToKhac(
                  FileGiayToKhac.concat(Object.values(event.target.files))
                );
                break;
              default:
                break;
            }
          }}
        />
        {children}
      </label>
    );
  };

  const updateYeuCauLienKet = async (value) => {
    console.log("log api");
    await setLoadingBtn(true);
    const {
      so_giay_phep,
      co_quan_cap,
      noi_cap,
      ngay_cap,
      phu_trach_chuyen_mon,
    } = formCCHND;
    const configs = {
      headers: {
        "content-type": "multipart/form-data",
      },
    };
    const data = new FormData();
    await data.append("so_giay_phep", so_giay_phep || "");
    await data.append("co_quan_cap", co_quan_cap || "");
    await data.append("ngay_cap", new Date(ngay_cap).getTime() / 1000);
    await data.append("hinh_thuc_thi", selectedHinhThuc?.id || null);
    await data.append("phu_trach_chuyen_mon", phu_trach_chuyen_mon || "");
    await data.append("van_bang_chuyen_mon_id", selectedVanBang?.value || null);
    for (let x = 0; x < ListIdCheckedPVCM.length; x++) {
      await data.append("pham_vi_chuyen_mon_ids[]", ListIdCheckedPVCM[x]);
    }
    for (let x = 0; x < listIdCheckedVTHN.length; x++) {
      await data.append("vi_tri_hanh_nghe_ids[]", listIdCheckedVTHN[x]);
    }
    // file đính kèm
    for (let x = 0; x < FileHinhChupCCHND.length; x++) {
      if (!FileHinhChupCCHND[x].url) {
        await data.append("dinh_kem_hinh_chup_cchnd[]", FileHinhChupCCHND[x]);
      }
    }
    for (let x = 0; x < FileAnhChanDung.length; x++) {
      if (!FileAnhChanDung[x].url) {
        await data.append("dinh_kem_anh_chan_dung[]", FileAnhChanDung[x]);
      }
    }
    for (let x = 0; x < FileCMNDCCCD.length; x++) {
      if (!FileCMNDCCCD[x].url) {
        await data.append("dinh_kem_cmnd[]", FileCMNDCCCD[x]);
      }
    }
    for (let x = 0; x < FileGiayToKhac.length; x++) {
      if (!FileGiayToKhac[x].url) {
        await data.append("dinh_kem_khac[]", FileGiayToKhac[x]);
      }
    }
    // delete files
    for (let x = 0; x < FileDelHinhChupCCHND.length; x++) {
      if (FileDelHinhChupCCHND[x].url) {
        await data.append(
          "delete_dinh_kem_hinh_chup_cchnd[]",
          FileDelHinhChupCCHND[x].url
        );
      }
    }
    for (let x = 0; x < FileDelAnhChanDung.length; x++) {
      if (FileDelAnhChanDung[x].url) {
        await data.append(
          "delete_dinh_kem_anh_chan_dung[]",
          FileDelAnhChanDung[x].url
        );
      }
    }
    for (let x = 0; x < FileDelCMNDCCCD.length; x++) {
      if (FileDelCMNDCCCD[x].url) {
        await data.append("delete_dinh_kem_cmnd[]", FileDelCMNDCCCD[x].url);
      }
    }
    for (let x = 0; x < FileDelGiayToKhac.length; x++) {
      if (FileDelGiayToKhac[x].url) {
        await data.append("delete_dinh_kem_khac[]", FileDelGiayToKhac[x].url);
      }
    }
    axios
      .put(
        value == 1
          ? api.API_LUU_YEU_CAU_LIEN_KET
          : api.API_GUI_YEU_CAU_LIEN_KET,
        data,
        configs
      )
      .then(async ({ data }) => {
        toast.success(data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "success",
        });
        resetState();
        props?.getChungChiUser();
        props.hide();
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setLoadingBtn(false);
      });
  };

  const handleSubmit = (e, value) => {
    e.preventDefault();
    updateYeuCauLienKet(value);
  };

  const resetState = () => {
    setFileAnhChanDung([]);
    setFileCMNDCCCD([]);
    setFileGiayToKhac([]);
    setFileHinhChupCCHND([]);
    setFileDelAnhChanDung([]);
    setFileDelCMNDCCCD([]);
    setFileDelGiayToKhac([]);
    setFileDelHinhChupCCHND([]);
    dispatch(commonActions.setListIdCheckedVTHN([]));
    dispatch(commonActions.setListIdCheckedPVCM([]));
  };
  return (
    <Modal
      backdrop="static"
      keyboard={false}
      size="xl"
      show={props?.show}
      onHide={() => {
        props?.hide();
      }}
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <LocalizationProvider dateAdapter={AdapterDateFns} locale={vi}>
        <Modal.Header className="px-4" closeButton>
          <Modal.Title className=" ms-auto ">
            <h2 className=" text-uppercase text-center text-primary">
              YÊU CẦU LIÊN KẾT DÀNH CHO DƯỢC SĨ ĐÃ CÓ CCHND
            </h2>
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {/* THÔNG TIN HÀNH CHÍNH */}
          <>
            {isLoading && <LoadingIndicator />}
            {!isLoading && (
              <>
                <div className="row card-box" style={{ pointerEvents: "none" }}>
                  <div className="col-12 text-title pb-3 fw-bolder">
                    Thông tin hành chính
                  </div>
                  <div className="col-4 mb-4">
                    <span className="required fw-bolder text-primary">
                      Họ đệm
                    </span>
                    <Form.Control
                      type="text"
                      className="mt-2"
                      style={{ fontSize: 14, fontWeight: 400, height: "38px" }}
                      onChange={(e) => {
                        setField("ho", e.target.value);
                      }}
                      value={formTTHC?.ho != null ? formTTHC?.ho : ""}
                      isInvalid={!!errors?.ho}
                    />
                    <Form.Control.Feedback type="invalid">
                      {errors?.ho}
                    </Form.Control.Feedback>
                  </div>
                  <div className="col-2 mb-4">
                    <span className="required fw-bolder text-primary">Tên</span>
                    <Form.Control
                      type="text"
                      className="mt-2"
                      style={{ fontSize: 14, fontWeight: 400, height: "38px" }}
                      onChange={(e) => {
                        setField("ten", e.target.value);
                      }}
                      value={formTTHC?.ten != null ? formTTHC?.ten : ""}
                      isInvalid={!!errors?.ten}
                    />
                    <Form.Control.Feedback type="invalid">
                      {errors?.ten}
                    </Form.Control.Feedback>
                  </div>
                  <div className="col-3 mb-4">
                    <span className="required fw-bolder d-block mb-2 text-primary">
                      Ngày sinh
                    </span>
                    <DesktopDatePicker
                      label=" "
                      inputFormat="dd/MM/yyyy"
                      value={formTTHC?.ngay_sinh}
                      onChange={(e) => {
                        setField("ngay_sinh", e);
                      }}
                      renderInput={(params) => <TextField {...params} />}
                      isInvalid={!!errors?.ngay_sinh}
                    />
                    <Form.Control.Feedback type="invalid">
                      {errors?.ngay_sinh}
                    </Form.Control.Feedback>
                  </div>
                  <div className="col-3 mb-4">
                    <span className="required fw-bolder text-primary">
                      Giới tính
                    </span>
                    <Select
                      key={"id"}
                      className="mt-2"
                      options={GioiTinh}
                      style={{ height: "44px" }}
                      onChange={(e) => {
                        setSelectedGioiTinh(e);
                      }}
                      value={selectedGioiTinh}
                    />
                    <span className="text-danger">
                      {!selectedGioiTinh ? " không được bỏ trống!" : ""}
                    </span>
                  </div>

                  <div className="col-lg-3 col-sm-6 col-md-6 mb-4">
                    <span className="required fw-bolder text-primary">
                      CMND/CCCD
                    </span>
                    <Form.Control
                      type="text"
                      className="mt-2"
                      style={{ fontSize: 14, fontWeight: 400, height: "38px" }}
                      onChange={(e) => {
                        setField("ma_cong_dan", e.target.value);
                      }}
                      value={
                        formTTHC?.ma_cong_dan != null
                          ? formTTHC?.ma_cong_dan
                          : ""
                      }
                      isInvalid={!!errors?.so_hieu}
                    />
                    <Form.Control.Feedback type="invalid">
                      {errors?.so_hieu}
                    </Form.Control.Feedback>
                  </div>
                  <div className="col-lg-3 col-sm-6 col-md-6 mb-4">
                    <span className="required fw-bolder d-block mb-2 text-primary">
                      Ngày cấp CMND/CCCD
                    </span>
                    <DesktopDatePicker
                      label=" "
                      inputFormat="dd/MM/yyyy"
                      value={formTTHC?.ngay_cap}
                      onChange={(e) => {
                        setField("ngay_cap", e.target.value);
                      }}
                      renderInput={(params) => <TextField {...params} />}
                      // isInvalid={!!errors?.ngay_cap}
                    />
                    <Form.Control.Feedback type="invalid">
                      {/* {errors?.ngay_cap} */}
                    </Form.Control.Feedback>
                  </div>
                  <div className="col-lg-6 col-sm-12 col-md-12 mb-4">
                    <span className="required fw-bolder d-block mb-2 text-primary">
                      Nơi cấp CMND/CCCD
                    </span>
                    <Select
                      key={"id"}
                      className="mt-2"
                      options={listNoiCapCMND}
                      style={{ height: "44px" }}
                      onChange={(e) => {
                        setSelectedNoiCapCMND(e);
                      }}
                      value={selectedNoiCapCMND}
                    />
                    <span className="text-danger">
                      {errors?.noi_tot_nghiep_id
                        ? "Nơi tốt nghiệp không được bỏ trống!"
                        : ""}
                    </span>
                  </div>
                </div>

                {/* THÔNG TIN CHỨNG CHỈ HÀNH NGHỀ DƯỢC */}
                <div className="row card-box">
                  <div className="col-12 text-title pb-3 fw-bolder">
                    THÔNG TIN CHỨNG CHỈ HÀNH NGHỀ DƯỢC
                  </div>
                  <div className="col-6 mb-4">
                    <span className="required fw-bolder text-primary">
                      Số giấy phép
                    </span>
                    <Form.Control
                      type="text"
                      className="mt-2"
                      style={{ fontSize: 14, fontWeight: 400, height: "38px" }}
                      onChange={(e) => {
                        setFieldCCHND("so_giay_phep", e.target.value);
                      }}
                      value={
                        formCCHND?.so_giay_phep != null
                          ? formCCHND?.so_giay_phep
                          : ""
                      }
                      isInvalid={!!errorsCCHND?.so_giay_phep}
                    />
                    <Form.Control.Feedback type="invalid">
                      {errorsCCHND?.so_giay_phep}
                    </Form.Control.Feedback>
                  </div>

                  <div className="col-6 mb-4">
                    <span className="required fw-bolder d-block mb-2 text-primary">
                      Ngày cấp chứng chỉ
                    </span>
                    <DesktopDatePicker
                      label=" "
                      inputFormat="dd/MM/yyyy"
                      value={formCCHND?.ngay_cap || null}
                      onChange={(e) => {
                        setFieldCCHND("ngay_cap", e);
                      }}
                      renderInput={(params) => <TextField {...params} />}
                      // isInvalid={!!errors?.ngay_cap}
                    />
                    <Form.Control.Feedback type="invalid">
                      {/* {errors?.ngay_cap} */}
                    </Form.Control.Feedback>
                  </div>

                  <div className="col-6 mb-4">
                    <span className="required fw-bolder text-primary">
                      Văn bằng chuyên môn
                    </span>
                    <Select
                      key={"id"}
                      className="mt-2"
                      options={listVanBangChuyenMon}
                      style={{ height: "44px" }}
                      onChange={(e) => {
                        setSelectedVanBang(e);
                      }}
                      value={selectedVanBang}
                    />
                    <span className="text-danger"></span>
                  </div>

                  <div className="col-6 mb-4">
                    <span className=" fw-bolder text-primary">Nơi cấp</span>
                    <Form.Control
                      type="text"
                      className="mt-2"
                      style={{ fontSize: 14, fontWeight: 400, height: "38px" }}
                      onChange={(e) => {
                        setFieldCCHND("co_quan_cap", e.target.value);
                      }}
                      value={
                        formCCHND?.co_quan_cap != null
                          ? formCCHND?.co_quan_cap
                          : ""
                      }

                      // isInvalid={!!errors?.so_hieu}
                    />
                    <Form.Control.Feedback type="invalid">
                      {/* {errors?.so_hieu} */}
                    </Form.Control.Feedback>
                  </div>

                  <div className="col-6 mb-4">
                    <span className="fw-bolder text-primary">
                      Phụ cấp chuyên môn tại
                    </span>
                    <Form.Control
                      type="text"
                      className="mt-2"
                      style={{ fontSize: 14, fontWeight: 400, height: "38px" }}
                      onChange={(e) => {
                        setFieldCCHND("phu_trach_chuyen_mon", e.target.value);
                      }}
                      value={
                        formCCHND?.phu_trach_chuyen_mon != null
                          ? formCCHND?.phu_trach_chuyen_mon
                          : ""
                      }

                      // isInvalid={!!errors?.so_hieu}
                    />
                    <Form.Control.Feedback type="invalid">
                      {/* {errors?.so_hieu} */}
                    </Form.Control.Feedback>
                  </div>

                  <div className="col-6 mb-4">
                    <span className="required fw-bolder text-primary">
                      Hình thức
                    </span>
                    <Select
                      key={"id"}
                      className="mt-2"
                      options={HinhThuc}
                      style={{ height: "44px" }}
                      onChange={(e) => {
                        setSelectedHinhThuc(e);
                      }}
                      value={selectedHinhThuc}
                    />
                    <span className="text-danger">
                      {/* {errors?.noi_tot_nghiep_id
                  ? "Nơi tốt nghiệp không được bỏ trống!"
                  : ""} */}
                    </span>
                  </div>
                  <div className="col-12 mb-4">
                    <span className="required fw-bolder text-primary">
                      Phạm vi chuyên môn
                    </span>
                    <div className="row">
                      {ListPVCM && ListPVCM.length > 0
                        ? ListPVCM.map((e) => {
                            return (
                              <div className="col-6">
                                <FormControlLabel
                                  key={e.value}
                                  control={
                                    <Checkbox
                                      key={`chilđren${e.value}`}
                                      checked={ListIdCheckedPVCM.includes(
                                        String(e.value)
                                      )}
                                      onChange={() => {
                                        let checkExist =
                                          ListIdCheckedPVCM.includes(
                                            String(e.value)
                                          );
                                        let listTemp = ListIdCheckedPVCM;
                                        if (checkExist) {
                                          let x1 = listTemp.filter(
                                            (x) => x !== String(e.value)
                                          );
                                          dispatch(
                                            commonActions.setListIdCheckedPVCM(
                                              x1
                                            )
                                          );
                                        } else {
                                          let x2 = [...listTemp, e.value];
                                          dispatch(
                                            commonActions.setListIdCheckedPVCM(
                                              x2
                                            )
                                          );
                                        }
                                      }}
                                      value={e?.id || ""}
                                    />
                                  }
                                  label={e?.label}
                                />
                              </div>
                            );
                          })
                        : null}
                    </div>
                  </div>
                  <div className="col-12 mb-4">
                    <span className="required fw-bolder text-primary mb-2">
                      Vị trí hành nghề
                    </span>
                    <div className="row">
                      {ListVTHN && ListVTHN.length > 0
                        ? ListVTHN?.map((e, i) => {
                            return <>{renderCheckbox(e)}</>;
                          })
                        : null}
                    </div>
                  </div>
                </div>

                {/* Đính kèm */}
                <div className="row card-box">
                  <div className="col-12 text-title pb-3 fw-bolder">
                    Đính kèm
                  </div>
                  {renderChooseFile({
                    title: "1. Hình chụp CCHND",
                    name: "hinhchupcchnd",
                    arrayFileMap: FileHinhChupCCHND,
                    setFile: (value) => {
                      setFileHinhChupCCHND(value);
                    },
                    arrayDelFile: FileDelHinhChupCCHND,
                    setFileDelete: (value) => {
                      setFileDelHinhChupCCHND(value);
                    },
                  })}
                  {renderChooseFile({
                    title: "2. Ảnh chân dung",
                    name: "anhchandung",
                    arrayFileMap: FileAnhChanDung,

                    setFile: (value) => {
                      setFileAnhChanDung(value);
                    },
                    arrayDelFile: FileDelAnhChanDung,
                    setFileDelete: (value) => {
                      setFileDelAnhChanDung(value);
                    },
                  })}
                  {renderChooseFile({
                    title: "3. CMND/CCCD",
                    name: "filecmndcccd",
                    arrayFileMap: FileCMNDCCCD,
                    setFile: (value) => {
                      setFileCMNDCCCD(value);
                    },
                    arrayDelFile: FileDelCMNDCCCD,

                    setFileDelete: (value) => {
                      setFileDelCMNDCCCD(value);
                    },
                  })}
                  {renderChooseFile({
                    title: "4. Giấy tờ chứng minh khác",
                    name: "giaytokhac",
                    arrayFileMap: FileGiayToKhac,
                    setFile: (value) => {
                      setFileGiayToKhac(value);
                    },
                    arrayDelFile: FileDelGiayToKhac,
                    setFileDelete: (value) => {
                      setFileDelGiayToKhac(value);
                    },
                  })}
                </div>
              </>
            )}
          </>
        </Modal.Body>
      </LocalizationProvider>
      <Modal.Footer style={{ width: "100%" }}>
        <div className="row w-100">
          <div className="col-12 text-center mt-5">
            {LoadingBtn ? (
              <Button variant="secondary" style={{ marginRight: 10 }}>
                <Spinner animation="border" role="status">
                  <span className="visually-hidden">Loading...</span>
                </Spinner>
              </Button>
            ) : (
              <>
                <Button
                  variant="secondary"
                  style={{ marginRight: 10 }}
                  onClick={() => {
                    props?.hide();
                    resetState();
                  }}
                >
                  Đóng
                </Button>
                {formCCHND?.trang_thai_ho_so !== "2" ? (
                  <>
                    <Button
                      variant="success"
                      style={{ marginRight: 10 }}
                      onClick={(e) => {
                        handleSubmit(e, 1);
                      }}
                    >
                      Lưu
                    </Button>
                    <Button
                      variant="primary"
                      onClick={(e) => {
                        handleSubmit(e, 2);
                      }}
                    >
                      Gửi yêu cầu
                    </Button>
                  </>
                ) : null}
              </>
            )}
          </div>
        </div>
      </Modal.Footer>
    </Modal>
  );
}
