import { DesktopDatePicker, LocalizationProvider } from "@mui/x-date-pickers";
import { AdapterDateFns } from "@mui/x-date-pickers/AdapterDateFns";
import { enAU } from "date-fns/locale";

import { TextField } from "@mui/material";
import axios from "axios";
import { useEffect, useState } from "react";
import { Form } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import Select from "react-select";
import { toast } from "react-toastify";
import api from "../../../../../configs/api";
import { GioiTinh } from "../../../../../data";
import {
  commonActions, selectIsSubmitted
} from "../../../../../redux-module/common/commonSlice";
import { compareDate } from "../../../../../../_metronic/helpers";

export default function ThongTinCaNhanCapNhat(props) {
  const dispatch = useDispatch();
  const isSubmitted = useSelector(selectIsSubmitted);

  const [SelectedGioiTinh, setSelectedGioiTinh] = useState({});
  const [listNoiCapCMND, setListNoiCapCMND] = useState([]);
  const [selectedNoiCapCMND, setSelectedNoiCapCMND] = useState(null);
  const [ngaySinh, setNgaySinh] = useState(null);
  const [ngayCap, setNgayCap] = useState(null);

  //validate thêm vị trí hành nghề
  const [form, setForm] = useState();
  const [errors, setErrors] = useState({});
  // location
  const [ListTinhThanh, setListTinhThanh] = useState([]);
  const [ListQuanHuyen, setListQuanHuyen] = useState([]);
  const [ListXaPhuong, setListXaPhuong] = useState([]);
  const [TinhThanhSelected, setTinhThanhSelected] = useState(null);
  const [QuanHuyenSelected, setQuanHuyenSelected] = useState(null);
  const [XaPhuongSelected, setXaPhuongSelected] = useState(null);

  // location Chỗ ở hiện nay
  const [ListTinhThanhCOHN, setListTinhThanhCOHN] = useState([]);
  const [ListQuanHuyenCOHN, setListQuanHuyenCOHN] = useState([]);
  const [ListXaPhuongCOHN, setListXaPhuongCOHN] = useState([]);
  const [TinhThanhCOHNSelected, setTinhThanhCOHNSelected] = useState(null);
  const [QuanHuyenCOHNSelected, setQuanHuyenCOHNSelected] = useState(null);
  const [XaPhuongCOHNSelected, setXaPhuongCOHNSelected] = useState(null);

  useEffect(() => {
    if (isSubmitted === true){
      handleSubmit();
    }
  }, [isSubmitted]);

  const handleGioiTinh = (id) => {
    let tempGioiTinh = GioiTinh.find((e) => e.value === id);
    setSelectedGioiTinh(tempGioiTinh);
  };

  const funcSetTinhThanhSelected = (values) => {
    setTinhThanhSelected(values);
    setField("tinh_thanh_thuong_tru_id", values.id);
  };

  const funcSetQuanHuyenSelected = (values) => {
    setQuanHuyenSelected(values);
    setField("quan_huyen_thuong_tru_id", values.id);
  };

  const funcSetXaPhuongSelected = (values) => {
    setXaPhuongSelected(values);
    setField("xa_phuong_thuong_tru_id", values.id);
  };

  const funcSetTinhThanhHTSelected = (values) => {
    setTinhThanhCOHNSelected(values);
    setField("tinh_thanh_hien_nay_id", values.id);
  };

  const funcSetQuanHuyenHTSelected = (values) => {
    setQuanHuyenCOHNSelected(values);
    setField("quan_huyen_hien_nay_id", values.id);
  };

  const funcSetXaPhuongHTSelected = (values) => {
    setXaPhuongCOHNSelected(values);
    setField("xa_phuong_hien_nay_id", values.id);
  };

  const handleNgaySinh = (newValue) => {
    const content = "Ngày sinh không được lớn hơn ngày hiện tại";
    if (compareDate(newValue, content) === true) {
      setNgaySinh(newValue);
      setField("ngay_sinh", new Date(newValue).getTime());
    }
  }

    const handleNgayCap = (newValue) => {
    const content = "Ngày cấp không được lớn hơn ngày hiện tại";
    if (compareDate(newValue, content) === true) {
      setNgayCap(newValue);
      setField("ngay_cap", new Date(newValue).getTime());
    }
  }

  useEffect(() => {
    const { data } = props;
    if (data) { 
      let dataForm = {
        ho: data?.ho || "",
        ten: data?.ten || "",
        ngay_sinh: Number(data?.ngay_sinh) || null,
        gioi_tinh: data?.gioi_tinh || null,
        tinh_thanh_thuong_tru_id: data?.tinh_thanh_id,
        quan_huyen_thuong_tru_id: data?.quan_huyen_id,
        xa_phuong_thuong_tru_id: data?.xa_phuong_id,
        so_nha_thuong_tru: data?.so_nha_thuong_tru,
        tinh_thanh_hien_nay_id: data?.tinh_thanh_hien_nay_id,
        quan_huyen_hien_nay_id: data?.quan_huyen_hien_nay_id,
        xa_phuong_hien_nay_id: data?.xa_phuong_hien_nay_id,
        so_nha: data?.so_nha || "",
        ma_cong_dan: data?.ma_cong_dan || "",
        ngay_cap: Number(data?.ngay_cap) || null,
        noi_cap: data?.noi_cap || "",
        dien_thoai: data?.dien_thoai || "",
        email: data?.email || "",
      };
      setForm(dataForm);
      dispatch(commonActions.setDoiTuongAll(dataForm));
      setSelectedNoiCapCMND({
        label: data?.noi_cap,
        value: "123",
      });
      getLocation();
      getLocationCOHN();
      getCATinhThanh();
      handleGioiTinh(data?.gioi_tinh);
    }
  }, [props?.data]);

  const getCATinhThanh = () => {
    axios
      .get(api.API_CA_TINH_THANH)
      .then(async ({ data }) => {
        setListNoiCapCMND(data?.results);
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {});
  };

  const getLocation = () => {
    let { tinh_thanh_id, xa_phuong_id, quan_huyen_id } = props?.data;
    getTinhThanh(tinh_thanh_id);
    if (tinh_thanh_id) {
      getQuanHuyen(tinh_thanh_id, quan_huyen_id);
    }
    if (quan_huyen_id) {
      getXaPhuong(quan_huyen_id, xa_phuong_id);
    }
  };

  const getLocationCOHN = () => {
    let {
      tinh_thanh_hien_nay_id,
      xa_phuong_hien_nay_id,
      quan_huyen_hien_nay_id,
    } = props.data;
    getTinhThanhCOHN(tinh_thanh_hien_nay_id);
    if (tinh_thanh_hien_nay_id) {
      getQuanHuyenCOHN(tinh_thanh_hien_nay_id, quan_huyen_hien_nay_id);
    }
    if (quan_huyen_hien_nay_id) {
      getXaPhuongCOHN(quan_huyen_hien_nay_id, xa_phuong_hien_nay_id);
    }
  };

  const setField = (field, value) => {
    setForm({
      ...form,
      [field]: value,
    });
    dispatch(
      commonActions.setDoiTuong({
        key: field,
        value,
      })
    );
    if (!!errors[field])
      setErrors({
        ...errors,
        [field]: null,
      });
  };

  const findFormErrors = () => {

    const {
      ho,
      ten,
      ngay_sinh,
      gioi_tinh,
      tinh_thanh_thuong_tru_id,
      quan_huyen_thuong_tru_id,
      xa_phuong_thuong_tru_id,
      tinh_thanh_hien_nay_id,
      quan_huyen_hien_nay_id,
      xa_phuong_hien_nay_id,
      ma_cong_dan,
      ngay_cap,
      noi_cap,
      dien_thoai,
      so_nha,
      so_nha_thuong_tru
    } = form;

    const newErrors = {};
    // name errors
    if (!ho || ho === "") newErrors.ho = "Họ không được bỏ trống!";
    if (!ten || ten === "") newErrors.ten = "Tên không được bỏ trống!";
    if (!ngay_sinh || ngay_sinh === "")
      newErrors.ngay_sinh = "Ngày sinh không được bỏ trống!";
    if (!gioi_tinh || gioi_tinh === "")
      newErrors.gioi_tinh = "Giới tính không được bỏ trống!";
    if (!tinh_thanh_thuong_tru_id || tinh_thanh_thuong_tru_id === "")
      newErrors.tinh_thanh_thuong_tru_id =
        "Tỉnh thành thường trú không được bỏ trống!";
    if (!quan_huyen_thuong_tru_id || quan_huyen_thuong_tru_id === "")
      newErrors.quan_huyen_thuong_tru_id =
        "Quận huyện thường trú không được bỏ trống!";
    if (!xa_phuong_thuong_tru_id || xa_phuong_thuong_tru_id === "")
      newErrors.xa_phuong_thuong_tru_id =
        "Xã phường thường trú không được bỏ trống!";
    if (!tinh_thanh_hien_nay_id || tinh_thanh_hien_nay_id === "")
      newErrors.tinh_thanh_hien_nay_id =
        "Tỉnh thành hiện tại không được bỏ trống!";
    if (!quan_huyen_hien_nay_id || quan_huyen_hien_nay_id === "")
      newErrors.quan_huyen_hien_nay_id =
        "Quận huyện hiện tại không được bỏ trống!";
    if (!xa_phuong_hien_nay_id || xa_phuong_hien_nay_id === "")
      newErrors.xa_phuong_hien_nay_id =
        "Xã phường hiện tại không được bỏ trống!";
    if (!ma_cong_dan || ma_cong_dan === "")
      newErrors.ma_cong_dan = "Mã công dân không được bỏ trống!";
    if (!ngay_cap || ngay_cap === "")
      newErrors.ngay_cap = "Ngày cấp không được bỏ trống!";
    if (!noi_cap || noi_cap === "")
      newErrors.noi_cap = "Nơi cấp không được bỏ trống!";
    if (!dien_thoai || dien_thoai === "")
      newErrors.dien_thoai = "Số điện thoại không được bỏ trống!";
    if (!so_nha || so_nha === "")
      newErrors.so_nha = "Địa chỉ hiện tại không được bỏ trống!";
    if (!so_nha_thuong_tru || so_nha_thuong_tru === "")
      newErrors.so_nha_thuong_tru = "Địa chỉ thường trú không được bỏ trống!";
    return newErrors;
  };

  const handleSubmit = async () => {
    const newErrors = findFormErrors();
    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
    } else {
      props?.changeValidate();
    }
  };

  // GET LOCATIONs
  const getTinhThanh = (value) => {
    axios
      .get(api.API_TINH_THANH)
      .then(({ data }) => {
        let resultsTinhThanh = data?.results;
        resultsTinhThanh.forEach((e) => {
          e.label = e.ten;
          e.value = e.id;
        });
        setListTinhThanh(resultsTinhThanh);
        if (value) {
          let TinhThanhSelected = resultsTinhThanh.find((e) => e.id === value);
          setTinhThanhSelected(TinhThanhSelected);
        }
      })
      .catch((error) => {
        if (error.response.data) {
          toast.error(error.response.data.msg, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "success",
          });
        }
      });
  };
  const getQuanHuyen = (id_tinh, value) => {
    axios
      .get(`${api.API_QUAN_HUYEN}/${id_tinh}?per_page=100`)
      .then(({ data }) => {
        let resultsQuanHuyen = data?.results;
        resultsQuanHuyen.forEach((e) => {
          e.label = e.ten;
          e.value = e.id;
        });
        setListQuanHuyen(resultsQuanHuyen);
        if (value) {
          let QuanHuyenSelected = resultsQuanHuyen.find((e) => e.id === value);
          setQuanHuyenSelected(QuanHuyenSelected);
        }
      })
      .catch((error) => {
        if (error.response.data) {
          toast.error(error.response.data.msg, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "success",
          });
        }
      });
  };
  const getXaPhuong = (id_xa, value) => {
    axios
      .get(`${api.API_XA_PHUONG}/${id_xa}?per_page=100`)
      .then(({ data }) => {
        let resultsXaPhuong = data?.results;
        resultsXaPhuong.forEach((e) => {
          e.label = e.ten;
          e.value = e.id;
        });
        setListXaPhuong(resultsXaPhuong);
        if (value) {
          let XaPhuongSelected = resultsXaPhuong.find((e) => e.id === value);
          setXaPhuongSelected(XaPhuongSelected);
        }
      })
      .catch((error) => {
        if (error.response.data) {
          toast.error(error.response.data.msg, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "success",
          });
        }
      });
  };
  const onChangeTinhThanh = async (values) => {
    funcSetTinhThanhSelected(values);
    getQuanHuyen(values.id);
    setQuanHuyenSelected(null);
    setXaPhuongSelected(null);
  };
  const onChangeQuanHuyen = (values) => {
    funcSetQuanHuyenSelected(values);
    setXaPhuongSelected(null);
    getXaPhuong(values.id);
  };
  const onChangeXaPhuong = (values) => {
    funcSetXaPhuongSelected(values);
  };
  // GET LOCATIONs COHN
  const getTinhThanhCOHN = (value) => {
    axios
      .get(api.API_TINH_THANH)
      .then(({ data }) => {
        let resultsTinhThanhCOHN = data?.results;
        resultsTinhThanhCOHN.forEach((e) => {
          e.label = e.ten;
          e.value = e.id;
        });
        setListTinhThanhCOHN(resultsTinhThanhCOHN);
        if (value) {
          let TinhThanhCOHNSelected = resultsTinhThanhCOHN.find(
            (e) => e.id === value
          );
          setTinhThanhCOHNSelected(TinhThanhCOHNSelected);
        }
      })
      .catch((error) => {
        if (error.response.data) {
          toast.error(error.response.data.msg, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "success",
          });
        }
      });
  };
  const getQuanHuyenCOHN = (id_tinh, value) => {
    axios
      .get(`${api.API_QUAN_HUYEN}/${id_tinh}?per_page=100`)
      .then(({ data }) => {
        let resultsQuanHuyenCOHN = data?.results;
        resultsQuanHuyenCOHN.forEach((e) => {
          e.label = e.ten;
          e.value = e.id;
        });
        setListQuanHuyenCOHN(resultsQuanHuyenCOHN);
        if (value) {
          let QuanHuyenCOHNSelected = resultsQuanHuyenCOHN.find(
            (e) => e.id === value
          );
          setQuanHuyenCOHNSelected(QuanHuyenCOHNSelected);
        }
      })
      .catch((error) => {
        if (error.response.data) {
          toast.error(error.response.data.msg, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "success",
          });
        }
      });
  };
  const getXaPhuongCOHN = (id_xa, value) => {
    axios
      .get(`${api.API_XA_PHUONG}/${id_xa}?per_page=100`)
      .then(({ data }) => {
        let resultsXaPhuongCOHN = data?.results;
        resultsXaPhuongCOHN.forEach((e) => {
          e.label = e.ten;
          e.value = e.id;
        });
        setListXaPhuongCOHN(resultsXaPhuongCOHN);
        if (value) {
          let XaPhuongCOHNSelected = resultsXaPhuongCOHN.find(
            (e) => e.id === value
          );
          setXaPhuongCOHNSelected(XaPhuongCOHNSelected);
        }
      })
      .catch((error) => {
        if (error.response.data) {
          toast.error(error.response.data.msg, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "success",
          });
        }
      });
  };
  const onChangeTinhThanhCOHN = async (values) => {
    funcSetTinhThanhHTSelected(values);
    getQuanHuyenCOHN(values.id);
    setQuanHuyenCOHNSelected(null);
    setXaPhuongCOHNSelected(null);
  };
  const onChangeQuanHuyenCOHN = (values) => {
    funcSetQuanHuyenHTSelected(values);
    setXaPhuongCOHNSelected(null);
    getXaPhuongCOHN(values.id);
  };
  const onChangeXaPhuongCOHN = (values) => {
    funcSetXaPhuongHTSelected(values);
  };

  return (
    <LocalizationProvider dateAdapter={AdapterDateFns} locale={enAU}>
      <div className="card py-6 px-6 mx-3 my-3">
        <div className="col-12 text-title pb-3 fw-bolder">
          Thông tin cá nhân
        </div>
        <div className="row">
          {/* Họ tên */}
          <div className="row col-12">
            <div className="col-3 mb-4">
              <span className="required fw-bolder text-primary">Họ</span>
              <Form.Control
                type="text"
                className="mt-2 text-uppercase"
                placeholder="Nhập họ"
                value={form?.ho != null ? form?.ho : ""}
                onChange={(e) => {
                  setField("ho", e.target.value.toUpperCase());
                }}
                style={{ fontSize: 14, fontWeight: 400, height: "38px" }}
              />
              {errors?.ho !== null ? (
                <span className="text-danger"> {errors?.ho}</span>
              ) : null}
            </div>
            <div className="col-3 mb-4">
              <span className="required fw-bolder text-primary">Tên</span>
              <Form.Control
                type="text"
                className="mt-2 text-uppercase"
                placeholder="Nhập tên"
                value={form?.ten != null ? form?.ten : ""}
                style={{ fontSize: 14, fontWeight: 400, height: "38px" }}
                onChange={(e) => {
                  setField("ten", e.target.value.toUpperCase());
                }}
              />
              {errors?.ten !== null ? (
                <span className="text-danger"> {errors?.ten}</span>
              ) : null}
            </div>
            <div className="col-3 mb-4">
              <span className="required fw-bolder d-block mb-2 text-primary">
                Ngày sinh
              </span>
              <DesktopDatePicker
                label=" "
                inputFormat="dd/MM/yyyy"
                value={ngaySinh}
                onChange={(e) => {
                  handleNgaySinh(e)
                }}
                renderInput={(params) => <TextField onKeyDown={(e)=> {e.preventDefault()}} {...params} />}
              />
              {errors?.ngay_sinh !== null ? (
                <span className="text-danger"> {errors?.ngay_sinh}</span>
              ) : null}
            </div>
            <div className="col-3 mb-4">
              <span className="required fw-bolder text-primary">Giới tính</span>
              <Select
                key={"id"}
                className="mt-2"
                options={GioiTinh}
                value={SelectedGioiTinh}
                style={{ height: "44px" }}
                placeholder={"Chọn giới tính"}
                onChange={(e) => {
                  setSelectedGioiTinh(e);
                  setField("gioi_tinh", e.value);
                }}
              />
              {errors?.gioi_tinh !== null ? (
                <span className="text-danger"> {errors?.gioi_tinh}</span>
              ) : null}
            </div>
          </div>

          {/* Hộ khẩu thường trú */}
          <div className="row col-12">
            <span className="fw-bolder pb-3">Hộ khẩu thường trú</span>
            <div className="col-3 mb-4">
              <span className="required fw-bolder d-block mb-2 text-primary">
                Tỉnh/TP
              </span>
              <Select
                key={"id"}
                labelField={"ten"}
                searchBy={"tenkhongdau"}
                placeholder="Chọn tỉnh thành"
                options={ListTinhThanh}
                onChange={(values) => onChangeTinhThanh(values)}
                closeOnSelect={true}
                value={TinhThanhSelected}
              />
              {errors?.tinh_thanh_thuong_tru_id !== null ? (
                <span className="text-danger"> {errors?.tinh_thanh_thuong_tru_id}</span>
              ) : null}
            </div>
            <div className="col-3 mb-4">
              <span className="required fw-bolder d-block mb-2 text-primary">
                Quận huyện
              </span>
              <Select
                key={"id"}
                labelField={"ten"}
                placeholder="Chọn quận huyện"
                options={ListQuanHuyen}
                onChange={(values) => onChangeQuanHuyen(values)}
                closeOnSelect={true}
                isDisabled={!TinhThanhSelected}
                value={QuanHuyenSelected}
              />
              {errors?.quan_huyen_thuong_tru_id !== null ? (
                <span className="text-danger"> {errors?.quan_huyen_thuong_tru_id}</span>
              ) : null}
            </div>
            <div className="col-3 mb-4">
              <span className="required fw-bolder d-block mb-2 text-primary">
                Phường/Xã
              </span>
              <Select
                key={"id"}
                labelField={"ten"}
                placeholder="Chọn xã phường"
                options={ListXaPhuong}
                onChange={(values) => onChangeXaPhuong(values)}
                closeOnSelect={true}
                isDisabled={!QuanHuyenSelected}
                value={XaPhuongSelected}
              />
              {errors?.xa_phuong_thuong_tru_id !== null ? (
                <span className="text-danger"> {errors?.xa_phuong_thuong_tru_id}</span>
              ) : null}
            </div>
            <div className="col-3 mb-4">
              <span className="required fw-bolder text-primary">Địa chỉ</span>
              <Form.Control
                type="text"
                className="mt-2"
                placeholder={"Nhập địa chỉ thường trú"}
                value={
                  form?.so_nha_thuong_tru != null
                    ? form?.so_nha_thuong_tru
                    : ""
                }
                style={{ fontSize: 14, fontWeight: 400, height: "38px" }}
                onChange={(e) => {
                  setField("so_nha_thuong_tru", e.target.value);
                }}
                isInvalid={!!errors?.dia_chi_thuong_tru}
              />
              {errors?.so_nha_thuong_tru !== null ? (
                <span className="text-danger"> {errors?.so_nha_thuong_tru}</span>
              ) : null}
            </div>
          </div>

          {/* Chỗ ở hiện tại */}
          <div className="row col-12">
            <span className="fw-bolder pb-3">Chỗ ở hiện tại</span>
            <div className="col-3 mb-4">
              <span className="required fw-bolder d-block mb-2 text-primary">
                Tỉnh/TP
              </span>
              <Select
                key={"id"}
                labelField={"ten"}
                searchBy={"tenkhongdau"}
                placeholder="Chọn tỉnh thành"
                options={ListTinhThanhCOHN}
                onChange={(values) => onChangeTinhThanhCOHN(values)}
                closeOnSelect={true}
                value={TinhThanhCOHNSelected}
              />
              {errors?.tinh_thanh_hien_nay_id !== null ? (
                <span className="text-danger"> {errors?.tinh_thanh_hien_nay_id}</span>
              ) : null}
            </div>
            <div className="col-3 mb-4">
              <span className="required fw-bolder d-block mb-2 text-primary">
                Quận huyện
              </span>
              <Select
                key={"id"}
                labelField={"ten"}
                placeholder="Chọn quận huyện"
                options={ListQuanHuyenCOHN}
                onChange={(values) => onChangeQuanHuyenCOHN(values)}
                closeOnSelect={true}
                multi={false}
                isDisabled={!TinhThanhCOHNSelected}
                value={QuanHuyenCOHNSelected}
              />
              {errors?.quan_huyen_hien_nay_id !== null ? (
                <span className="text-danger"> {errors?.quan_huyen_hien_nay_id}</span>
              ) : null}
            </div>
            <div className="col-3 mb-4">
              <span className="required fw-bolder d-block mb-2 text-primary">
                Phường/Xã
              </span>
              <Select
                key={"id"}
                labelField={"ten"}
                placeholder="Chọn xã phường"
                options={ListXaPhuongCOHN}
                onChange={(values) => onChangeXaPhuongCOHN(values)}
                closeOnSelect={true}
                isDisabled={!QuanHuyenCOHNSelected}
                value={XaPhuongCOHNSelected}
              />
              {errors?.xa_phuong_hien_nay_id !== null ? (
                <span className="text-danger"> {errors?.xa_phuong_hien_nay_id}</span>
              ) : null}
            </div>
            <div className="col-3 mb-4">
              <span className="required fw-bolder text-primary">Địa chỉ</span>
              <Form.Control
                type="text"
                className="mt-2"
                placeholder="Nhập địa chỉ hiện tại"
                value={form?.so_nha != null ? form?.so_nha : ""}
                style={{ fontSize: 14, fontWeight: 400, height: "38px" }}
                onChange={(e) => {
                  setField("so_nha", e.target.value);
                }}
              />
              {errors?.so_nha !== null ? (
                <span className="text-danger"> {errors?.so_nha}</span>
              ) : null}
            </div>
          </div>

          {/* CMND/SDT */}
          <div className="row col-12">
            <div className="col-4 mb-4">
              <span className="required fw-bolder text-primary">CMND/CCCD</span>
              <Form.Control
                type="text"
                className="mt-2"
                placeholder="Nhập số CMND/CCCD"
                value={form?.ma_cong_dan != null ? form?.ma_cong_dan : ""}
                style={{ fontSize: 14, fontWeight: 400, height: "38px" }}
                onChange={(e) => {
                  setField("ma_cong_dan", e.target.value);
                }}
              />
              {errors?.ma_cong_dan !== null ? (
                <span className="text-danger"> {errors?.ma_cong_dan}</span>
              ) : null}
            </div>
            <div className="col-4 mb-4">
              <span className="required fw-bolder d-block mb-2 text-primary">
                Ngày cấp CMND/CCCD
              </span>
              <DesktopDatePicker
                label=" "
                inputFormat="dd/MM/yyyy"
                value={ngayCap}
                onChange={(e) => {
                  handleNgayCap(e)
                }}
                renderInput={(params) => <TextField onKeyDown={(e)=> {e.preventDefault()}} {...params} />}
              />
              {errors?.ngay_cap !== null ? (
                <span className="text-danger"> {errors?.ngay_cap}</span>
              ) : null}
            </div>
            <div className="col-4 mb-4">
              <span className="required fw-bolder d-block mb-2 text-primary">
                Nơi cấp CMND/CCCD
              </span>
              <Select
                key={"id"}
                className="mt-2"
                options={listNoiCapCMND}
                style={{ height: "38px" }}
                onChange={(e) => {
                  setSelectedNoiCapCMND(e);
                  setField("noi_cap", e?.label);
                }}
                value={selectedNoiCapCMND}
              />
              {errors?.noi_cap !== null ? (
                <span className="text-danger"> {errors?.noi_cap}</span>
              ) : null}
            </div>
            <div className="col-4 mb-4">
              <span className="fw-bolder d-block mb-2 text-primary">
                Số điện thoại
              </span>
              <Form.Control
                type="text"
                className="mt-2"
                placeholder="Nhập số điện thoại"
                style={{ fontSize: 14, fontWeight: 400, height: "38px" }}
                onChange={(e) => {
                  setField("dien_thoai", e.target.value);
                }}
                value={form?.dien_thoai != null ? form?.dien_thoai : ""}
              />
              {errors?.dien_thoai !== null ? (
                <span className="text-danger"> {errors?.dien_thoai}</span>
              ) : null}
            </div>
            <div className="col-4 mb-4">
              <span className="fw-bolder d-block mb-2 text-primary">Email</span>
              <Form.Control
                type="text"
                className="mt-2"
                placeholder="Nhập email"
                value={form?.email != null ? form?.email : ""}
                style={{ fontSize: 14, fontWeight: 400, height: "38px" }}
                onChange={(e) => {
                  setField("email", e.target.value);
                }}
              />
              {errors?.email !== null ? (
                <span className="text-danger"> {errors?.email}</span>
              ) : null}
            </div>
          </div>
        </div>
      </div>
    </LocalizationProvider>
  );
}
