import axios from "axios";
import { useEffect, useState } from "react";
import BlockUi from "react-block-ui";
import { Form } from "react-bootstrap";
import DataTable from "react-data-table-component";
import { Oval, TailSpin } from "react-loader-spinner";
import Select from "react-select";
import { toast } from "react-toastify";
import { KTCard, KTSVG, useDebounce } from "../../../../../_metronic/helpers";
import api from "../../../../configs/api";
import { PopupDelete } from "../../popup/PopupDelete";
import clsx from "clsx";
import ReactTooltip from "react-tooltip";

export function ThanhPhanHoSo() {
  const [listTPHS, setListTPHS] = useState([]);
  const [perPage, setPerPage] = useState(10);
  const [isLoading, setIsLoading] = useState(true);
  const [totalRows, setTotalRows] = useState(0);
  const [selectedData, setSelectedData] = useState([]);
  const [options, setOptions] = useState([]);
  const [showUpdate, setShowUpdate] = useState(false);
  const [id, setId] = useState("");
  const [page, setPage] = useState(1);
  const [trang_thai, setTrang_thai] = useState(true);
  const [idThuTuc, setIdThuTuc] = useState(null);

  //open modal delete
  const [isvisibleDelete, setIsvisibleDelete] = useState(false);
  const [idDelete, setIdDelete] = useState("");
  const closeModalDelete = () => setIsvisibleDelete(false);
  const showModalDelete = () => setIsvisibleDelete(true);

  const [searchTerm, setSearchTerm] = useState("");

  const debouncedSearchTerm = useDebounce(searchTerm, 1000);

  useEffect(
    () => {
      getListThuTuc();

      if (debouncedSearchTerm !== undefined && searchTerm !== undefined) {
        getList(1, perPage, debouncedSearchTerm);
      }
    },
    [debouncedSearchTerm] // Only call effect if debounced search term changes
  );

  const getList = (page_number = page, size = perPage, search_key = "") => {
    setIsLoading(true);
    axios
      .post(
        api.API_GET_THANH_PHAN_HO_SO +
          "_get_list" +
          `?page=${page}&per_page=${size}`,
        {
          search_ten: search_key,
        }
      )
      .then(async ({ data }) => {
        setListTPHS(data?.results);
        setTotalRows(data?.total);

        setIsLoading(false);
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };
  const getListThuTuc = (
    page_number = page,
    size = perPage,
    search_key = ""
  ) => {
    axios
      .post(api.API_GET_THU_TUC + `?page=${page}&per_page=${1000}`, {})
      .then(async ({ data }) => {
        console.log("thutuc", data);
        if (data) {
          data?.results?.forEach((e) => {
            e.label = e.ten;
            e.value = e.id;
            // delete e.ten;
          });

          let newArray = [];
          newArray.unshift({
            ma_thu_tuc: null,
            ten: "Chọn thủ tục",
            value: null,
            label: "Chọn thủ tục",
          });
          newArray.push(...data?.results);

          console.log("option", newArray);

          setOptions(newArray);
          setSelectedData(newArray[0]);
        }
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  //validate thêm chỉ định chuyên khoa
  const [form, setForm] = useState({});
  const [errors, setErrors] = useState({});
  const setField = (field, value) => {
    setForm({
      ...form,
      [field]: value,
    });
    if (!!errors[field])
      setErrors({
        ...errors,
        [field]: null,
      });
  };
  const findFormErrors = () => {
    const { ten } = form;
    const newErrors = {};
    // name errors
    if (!ten || ten === "") newErrors.ten = "Tên không được bỏ trống!";
    if (!idThuTuc || idThuTuc === "")
      newErrors.idThuTuc = "Vui lòng chọn thủ tục!";

    return newErrors;
  };

  // handle api
  const addTPHS = async (e) => {
    e.preventDefault();
    // get our new errors
    const newErrors = findFormErrors();
    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
    } else {
      setIsLoading(true);
      const { ten } = form;
      const json = {
        ten,
        trang_thai,
        thu_tuc_id: idThuTuc,
      };
      await axios
        .post(api.API_GET_THANH_PHAN_HO_SO + "_add", json)
        .then(async ({ data }) => {
          console.log("data", data);
          resetState();
          toast.success(data?.msg, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "success",
          });
          getList(1, perPage, "");
          setIsLoading(false);
          // navigate(0);
        })
        .catch((error) => {
          console.log("error", error?.response);
          toast.error(error?.data?.errors, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "error",
          });
        })
        .finally(() => {
          setIsLoading(false);
        });
    }
  };

  const updateTPHS = async (e) => {
    e.preventDefault();
    // get our new errors
    const newErrors = findFormErrors();
    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
    } else {
      setIsLoading(true);
      const { ten, id } = form;
      const json = {
        ten,
        trang_thai,
        thu_tuc_id: idThuTuc,
      };
      await axios
        .put(api.API_GET_THANH_PHAN_HO_SO + "/" + id, json)
        .then(async ({ data }) => {
          console.log("data", data);
          resetState();
          toast.success(data?.msg, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "success",
          });
          getList(1, perPage, "");
          setIsLoading(false);
          // navigate(0);
        })
        .catch((error) => {
          console.log("error", error?.response);
          toast.error(error?.data?.errors, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "error",
          });
        })
        .finally(() => {
          setIsLoading(false);
        });
    }
  };

  const resetState = async () => {
    await setPerPage(10);
    await setTotalRows(0);
    await setForm({
      ten: "",
    });
    await setTrang_thai(true);
    await setErrors({});
    await setId("");
    await setShowUpdate(false);
    await setOptions([]);
    await setIdThuTuc(null);

    getListThuTuc();
  };
  console.log("trang_thai", trang_thai);
  // DETAIL
  const detail = async (data) => {
    setForm({
      ...data,
    });
    setId(data.id);
    setTrang_thai(data.trang_thai);
    let thutuc = options.find((e) => e.value === data.thu_tuc.id);
    console.log("thutuc", thutuc);
    setSelectedData(thutuc);
    setShowUpdate(true);
  };

  const handlePerRowsChange = async (newPerPage, page) => {
    await setIsLoading(true);
    axios
      .post(
        api.API_GET_THANH_PHAN_HO_SO +
          "_get_list" +
          `?page=${page}&per_page=${newPerPage}`,
        {
          ten: searchTerm,
        }
      )
      .then(({ data }) => {
        if (data?.results) {
          setListTPHS(data?.results);
          setPerPage(newPerPage);
        }
      })
      .catch(() => {})
      .finally(() => {
        setIsLoading(false);
      });
  };

  const deleteTPHS = () => {
    const json = {
      id: idDelete,
    };
    console.log("123213", json);
    axios
      .delete(api.API_GET_THANH_PHAN_HO_SO + "/" + idDelete, {})
      .then(async ({ data }) => {
        console.log("data delete success", data);
        await toast.success(data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "success",
        });
        closeModalDelete();
        resetState();
        if (page === 1) {
          getList({ page_number: 1 });
        } else if (listTPHS?.length === 1) {
          getList({ page_number: page - 1 });
        } else {
          getList({ page_number: page });
        }
      })
      .catch((error) => {
        console.log("error", error);
        toast.error(error?.data?.errors, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };
  const handlePageChange = (page) => {
    setPage(page);
    getList(page);
  };
  //đổi màu khi selected
  const [data, setData] = useState(listTPHS);
  useEffect(() => {
    setData(listTPHS);
  }, [listTPHS]);
  const handleRowClicked = (row) => {
    const updatedData = listTPHS?.map((item) => {
      if (row.id !== item.id) {
        return item;
      }
      return {
        ...item,
        toggleSelected: !item.toggleSelected,
      };
    });
    setData(updatedData);
  };
  const formatGroupLabel = (listTemp) => (
    <div style={groupStyles}>
      <span>{listTemp.ten}</span>
    </div>
  );
  const groupStyles = {
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
  };
  const paginationOptions = {
    rowsPerPageText: "Dòng hiển thị",
    rangeSeparatorText: "trên",
  };
  const customStyles = {
    headRow: {
      style: {
        borderTopStyle: "solid",
        borderTopWidth: "1px",
        borderTopColor: "#e0e0e0",
        borderBottomColor: "#e0e0e0",
      },
    },
    headCells: {
      style: {
        color: "#202124",
        fontSize: "16px",
      },
    },
    rows: {
      highlightOnHoverStyle: {
        backgroundColor: "rgb(230, 244, 244)",
        borderBottomColor: "#FFFFFF",
        outline: "1px solid #FFFFFF",
      },
    },
    pagination: {
      style: {
        border: "none",
      },
    },
  };
  const columns = [
    {
      name: "Mã thành phần",
      grow: 1,
      selector: (row) => (
        <div
          onClick={() => {
            detail(row);
            handleRowClicked(row);
          }}
        >
          <span>{row.ma_tp_ho_so}</span>
        </div>
      ),
      style: {
        color: "#1251ff",
        fontSize: "14px",
        fontWeight: 500,
        width: 300,
        cursor: "pointer",
      },
    },
    {
      name: "Mã thủ tục",
      selector: (row) => <span>{row.thu_tuc.ma_thu_tuc}</span>,
      grow: 1,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },

    {
      name: "Tên thành phần",
      selector: (row) => (
        <span data-tip={row?.ten}>
          {row.ten}
          <ReactTooltip place="top" type="dark" effect="float" />
        </span>
      ),
      grow: 6,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },

    {
      name: "Trang thái",
      selector: (row) => <span>{row.trang_thai}</span>,
      grow: 1,
      cell: (row) => (
        <td>
          {row.trang_thai ? (
            <span className="badge badge-light-success fs-7 fw-bold">
              Hoạt động
            </span>
          ) : (
            <span className="badge badge-light-dark fs-7 fw-bold">
              Không hoạt động
            </span>
          )}
        </td>
      ),
    },

    {
      name: "Thao tác",
      grow: 1,

      cell: (row) => (
        <div className="container my-3">
          <a
            onClick={async () => {
              await setIdDelete(row?.id);
              showModalDelete();
            }}
            className="btn btn-icon btn-bg-light btn-color-danger btn-sm"
          >
            <KTSVG
              path="/media/icons/duotune/general/gen027.svg"
              className="svg-icon-3"
            />
          </a>
        </div>
      ),
    },
  ];
  const conditionalRowStyles = [
    {
      when: (row) => row.toggleSelected,
      style: {
        backgroundColor: "#f5f5f5",
        userSelect: "none",
      },
    },
  ];
  const cardBox = {
    backgroundColor: "#fff",
    backgroundClip: "border-box",
    border: "1px solid #e7eaed",
    padding: "1.5rem",
    marginBottom: "24px",
    borderRadius: "0.5rem",
  };
  return (
    <KTCard>
      <div className="page">
        <div className="container-fluid">
          <div className="fv-row mb-7 mt-6">
            {/* begin::Label */}
            <label className="fw-bold fs-6 mb-2">Tên thành phần hồ sơ</label>
            {/* end::Label */}

            {/* begin::Input */}
            <div className="col-md-6">
              <input
                placeholder="Nhập tên tìm kiếm..."
                type="text"
                name="name"
                className={clsx("form-control form-control-solid mb-3 mb-lg-0")}
                autoComplete="off"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>

            {/* end::Input */}
          </div>
          <BlockUi
            tag="div"
            // blocking={!isLoading}
            loader={
              <TailSpin
                arialLabel="loading-indicator"
                height={60}
                width={60}
                strokeWidth={2}
                strokeWidthSecondary={1}
                color="blue"
                secondaryColor="blue"
                wrapperclassName={"d-inline-flex"}
              />
            }
          >
            <div className="row">
              <div className="col-md-8">
                <div className="" style={cardBox}>
                  <DataTable
                    noDataComponent={"Không có dữ liệu."}
                    sortServer
                    progressPending={isLoading}
                    columns={columns}
                    data={data}
                    customStyles={customStyles}
                    pagination
                    highlightOnHover
                    pointerOnHover
                    paginationServer
                    paginationTotalRows={totalRows}
                    onChangeRowsPerPage={handlePerRowsChange}
                    onChangePage={handlePageChange}
                    paginationComponentOptions={paginationOptions}
                    conditionalRowStyles={conditionalRowStyles}
                    // onRowClicked={(data) => {
                    //   detail(data);
                    //   handleRowClicked(data);
                    // }}
                    progressComponent={
                      <div
                        style={{
                          padding: "24px",
                        }}
                      >
                        <Oval
                          arialLabel="loading-indicator"
                          color="#007bff"
                          height={40}
                        />
                      </div>
                    }
                  />
                </div>
              </div>
              <div className="col-md-4">
                <div className="" style={cardBox}>
                  <div className="">
                    <label className="fw-bold fs-6 mb-2">
                      Tạo mới/ cập nhật thành phần hồ sơ.
                    </label>
                  </div>
                  {showUpdate && (
                    <div className="row mt-3">
                      <div className="col-4">
                        <label className="required fw-bold fs-6 mb-2">
                          Mã thành phần
                        </label>
                      </div>
                      <div className="col-8">
                        <Form.Control
                          disabled={true}
                          type="text"
                          placeholder="Nhập mã hồ sơ"
                          style={{ fontSize: 14, fontWeight: 500 }}
                          value={
                            form.ma_tp_ho_so === null ? "" : form.ma_tp_ho_so
                          }
                        />
                      </div>
                    </div>
                  )}

                  <div className="row mt-3">
                    <div className="col-4">
                      <label className="required fw-bold fs-6 mb-2">
                        Tên thành phần
                      </label>
                    </div>
                    <div className="col-8">
                      <Form.Control
                        as="textarea"
                        rows={3}
                        placeholder="Nhập tên hồ sơ"
                        style={{ fontSize: 14, fontWeight: 500 }}
                        value={form.ten === null ? "" : form.ten}
                        onChange={(e) => {
                          setField("ten", e.target.value);
                        }}
                        isInvalid={!!errors.ten}
                      />
                      <Form.Control.Feedback type="invalid">
                        {errors.ten}
                      </Form.Control.Feedback>
                    </div>
                  </div>

                  <div className="row mt-3">
                    <div className="col-4">
                      <label className="required fw-bold fs-6 mb-2">
                        Chọn thủ tục
                      </label>
                    </div>
                    <div className="col-8">
                      <Select
                        value={selectedData}
                        key={"id"}
                        options={options}
                        formatGroupLabel={formatGroupLabel}
                        onChange={async (data) => {
                          await setIdThuTuc(data?.id);
                          setSelectedData(data);
                          console.log("onchange", data);
                        }}
                      />
                      {idThuTuc == null ? (
                        <span className="text-danger" style={{ fontSize: 12 }}>
                          {errors?.idThuTuc}
                        </span>
                      ) : null}
                    </div>
                  </div>

                  <div className="row mt-3">
                    <div className="col-4">
                      <label className="fw-bold fs-6 mb-2">Trạng thái</label>
                    </div>
                    <div className="col-8">
                      <div className="form-check form-check-solid form-switch fv-row">
                        <input
                          className="form-check-input w-45px h-30px"
                          type="checkbox"
                          id="trang_thai"
                          // value={trang_thai}
                          checked={trang_thai}
                          onChange={(e) => {
                            setTrang_thai(!trang_thai);
                          }}
                        />
                        <label className="form-check-label"></label>
                      </div>
                    </div>
                  </div>

                  <div className="row mt-5">
                    <div className="col-4">
                      {showUpdate ? (
                        <button
                          className="btn text-white bg-blue2"
                          style={{ fontSize: ".9rem" }}
                          onClick={(e) => {
                            resetState();
                            getList(1, perPage, "");
                          }}
                        >
                          <i className="fas fa-sync-alt text-white"></i> Làm mới
                        </button>
                      ) : null}
                    </div>

                    <div className="col-8">
                      <button
                        className="btn text-white bg-blue2 mr-3"
                        style={{ fontSize: ".9rem" }}
                        onClick={(e) => {
                          showUpdate ? updateTPHS(e) : addTPHS(e);
                        }}
                      >
                        {showUpdate ? (
                          <i className="fa fa-pencil-alt text-white"></i>
                        ) : (
                          <i className="fas fa-save text-white"></i>
                        )}{" "}
                        {showUpdate ? "Cập nhật" : "Lưu thành phần hồ sơ"}
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            </div>

            <PopupDelete
              title="Bạn có đồng ý xóa thành phần hồ sơ này không?"
              show={isvisibleDelete}
              onHide={closeModalDelete}
              onDetele={() => {
                deleteTPHS();
              }}
            />
          </BlockUi>
        </div>
      </div>
    </KTCard>
  );
}
