import { useEffect } from "react";
import { Outlet } from "react-router-dom";
import { HeaderWrapper } from "./components/header/HeaderWrapper";
import { Toolbar } from "./components/toolbar/Toolbar";
import { ScrollTop } from "./components/ScrollTop";
import { Content } from "./components/Content";
import { PageDataProvider } from "./core";
import { useLocation } from "react-router-dom";
import { MenuComponent } from "../assets/ts/components";

const MasterLayout = () => {
  const location = useLocation();
  useEffect(() => {
    setTimeout(() => {
      MenuComponent.reinitialization();
    }, 500);
  }, []);

  useEffect(() => {
    setTimeout(() => {
      MenuComponent.reinitialization();
    }, 500);
  }, [location.key]);

  return (
    <PageDataProvider>
      <div id="main" className="main">

        <div className="topnav">
          <a href="#home">Home</a>
          <a href="#news">News</a>
          <a href="#contact">Contact</a>
        </div>
        <div>
          <div className="sidenav text-center">
            <button className="btn btn-link" title="Trang chủ"><i className="fas fa-home ms-1"></i></button><br/>
            <button className="btn btn-link" title="Quản lý tin tức"><i className="fas fa-newspaper ms-1"></i></button><br/>
            <button className="btn btn-link" title="Quản lý người dùng"><i className="fas fa-users ms-1"></i></button><br/>
            <button className="btn btn-link" title="Quản lý tin tư"><i className="fas fa-notes-medical ms-1"></i></button><br/>
            <button className="btn btn-link"><i className="fas fa-user-circle ms-1"></i></button><br/>
          </div>  
          <div className="content">
            <Content>
              <Outlet></Outlet>
            </Content>
          </div>
        </div>
      </div>
      <ScrollTop />
    </PageDataProvider>
  );
};

export { MasterLayout };
