        })
        .catch((error) => {
          toast.error(error?.data?.errors, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "error",
          });
        })
        .finally(() => { })
    }
  }

