import { AdapterDateFns } from "@mui/x-date-pickers/AdapterDateFns";
import { LocalizationProvider, DesktopDatePicker } from "@mui/x-date-pickers"
import { TextField } from "@mui/material";
import { vi } from "date-fns/locale";
import { useState } from "react";
import { Form } from "react-bootstrap";
import { GioiTinh, HinhThuc } from "../../../../app/data";
import Select from "react-select";


import { PageTitle } from "../../../../_metronic/layout/core";
import { PhamViVaViTriChuyenMon } from "../danh-sach-ho-so-thu-ly-duoc-si/components/PhamViChuyenMon";
import { ThongTinHanhChinhCapNhat } from "../danh-sach-ho-so-thu-ly-duoc-si/components/thong-tin-hanh-chinh/ThongTinHanhChinhCapNhat";
import { BangCap } from "../duoc-si/bang-cap/BangCap";
import { LichSuChungChi } from "../duoc-si/lich-su-chung-chi/LichSuChungChi";
import { LichSuDaoTao } from "../duoc-si/lich-su-dao-tao/LichSuDaoTao";
import { LichSuThucHanh } from "../duoc-si/lich-su-thuc-hanh/LichSuThucHanh";

export function ChiTietChungChi() {
    const usersBreadcrumbs = [
        {
            title: "Trang chủ",
            path: "/",
            isSeparator: false,
            isActive: false,
        },
        {
            title: "",
            path: "",
            isSeparator: true,
            isActive: false,
        },
    ];

    const [selectedGioiTinh, setSelectedGioiTinh] = useState({});


    return (
        <div className="container-fluid">
            <PageTitle breadcrumbs={usersBreadcrumbs}>
                Chi tiết chứng chỉ hành nghề
            </PageTitle>
            <LocalizationProvider dateAdapter={AdapterDateFns} locale={vi}>
                <div className="mt-10">
                    <ThongTinHanhChinhCapNhat />
                </div>

                <BangCap />
                <LichSuThucHanh />
                <LichSuDaoTao />
                <LichSuChungChi />

                <div className="card bg-white py-6 px-6 my-3">
                    <div className="row">
                        <div className="col-12 text-title pb-3 fw-bolder">
                            Thông tin chứng chỉ hành nghề dược
                        </div>
                    </div>
                    <div className="row mb-10">

                        <div className="col-3 mb-4">
                            <span className="required fw-bolder text-primary">Số CCHND</span>
                            <Form.Control
                                type="text"
                                className="mt-2"
                                placeholder="Nhập số cchnd"
                                style={{ fontSize: 14, fontWeight: 400, height: "38px" }}
                                onChange={(e) => { }}
                                value={""}
                            // isInvalid={!!errors?.ho}
                            />
                            <Form.Control.Feedback type="invalid">
                                {/* {errors?.so_hieu} */}
                            </Form.Control.Feedback>
                        </div>
                        <div className="col-3 mb-4">
                            <span className="required fw-bolder d-block mb-2 text-primary">
                                Ngày cấp
                            </span>
                            <DesktopDatePicker
                                label=" "
                                inputFormat="dd/MM/yyyy"
                                // value={ngayCap}
                                onChange={() => { }}
                                renderInput={(params) => <TextField {...params} />}
                            // isInvalid={!!errors?.ngay_cap}
                            />
                            <Form.Control.Feedback type="invalid">
                                {/* {errors?.ngay_cap} */}
                            </Form.Control.Feedback>
                        </div>
                        <div className="col-3 mb-4">
                            <span className="required fw-bolder d-block mb-2 text-primary">
                                Ngày hiệu lực
                            </span>
                            <DesktopDatePicker
                                label=" "
                                inputFormat="dd/MM/yyyy"
                                // value={ngayCap}
                                onChange={() => { }}
                                renderInput={(params) => <TextField {...params} />}
                            // isInvalid={!!errors?.ngay_cap}
                            />
                            <Form.Control.Feedback type="invalid">
                                {/* {errors?.ngay_cap} */}
                            </Form.Control.Feedback>
                        </div>
                        <div className="col-3 mb-4">
                            <span className="required fw-bolder d-block mb-2 text-primary">
                                Ngày hết hạn
                            </span>
                            <DesktopDatePicker
                                label=" "
                                inputFormat="dd/MM/yyyy"
                                // value={ngayCap}
                                onChange={() => { }}
                                renderInput={(params) => <TextField {...params} />}
                            // isInvalid={!!errors?.ngay_cap}
                            />
                            <Form.Control.Feedback type="invalid">
                                {/* {errors?.ngay_cap} */}
                            </Form.Control.Feedback>
                        </div>


                        <div className="col-5 mb-4">
                            <span className="required fw-bolder text-primary">Số QĐ cấp CCHN</span>
                            <Form.Control
                                type="text"
                                className="mt-2"
                                placeholder="Nhập số cchnd"
                                style={{ fontSize: 14, fontWeight: 400, height: "38px" }}
                                onChange={(e) => { }}
                                value={""}
                            // isInvalid={!!errors?.ho}
                            />
                            <Form.Control.Feedback type="invalid">
                                {/* {errors?.so_hieu} */}
                            </Form.Control.Feedback>
                        </div>
                        <div className="col-5 mb-4">
                            <span className="required fw-bolder d-block mb-2 text-primary">
                                Số QĐ cấp CCHN
                            </span>
                            <DesktopDatePicker
                                label=" "
                                inputFormat="dd/MM/yyyy"
