/* eslint-disable react-hooks/exhaustive-deps */
import { DesktopDatePicker, LocalizationProvider } from "@mui/x-date-pickers";
import { AdapterDateFns } from "@mui/x-date-pickers/AdapterDateFns";
import { enAU } from "date-fns/locale";

import TextField from "@mui/material/TextField";
import axios from "axios";
import { useEffect, useState } from "react";
import { Form } from "react-bootstrap";
import ImageUploading from "react-images-uploading";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import Select from "react-select";
import { toast } from "react-toastify";
import { PageTitle } from "../../../../_metronic/layout/core";
import api from "../../../configs/api";
import { GioiTinh } from "../../../data";
import {
    authActions,
    selectCurrentUser,
    selectRoleUser,
} from "../../../redux-module/auth/authSlice";
import LoadingOverlay from "react-loading-overlay";

export default function ThongTinDuocSi() {
    const navigate = useNavigate();
    const dispatch = useDispatch();
    const roleUser = useSelector(selectRoleUser);
    const thongTinDuocSi = useSelector(selectCurrentUser);

    const [form, setForm] = useState({});
    const [errors, setErrors] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    const [listNoiCapCMND, setListNoiCapCMND] = useState([]);
    const [selectedNoiCapCMND, setSelectedNoiCapCMND] = useState();

    const [selectedGioiTinh, setSelectedGioiTinh] = useState(null);

    const [listTinhThanh, setListTinhThanh] = useState([]);
    const [listQuanHuyen, setListQuanHuyen] = useState([]);
    const [listXaPhuong, setListXaPhuong] = useState([]);
    const [listTinhThanhCOHN, setListTinhThanhCOHN] = useState([]);
    const [listQuanHuyenCOHN, setListQuanHuyenCOHN] = useState([]);
    const [listXaPhuongCOHN, setListXaPhuongCOHN] = useState([]);

    const [selectedTTTT, setSelectedTTTT] = useState();
    const [selectedQHTT, setSelectedQHTT] = useState();
    const [selectedXPTT, setSelectedXPTT] = useState();

    const [selectedTTHN, setSelectedTTHN] = useState();
    const [selectedQHHN, setSelectedQHHN] = useState();
    const [selectedXPHN, setSelectedXPHN] = useState();

    useEffect(async () => {
        setSelectedNoiCapCMND({
            label: thongTinDuocSi?.noi_cap,
            value: "tempNoicap",
        });
        setForm(thongTinDuocSi);
        getTinhThanh();
        getCATinhThanh();
        handleGioiTinh();
    }, []);

    const formatGroupLabel = (listTemp) => (
        <div style={groupStyles}>
            <span>{listTemp.ten}</span>
        </div>
    );
    const groupStyles = {
        display: "flex",
        alignItems: "center",
        justifyContent: "space-between",
    };

    const setField = (field, value) => {
        setForm({
            ...form,
            [field]: value,
        });
        if (!!errors[field])
            setErrors({
                ...errors,
                [field]: null,
            });
    };

    // GET LOCATIONs
    const getTinhThanh = () => {
        axios
            .get(api.API_TINH_THANH)
            .then(({ data }) => {
                let resultsTinhThanh = data?.results;
                resultsTinhThanh.forEach((e) => {
                    e.label = e.ten;
                    e.value = e.id;
                });

                if (thongTinDuocSi.tinh_thanh_id) {
                    const tttt = resultsTinhThanh.find(
                        (e) => e.id === thongTinDuocSi.tinh_thanh_id
                    );
                    setSelectedTTTT(tttt);
                    getQuanHuyen(tttt.id, "thuongtru");
                }
                if (thongTinDuocSi.tinh_thanh_hien_nay_id) {
                    const tthn = resultsTinhThanh.find(
                        (e) => e.id === thongTinDuocSi.tinh_thanh_hien_nay_id
                    );
                    setSelectedTTHN(tthn);
                    getQuanHuyen(tthn.id, "hiennay");
                }

                setListTinhThanh(resultsTinhThanh);
                setListTinhThanhCOHN(resultsTinhThanh);
            })
            .catch((error) => {
                if (error.response.data) {
                    toast.error(error.response.data.msg, {
                        position: "top-right",
                        autoClose: 2000,
                        hideProgressBar: false,
                        closeOnClick: true,
                        pauseOnHover: true,
                        draggable: true,
                        progress: undefined,
                        toastId: "success",
                    });
                }
            });
    };
    const getQuanHuyen = (id, type) => {
        axios
            .get(`${api.API_QUAN_HUYEN}/${id}?per_page=100`)
            .then(({ data }) => {
                let resultsQuanHuyen = data?.results;
                resultsQuanHuyen.forEach((e) => {
                    e.label = e.ten;
                    e.value = e.id;
                });

                if (thongTinDuocSi.tinh_thanh_id === id) {
                    const qhtt = resultsQuanHuyen.find(
                        (e) => e.id === thongTinDuocSi.quan_huyen_id
                    );
                    setSelectedQHTT(qhtt);
                    getXaPhuong(qhtt.id, "thuongtru");
                }
                if (thongTinDuocSi.tinh_thanh_hien_nay_id === id) {
                    const qhhn = resultsQuanHuyen.find(
                        (e) => e.id === thongTinDuocSi.quan_huyen_hien_nay_id
                    );
                    setSelectedQHHN(qhhn);
                    getXaPhuong(qhhn.id, "hiennay");
                }

                if (type === "thuongtru") {
                    setListQuanHuyen(resultsQuanHuyen);
                } else if (type === "hiennay") {
                    setListQuanHuyenCOHN(resultsQuanHuyen);
                }
            })
            .catch((error) => {
                if (error.response.data) {
                    toast.error(error.response.data.msg, {
                        position: "top-right",
                        autoClose: 2000,
                        hideProgressBar: false,
                        closeOnClick: true,
                        pauseOnHover: true,
                        draggable: true,
                        progress: undefined,
                        toastId: "success",
                    });
                }
            });
    };
    const getXaPhuong = (id, type) => {
        axios
            .get(`${api.API_XA_PHUONG}/${id}?per_page=100`)
            .then(({ data }) => {
                let resultsXaPhuong = data?.results;
                resultsXaPhuong.forEach((e) => {
                    e.label = e.ten;
                    e.value = e.id;
                });

                if (thongTinDuocSi.quan_huyen_id === id) {
                    const xptt = resultsXaPhuong.find(
                        (e) => e.id === thongTinDuocSi.xa_phuong_id
                    );
                    setSelectedXPTT(xptt);
                }
                if (thongTinDuocSi.quan_huyen_hien_nay_id === id) {
                    const xphn = resultsXaPhuong.find(
                        (e) => e.id === thongTinDuocSi.xa_phuong_hien_nay_id
                    );
                    setSelectedXPHN(xphn);
                }

                if (type === "thuongtru") {
                    setListXaPhuong(resultsXaPhuong);
                } else if (type === "hiennay") {
                    setListXaPhuongCOHN(resultsXaPhuong);
                }
            })
            .catch((error) => {
                if (error.response.data) {
                    toast.error(error.response.data.msg, {
                        position: "top-right",
                        autoClose: 2000,
                        hideProgressBar: false,
                        closeOnClick: true,
                        pauseOnHover: true,
                        draggable: true,
                        progress: undefined,
                        toastId: "success",
                    });
                }
            });
    };

    // HANDLE LOCATIONs
    const onChangeTinhThanh = (values) => {
        setField("tinh_thanh_id", values.id);
        getQuanHuyen(values.id, "thuongtru");
        setSelectedTTTT(values);
        setSelectedQHTT(null);
        setSelectedXPTT(null);
    };
    const onChangeQuanHuyen = (values) => {
        setField("quan_huyen_id", values.id);
        getXaPhuong(values.id, "thuongtru");
        setSelectedQHTT(values);
        setSelectedXPTT(null);
    };
    const onChangeXaPhuong = (values) => {
        setField("xa_phuong_id", values.id);
        setSelectedXPTT(values);
    };

    const onChangeTinhThanhCOHN = (values) => {
        setField("tinh_thanh_hien_nay_id", values.id);
        getQuanHuyen(values.id, "hiennay");
        setSelectedTTHN(values);
        setSelectedQHHN(null);
        setSelectedXPHN(null);
    };
    const onChangeQuanHuyenCOHN = (values) => {
        setField("quan_huyen_hien_nay_id", values.id);
        getXaPhuong(values.id, "hiennay");
        setSelectedQHHN(values);
        setSelectedXPHN(null);
    };
    const onChangeXaPhuongCOHN = (values) => {
        setField("xa_phuong_hien_nay_id", values.id);
        setSelectedXPHN(values);
    };

    const getCATinhThanh = () => {
        axios
            .get(api.API_CA_TINH_THANH)
            .then(async ({ data }) => {
                const list = data.results;
                setListNoiCapCMND(list);
            })
            .catch((error) => {
                toast.error(error?.response?.data?.msg, {
                    position: "top-right",
                    autoClose: 2000,
                    hideProgressBar: false,
                    closeOnClick: true,
                    pauseOnHover: true,
                    draggable: true,
                    progress: undefined,
                    toastId: "error",
                });
            })
            .finally(() => { });
    };

    const selectedNS = (date) => {
        setField("ngay_sinh", new Date(date).getTime());
    };
    const handleNgayCap = (date) => {
        setField("ngay_cap", new Date(date).getTime());
    };
    const handleGioiTinh = () => {
        if (thongTinDuocSi.gioi_tinh) {
            GioiTinh.forEach((e) => {
                if (e.value === thongTinDuocSi.gioi_tinh) {
                    setSelectedGioiTinh(e);
                }
            });
        }
    };

    const findFormErrors = () => {
        const { ho, ten, ngay_sinh, ma_cong_dan, ngay_cap, dien_thoai, email } =
            form;
        const newErrors = {};

        if (!ho || ho === "") newErrors.ho = "Họ không được bỏ trống!";
        if (!ten || ten === "") newErrors.ten = "Tên không được bỏ trống!";
        if (!ma_cong_dan || ma_cong_dan === "")
            newErrors.ma_cong_dan = "CMND/CCCD không được bỏ trống!";
        if (!dien_thoai || dien_thoai === "")
            newErrors.dien_thoai = "Số điện thoại không được bỏ trống!";
        if (!email || email === "") newErrors.email = "Email không được bỏ trống!";

        if (!ngay_sinh) newErrors.ngay_sinh = "Ngày sinh không được bỏ trống!";
        else if (ngay_sinh > new Date().getTime())
            newErrors.ngay_sinh = "Ngày sinh không hợp lệ!";
        if (!ngay_cap)
            newErrors.ngay_cap = "Ngày cấp CMND/CCCD không được bỏ trống!";
        else if (ngay_cap > new Date().getTime())
            newErrors.ngay_cap = "Ngày cấp CMND/CCCD không hợp lệ!";

        if (!selectedGioiTinh) {
            newErrors.gioi_tinh = "Giới tính không được bỏ trống!";
        }
        if (!selectedNoiCapCMND) {
            newErrors.noi_cap = "Nơi cấp CMND/CCCD không được bỏ trống!";
        }

        if (!selectedTTTT) {
            newErrors.selectedTTTT = "Tỉnh thành thường trú không được bỏ trống!";
        }
        if (!selectedQHTT) {
            newErrors.selectedQHTT = "Quận huyện thường trú không được bỏ trống!";
        }
        if (!selectedXPTT) {
            newErrors.selectedXPTT = "Xã phường thường trú không được bỏ trống!";
        }
        if (!selectedTTHN) {
            newErrors.selectedTTHN = "Tỉnh thành hiện nay không được bỏ trống!";
        }
        if (!selectedQHHN) {
            newErrors.selectedQHHN = "Quận huyện hiện nay không được bỏ trống!";
        }
        if (!selectedXPHN) {
            newErrors.selectedXPHN = "Xã phường hiện nay không được bỏ trống!";
        }

        return newErrors;
    };

    const onSubmit = async (e) => {
        await setIsLoading(true);
        e.preventDefault();
        const newErrors = findFormErrors();
        if (Object.keys(newErrors).length > 0) {
            setErrors(newErrors);
            setIsLoading(false);
        } else {
            const { ho, ten, ngay_sinh, ma_cong_dan, ngay_cap, dien_thoai, email } =
                form;

            const json = new FormData();

            await json.append("ho", ho);
            await json.append("ten", ten);
            await json.append("ngay_sinh", ngay_sinh);
            await json.append(
                "gioi_tinh",
                selectedGioiTinh ? selectedGioiTinh.value : null
            );
            await json.append("ma_cong_dan", ma_cong_dan);
            await json.append("ngay_cap", ngay_cap);
            await json.append(
                "noi_cap",
                selectedNoiCapCMND ? selectedNoiCapCMND.label : null
            );
            await json.append("dien_thoai", dien_thoai);
            await json.append("email", email);
            await json.append(
                "tinh_thanh_id",
                selectedTTTT ? selectedTTTT.value : null
            );
            await json.append(
                "quan_huyen_id",
                selectedQHTT ? selectedQHTT.value : null
            );
            await json.append(
                "xa_phuong_id",
                selectedXPTT ? selectedXPTT.value : null
            );
            await json.append(
                "tinh_thanh_hien_nay_id",
                selectedTTHN ? selectedTTHN.value : null
            );
            await json.append(
                "quan_huyen_hien_nay_id",
                selectedQHHN ? selectedQHHN.value : null
            );
            await json.append(
                "xa_phuong_hien_nay_id",
                selectedXPHN ? selectedXPHN.value : null
            );

            axios
                .put(api.API_QL_NGUOI_DUNG_ID + "/" + thongTinDuocSi.id, json)
                .then(({ data }) => {
                    toast.success("Cập nhật thông tin thành công", {
                        position: "top-right",
                        autoClose: 2000,
                        hideProgressBar: false,
                        closeOnClick: true,
                        pauseOnHover: true,
                        draggable: true,
                        progress: undefined,
                        toastId: "success",
                    });
                    dispatch(
                        authActions.setCurrentUser({
                            ...data?.results,
                            assigned_role: roleUser,
                        })
                    );
                })
                .catch((error) => {
                    toast.error(error?.data?.errors, {
                        position: "top-right",
                        autoClose: 2000,
                        hideProgressBar: false,
                        closeOnClick: true,
                        pauseOnHover: true,
                        draggable: true,
                        progress: undefined,
                        toastId: "error",
                    });
                })
                .finally(() => {
                    setIsLoading(false);
                });
        }
    };

    const cardBox = {
        backgroundColor: "#fff",
        backgroundClip: "border-box",
        border: "1px solid #e7eaed",
        padding: "1.5rem",
        marginBottom: "24px",
        borderRadius: "0.5rem",
    };
    const usersBreadcrumbs = [
        {
            title: "Trang chủ",
            path: "/",
            isSeparator: false,
            isActive: false,
        },
        {
            title: "",
            path: "",
            isSeparator: true,
            isActive: false,
        },
    ];

    return (
        <div>
            <LoadingOverlay spinner active={isLoading} text="Đang tải dữ liệu ...">
                <LocalizationProvider dateAdapter={AdapterDateFns} locale={enAU}>
                    <PageTitle breadcrumbs={usersBreadcrumbs}>
                        Thông tin tài khoản
                    </PageTitle>
                    <div className="card">
                        <div className="card-body">
                            <div className="row">
                                <div className="row col-12 d-flex justify-content-between mx-0">
                                    <div className="col-md-6">
                                        <div style={cardBox}>
                                            <ImageUploading maxNumber={1} dataURLKey="data_url">
                                                {({
                                                    imageList,
                                                    onImageUpload,
                                                    onImageRemove,
                                                    isDragging,
                                                    dragProps,
                                                }) => (
                                                    <div className="form-group">
                                                        {imageList.length >= 1 ? (
                                                            imageList.map((image, index) => (
                                                                <div key={index} className="form-group">
                                                                    <button
                                                                        style={{ fontSize: "14px" }}
                                                                        className="btn btn-danger"
                                                                        onClick={() => onImageRemove(index)}
                                                                    >
                                                                        Xóa
                                                                    </button>
                                                                    <div>
                                                                        <img
                                                                            className="mr-2 my-2"
                                                                            src={image.data_url}
                                                                            alt=""
                                                                            width="140"
                                                                        />
                                                                    </div>
                                                                </div>
                                                            ))
                                                        ) : (
                                                            <div>
                                                                <div className="avatar-img">
                                                                    <i
                                                                        className="fas fa-camera btn-change-avatar"
                                                                    // onClick={onImageUpload}
                                                                    ></i>
                                                                    <img
                                                                        src="/media/avatars/default-forum-user.png"
                                                                        alt=""
                                                                        className="rounded-circle border p-1"
                                                                        id="avatar-img"
                                                                        width="150"
                                                                        height="150"
                                                                        backgroundcolor="red"
                                                                    />
                                                                </div>
                                                            </div>
                                                        )}
                                                    </div>
                                                )}
                                            </ImageUploading>
                                            <div>
                                                <label className="font-weight-bold"></label>
                                            </div>
                                            <div className="row">
                                                <div className="col-6">
                                                    <div className="form-group">
                                                        <label className="fw-bold fs-6 mb-1">
                                                            Họ <span className="text-danger"> (*) </span>
                                                        </label>
                                                        <Form.Control
                                                            type="text"
                                                            placeholder="Nhập họ"
                                                            style={{
                                                                fontSize: 14,
                                                                fontWeight: 400,
                                                                height: "38px",
                                                            }}
                                                            onChange={(e) => {
                                                                setField("ho", e.target.value);
                                                            }}
                                                            defaultValue={form.ho != null ? form?.ho : ""}
                                                        />
                                                        {errors?.ho !== null ? (
                                                            <span className="text-danger"> {errors?.ho}</span>
                                                        ) : null}
                                                    </div>
                                                </div>
                                                <div className="col-6">
                                                    <div className="form-group">
                                                        <label className="fw-bold fs-6 mb-1">
                                                            Tên <span className="text-danger"> (*) </span>
                                                        </label>
                                                        <Form.Control
                                                            type="text"
                                                            placeholder="Nhập tên"
                                                            style={{
                                                                fontSize: 14,
                                                                fontWeight: 400,
                                                                height: "38px",
                                                            }}
                                                            onChange={(e) => {
                                                                setField("ten", e.target.value);
                                                            }}
                                                            defaultValue={form.ten != null ? form?.ten : ""}
                                                        />
                                                        {errors?.ten !== null ? (
                                                            <span className="text-danger">
                                                                {" "}
                                                                {errors?.ten}
                                                            </span>
                                                        ) : null}
                                                    </div>
                                                </div>
                                            </div>

                                            <div className="form-group mt-2">
                                                <label className="fw-bold fs-6 mb-2">
                                                    Ngày sinh <span className="text-danger"> (*) </span>
                                                </label>
                                                <DesktopDatePicker
                                                    label=" "
                                                    inputFormat="dd/MM/yyyy"
                                                    onChange={selectedNS}
                                                    maxDate={new Date()}
                                                    renderInput={(params) => (
                                                        <TextField size="small" {...params} />
                                                    )}
                                                    value={form.ngay_sinh ? form.ngay_sinh : null}
                                                />
                                                {errors?.ngay_sinh !== null ? (
                                                    <span className="text-danger">
                                                        {" "}
                                                        {errors?.ngay_sinh}
                                                    </span>
                                                ) : null}
                                            </div>

                                            <div className="form-group mt-2">
                                                <label className="fw-bold fs-6 mb-2">
                                                    Giới tính <span className="text-danger"> (*) </span>
                                                </label>
                                                <Select
                                                    key={"id"}
                                                    options={GioiTinh}
                                                    value={selectedGioiTinh}
                                                    placeholder="Chọn giới tính"
                                                    formatGroupLabel={formatGroupLabel}
                                                    onChange={(e) => {
                                                        setSelectedGioiTinh(e);
                                                    }}
                                                />
                                                {errors?.gioi_tinh !== null ? (
                                                    <span className="text-danger">
                                                        {" "}
                                                        {errors?.gioi_tinh}
                                                    </span>
                                                ) : null}
                                            </div>

                                            <div className="form-group mt-2">
                                                <label className="fw-bold fs-6 mb-2">
                                                    CMND/CCCD <span className="text-danger"> (*) </span>
                                                </label>
                                                <Form.Control
                                                    type="text"
                                                    placeholder="Nhập CMND/CCCD"
                                                    style={{
                                                        fontSize: 14,
                                                        fontWeight: 400,
                                                        height: "38px",
                                                    }}
                                                    onChange={(e) => {
                                                        setField("ma_cong_dan", e.target.value);
                                                    }}
                                                    value={
                                                        form.ma_cong_dan != null ? form.ma_cong_dan : ""
                                                    }
                                                />
                                                {errors?.ma_cong_dan !== null ? (
                                                    <span className="text-danger">
                                                        {" "}
                                                        {errors?.ma_cong_dan}
                                                    </span>
                                                ) : null}
                                            </div>

                                            <div className="form-group mt-2">
                                                <label className="fw-bold fs-6 mb-2">
                                                    Ngày cấp <span className="text-danger"> (*) </span>
                                                </label>
                                                <DesktopDatePicker
                                                    label=" "
                                                    inputFormat="dd/MM/yyyy"
                                                    onChange={handleNgayCap}
                                                    maxDate={new Date()}
                                                    renderInput={(params) => (
                                                        <TextField size="small" {...params} />
                                                    )}
                                                    value={form.ngay_cap != null ? form?.ngay_cap : null}
                                                />
                                                {errors?.ngay_cap !== null ? (
                                                    <span className="text-danger">
                                                        {" "}
                                                        {errors?.ngay_cap}
                                                    </span>
                                                ) : null}
                                            </div>

                                            <div className="form-group mt-2">
                                                <label className="required fw-bold fs-6 mb-2">
                                                    Nơi cấp CMND/CCCD
                                                </label>
                                                <Select
                                                    key={"id"}
                                                    options={listNoiCapCMND}
                                                    value={selectedNoiCapCMND}
                                                    placeholder="Chọn nơi cấp CMND/CCCD"
                                                    formatGroupLabel={formatGroupLabel}
                                                    onChange={(e) => {
                                                        setField("noi_cap", e.value);
                                                        setSelectedNoiCapCMND(e);
                                                    }}
                                                />
                                                {errors?.noi_cap !== null ? (
                                                    <span className="text-danger">
                                                        {" "}
                                                        {errors?.noi_cap}
                                                    </span>
                                                ) : null}
                                            </div>

                                            <div className="form-group mt-2">
                                                <label className="fw-bold fs-6 mb-2">
                                                    Số điện thoại{" "}
                                                    <span className="text-danger"> (*) </span>
                                                </label>
                                                <Form.Control
                                                    type="text"
                                                    placeholder="Nhập số điện thoại"
                                                    style={{
                                                        fontSize: 14,
                                                        fontWeight: 400,
                                                        height: "38px",
                                                    }}
                                                    onChange={(e) => {
                                                        setField("dien_thoai", e.target.value);
                                                    }}
                                                    value={form.dien_thoai != null ? form.dien_thoai : ""}
                                                />
                                                {errors?.dien_thoai !== null ? (
                                                    <span className="text-danger">
                                                        {" "}
                                                        {errors?.dien_thoai}
                                                    </span>
                                                ) : null}
                                            </div>

                                            <div className="form-group mt-2">
                                                <label className="fw-bold fs-6 mb-2">
                                                    Email <span className="text-danger"> (*) </span>
                                                </label>
                                                <Form.Control
                                                    type="text"
                                                    placeholder="Nhập email"
                                                    style={{
                                                        fontSize: 14,
                                                        fontWeight: 400,
                                                        height: "38px",
                                                    }}
                                                    onChange={(e) => {
                                                        setField("email", e.target.value);
                                                    }}
                                                    value={form.email != null ? form.email : ""}
                                                />
                                                {errors?.email !== null ? (
                                                    <span className="text-danger"> {errors?.email}</span>
                                                ) : null}
                                            </div>
                                        </div>
                                    </div>

                                    <div className="col-md-6">
                                        <div style={cardBox}>
                                            <div className="mt-5">
                                                <label className="font-weight-bold text-uppercase">
                                                    Hộ khẩu thường trú:
                                                </label>
                                            </div>

                                            <div className="form-group mt-4">
                                                <label className="fw-bold required fs-6 mb-2"> Tỉnh/Thành phố </label>
                                                <Select
                                                    key={"id"}
                                                    labelField={"ten"}
                                                    placeholder="Chọn tỉnh thành"
                                                    searchBy={"tenkhongdau"}
                                                    options={listTinhThanh}
                                                    onChange={(values) => onChangeTinhThanh(values)}
                                                    value={selectedTTTT}
                                                />
                                                {errors?.selectedTTTT !== null &&
                                                    !form.tinh_thanh_id ? (
                                                    <span className="text-danger">
                                                        {errors?.selectedTTTT}
                                                    </span>
                                                ) : null}
                                            </div>

                                            <div className="form-group mt-4">
                                                <label className="fw-bold fs-6 mb-2">
                                                    Quận/Huyện <span className="text-danger"> (*) </span>
                                                </label>
                                                <Select
                                                    key={"id"}
                                                    labelField={"ten"}
                                                    placeholder="Chọn quận huyện"
                                                    options={listQuanHuyen}
                                                    onChange={(values) =>
                                                        values ? onChangeQuanHuyen(values) : null
                                                    }
                                                    isDisabled={selectedTTTT ? false : true}
                                                    value={selectedQHTT}
                                                />
                                                {errors?.selectedQHTT !== null &&
                                                    !form.quan_huyen_id ? (
                                                    <span className="text-danger">
                                                        {" "}
                                                        {errors?.selectedQHTT}
                                                    </span>
                                                ) : null}
                                            </div>

                                            <div className="form-group mt-4">
                                                <label className="fw-bold fs-6 mb-2">
                                                    Xã/Phường <span className="text-danger"> (*) </span>
                                                </label>
                                                <Select
                                                    key={"id"}
                                                    labelField={"ten"}
                                                    placeholder="Chọn xã phường"
                                                    options={listXaPhuong}
                                                    onChange={(values) =>
                                                        values ? onChangeXaPhuong(values) : null
                                                    }
                                                    isDisabled={selectedQHTT ? false : true}
                                                    value={selectedXPTT}
                                                />
                                                {errors?.selectedXPTT !== null && !form.xa_phuong_id ? (
                                                    <span className="text-danger">
                                                        {" "}
                                                        {errors?.selectedXPTT}
                                                    </span>
                                                ) : null}
                                            </div>

                                            <div className="form-group mt-4">
                                                <label className="fw-bold fs-6 mb-2">
                                                    Số nhà, tên đường
                                                </label>
                                                <Form.Control
                                                    type="text"
                                                    placeholder="Nhập số nhà tên đường"
                                                    style={{
                                                        fontSize: 14,
                                                        fontWeight: 400,
                                                        height: "38px",
                                                    }}
                                                    value={form.dia_chi != null ? form.dia_chi : ""}
                                                    onChange={(e) => {
                                                        setField("dia_chi", e.target.value);
                                                    }}
                                                />
                                            </div>

                                            <div style={{ marginTop: "3rem" }}>
                                                <label className="font-weight-bold text-uppercase">
                                                    Chỗ ở hiện nay:
                                                </label>
                                            </div>

                                            <div className="form-group mt-5">
                                                <label className="fw-bold fs-6 mb-2">
                                                    Tỉnh/Thành phố{" "}
                                                    <span className="text-danger"> (*) </span>
                                                </label>
                                                <Select
                                                    key={"id"}
                                                    labelField={"ten"}
                                                    placeholder="Chọn tỉnh thành"
                                                    searchBy={"tenkhongdau"}
                                                    options={listTinhThanhCOHN}
                                                    onChange={(values) => onChangeTinhThanhCOHN(values)}
                                                    value={selectedTTHN}
                                                />
                                                {errors?.selectedTTHN !== null &&
                                                    !form.tinh_thanh_hien_nay_id ? (
                                                    <span className="text-danger">
                                                        {" "}
                                                        {errors?.selectedTTHN}
                                                    </span>
                                                ) : null}
                                            </div>

                                            <div className="form-group mt-4">
                                                <label className="fw-bold fs-6 mb-2">
                                                    Quận/Huyện <span className="text-danger"> (*) </span>
                                                </label>
                                                <Select
                                                    key={"id"}
                                                    className={"quan_huyen"}
                                                    labelField={"ten"}
                                                    placeholder="Chọn quận huyện"
                                                    options={listQuanHuyenCOHN}
                                                    onChange={(values) =>
                                                        values ? onChangeQuanHuyenCOHN(values) : null
                                                    }
                                                    isDisabled={selectedTTHN ? false : true}
                                                    value={selectedQHHN}
                                                />
                                                {errors?.selectedQHHN !== null &&
                                                    !form.quan_huyen_hien_nay_id ? (
                                                    <span className="text-danger">
                                                        {" "}
                                                        {errors?.selectedQHHN}
                                                    </span>
                                                ) : null}
                                            </div>

                                            <div className="form-group mt-4">
                                                <label className="fw-bold fs-6 mb-2">
                                                    Xã/Phường <span className="text-danger"> (*) </span>
                                                </label>
                                                <Select
                                                    key={"id"}
                                                    labelField={"ten"}
                                                    placeholder="Chọn xã phường"
                                                    options={listXaPhuongCOHN}
                                                    onChange={(values) =>
                                                        values ? onChangeXaPhuongCOHN(values) : null
                                                    }
                                                    isDisabled={selectedQHHN ? false : true}
                                                    value={selectedXPHN}
                                                />
                                                {errors?.selectedXPHN !== null &&
                                                    !form.xa_phuong_hien_nay_id ? (
                                                    <span className="text-danger">
                                                        {" "}
                                                        {errors?.selectedXPHN}
                                                    </span>
                                                ) : null}
                                            </div>

                                            <div className="form-group mt-4">
                                                <label className="fw-bold fs-6 mb-2">
                                                    Số nhà, tên đường
                                                </label>
                                                <Form.Control
                                                    type="text"
                                                    placeholder="Nhập số nhà tên đường"
                                                    style={{
                                                        fontSize: 14,
                                                        fontWeight: 400,
                                                        height: "38px",
                                                    }}
                                                    value={
                                                        form.dia_chi_thuong_tru === null
                                                            ? ""
                                                            : form.dia_chi_thuong_tru
                                                    }
                                                    onChange={(e) => {
                                                        setField("dia_chi_thuong_tru", e.target.value);
                                                    }}
                                                />
                                            </div>
                                        </div>
                                    </div>
                                </div>
                                <div className="col-12 text-center mt-5">
                                    <button
                                        className="btn btn-secondary"
                                        onClick={() => navigate(-1)}
                                    >
                                        Trở về
                                    </button>
                                    <button
                                        onClick={(e) => {
                                            onSubmit(e);
                                        }}
                                        className="btn btn-primary"
                                        style={{ marginLeft: "10px" }}
                                    >
                                        Lưu thông tin
                                    </button>
                                </div>
                            </div>
                        </div>
                    </div>
                </LocalizationProvider>
            </LoadingOverlay>
        </div>
    );
}
