/* eslint-disable react-hooks/exhaustive-deps */

import { AdapterDateFns } from "@mui/x-date-pickers/AdapterDateFns";
import { enAU } from "date-fns/locale";
import axios from "axios";
import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import Select from "react-select";
import { toast } from "react-toastify";
import { PageTitle } from "../../../../_metronic/layout/core";
import api from "../../../configs/api";

import {
  authActions,
  selectCurrentUser,
  selectRoleUser,
} from "../../../redux-module/auth/authSlice";
import LoadingOverlay from "react-loading-overlay";
import { Form } from "react-bootstrap";

export default function DoiMatKhau() {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const roleUser = useSelector(selectRoleUser);
  console.log("first,roleUser", roleUser);
  const thongTinDuocSi = useSelector(selectCurrentUser);
  console.log("thongTinDuocSi", thongTinDuocSi);
  const [isLoading, setIsLoading] = useState(false);
  const [hoTen, setHoTen] = useState(thongTinDuocSi?.assigned_role[0]?.ten);
  const [taiKhoan, setTaiKhoan] = useState(thongTinDuocSi?.tai_khoan);
  const [password, setPassword] = useState("");
  const [confirmPass, setConfirmPass] = useState("");
  console.log("hoTen", hoTen);
  //   const [hoTen, setHoTen] = useState("");
  //   const initialState = {
  //     dien_thoai: "",
  //     tai_khoan: "",
  //     mat_khau: "",
  //     retype: "",
  //   };

  const usersBreadcrumbs = [
    {
      title: "Trang chủ",
      path: "/",
      isSeparator: false,
      isActive: false,
    },
    {
      title: "",
      path: "",
      isSeparator: true,
      isActive: false,
    },
  ];
  const [form, setForm] = useState({});
  const [errors, setErrors] = useState({});
  const setField = (field, value) => {
    setForm({
      ...form,
      [field]: value,
    });
    if (!!errors[field])
      setErrors({
        ...errors,
        [field]: null,
      });
  };

  const findFormErrors = () => {
    const { tai_khoan, mat_khau, dien_thoai, retype } = form;
    const newErrors = {};
    // name errors
    if (!tai_khoan || tai_khoan === "")
      newErrors.tai_khoan = "Tài khoản không được bỏ trống!";
    if (!mat_khau || mat_khau === "")
      newErrors.mat_khau = "Mật khẩu được bỏ trống!";
    if (!dien_thoai || dien_thoai === "")
      newErrors.dien_thoai = "Điện thoại không được bỏ trống!";
    if (!retype || retype === "")
      newErrors.retype = "Xác nhận mật khẩu không được bỏ trống!";
    if (mat_khau !== retype) newErrors.retype = "Mật khẩu không trùng";

    return newErrors;
  };

  //   const handleSubmit = (e) => {
  //     e.preventDefault();
  //     const newErrors = findFormErrors();
  //     if (Object.keys(newErrors).length > 0) {
  //       console.log(newErrors);
  //       setErrors(newErrors);
  //     } else {
  //       changePass(e);
  //     }
  //   };

  //   const changePass = async () => {
  //     const { tai_khoan, mat_khau, dien_thoai } = form;
  //     const json = {
  //       tai_khoan,
  //       mat_khau,
  //       dien_thoai,
  //       vai_tro_id: vaitro,
  //     };
  //     await axios
  //       .post(api.API_REGISTER, json)
  //       .then(async ({ data }) => {
  //         toast.success("Đổi mật khẩu thành công", {
  //           position: "top-right",
  //           autoClose: 2000,
  //           hideProgressBar: false,
  //           closeOnClick: true,
  //           pauseOnHover: true,
  //           draggable: true,
  //           progress: undefined,
  //           toastId: "success",
  //         });
  //         // setResetfield(initialState);
  //         // navigate(`/dang-nhap`);
  //       })
  //       .catch((error) => {
  //         console.log("error", error?.response);
  //         toast.error(error?.data?.errors, {
  //           position: "top-right",
  //           autoClose: 2000,
  //           hideProgressBar: false,
  //           closeOnClick: true,
  //           pauseOnHover: true,
  //           draggable: true,
  //           progress: undefined,
  //           toastId: "error",
  //         });
  //       })
  //       .finally(() => {});
  //   };
  function getPassword(event) {
    setPassword(event.target.value);
  }
  function confirmPassword(event) {
    let val = event.target.value;
    console.log(confirmPass);
    if (password === val) {
      setConfirmPass("");
    } else {
      setConfirmPass("Mật khẩu không khớp");
    }
  }

  return (
    <div>
      <LoadingOverlay spinner active={isLoading} text="Đang tải dữ liệu ...">
        <PageTitle breadcrumbs={usersBreadcrumbs}>Đổi mật khẩu</PageTitle>
        <div className="card-body w-100 w-md-75 p-3 m-auto ">
          <Form action="#" className="box-changepass-wrapper">
            <div className="form-group mt-10 mb-6 input-icon">
              <label className="label-input fw-bold">Họ và tên</label>
              <Form.Control
                type="text"
                placeholder="Họ và tên"
                className="input-register input-login"
                style={{ fontSize: 14, fontWeight: 400 }}
                disabled
                value={hoTen}
                // value={form.dien_thoai === null ? "" : form.dien_thoai}
                // onChange={(e) => {
                //   setField("dien_thoai", e.target.value);
                // }}
                // isInvalid={!!errors.dien_thoai}
              />
              {/* <i className="fa fa-user"></i> */}

              {/* <Form.Control.Feedback type="invalid">
                        {errors.dien_thoai}
                      </Form.Control.Feedback> */}
            </div>
            <div className="form-group mb-6">
              <label className="label-input fw-bold">Tên đăng nhập</label>
              <div className="input-group input-icon">
                <Form.Control
                  type="text"
                  placeholder="Tên đăng nhập"
                  className="input-register"
                  style={{ fontSize: 14, fontWeight: 400 }}
                  disabled
                  value={taiKhoan}
                  //   value={form.tai_khoan === null ? "" : form.tai_khoan}
                  //   onChange={(e) => {
                  //     setField("tai_khoan", e.target.value);
                  //   }}
                  //   isInvalid={!!errors.tai_khoan}
                />
                {/* <i className="fa fa-user"></i> */}
                {/* <Form.Control.Feedback type="invalid">
                          {errors.tai_khoan}
                        </Form.Control.Feedback> */}
              </div>
            </div>

            <div className="form-group mb-6">
              <label className="label-input fw-bold">Mật khẩu cũ</label>
              <div className="input-group input-icon">
                <Form.Control
                  type="password"
                  placeholder="Nhập mật khẩu cũ"
                  className="input-register"
                  style={{ fontSize: 14, fontWeight: 400 }}
                  //   value={form.mat_khau === null ? "" : form.mat_khau}
                  //   onChange={(e) => {
                  //     setField("mat_khau", e.target.value);
                  //   }}
                  //   isInvalid={!!errors.mat_khau}
                />
                {/* <i className="fa fa-key"></i> */}

                {/* <Form.Control.Feedback type="invalid">
                          {errors.mat_khau}
                        </Form.Control.Feedback> */}
              </div>
            </div>

            <div className="form-group mb-10">
              <label className="label-input fw-bold">Nhập mật khẩu mới</label>
              <div className="input-group input-icon">
                <Form.Control
                  type="password"
                  placeholder="Nhập mật khẩu mới"
                  className="input-register"
                  style={{ fontSize: 14, fontWeight: 400 }}
                  onChange={getPassword}
                  //   onChange={(e) => {
                  //     setField("retype", e.target.value);
                  //   }}
                  //   isInvalid={!!errors.retype}
                />
                {/* <i className="fa fa-key"></i> */}

                {/* <Form.Control.Feedback type="invalid">
                          {errors.retype}
                        </Form.Control.Feedback> */}
              </div>
            </div>
            <div className="form-group mb-10">
              <label className="label-input fw-bold">
                Xác nhận mật khẩu mới
              </label>
              <div className="input-group input-icon">
                <Form.Control
                  type="password"
                  placeholder="Nhập lại mật khẩu mới"
                  className="input-register"
                  style={{ fontSize: 14, fontWeight: 400 }}
                  onChange={confirmPassword}
                  //   onChange={(e) => {
                  //     setField("retype", e.target.value);
                  //   }}
                  //   isInvalid={!!errors.retype}
                />
                {/* <i className="fa fa-key"></i> */}

                {/* <Form.Control.Feedback type="invalid">
                          {errors.retype}
                        </Form.Control.Feedback> */}
              </div>
              <p style={{ color: "red" }}>{confirmPass}</p>
            </div>
            <div className="d-flex flex-row justify-content-center">
              <div className="form-group my-2 text-center m-5">
                <button
                  className="btn btn-secondary btn-block w-100 mb-8 btn-trolai"
                  type="button"
                  id="btn-register"
                  onClick={() => {
                    navigate(-1);
                  }}
                >
                  <i className="fas fa-chevron-circle-left"></i> Trở lại
                </button>
              </div>
              <div className="form-group my-2 text-center">
                <button
                  className="btn btn-primary btn-block btn-login w-100 mb-8"
                  type="button"
                  id="btn-register"
                  // onClick={async (e) => {
                  //   handleSubmit(e);
                  // }}
                >
                  <i className="fas fa-chevron-circle-right"></i> Đổi mật khẩu
                </button>
              </div>
            </div>
          </Form>
        </div>
      </LoadingOverlay>
    </div>
  );
}
