/* eslint-disable react-hooks/exhaustive-deps */

import axios from "axios";
import { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import { PageTitle } from "../../../../_metronic/layout/core";
import api from "../../../configs/api";

import { Form } from "react-bootstrap";
import LoadingOverlay from "react-loading-overlay";
import { selectCurrentUser, selectRoleUser } from "../../../redux-module/auth/authSlice";

export default function DoiMatKhau() {
  const navigate = useNavigate();
  const thongTinDuocSi = useSelector(selectCurrentUser);
  const currentRole = useSelector(selectRoleUser)[0]?.ten_en
  const [isLoading, setIsLoading] = useState(false);

  const usersBreadcrumbs = [
    {
      title: "Trang chủ",
      path: "/",
      isSeparator: false,
      isActive: false,
    },
    {
      title: "",
      path: "",
      isSeparator: true,
      isActive: false,
    },
  ];
  const [form, setForm] = useState({});
  const [errors, setErrors] = useState({});
  const setField = (field, value) => {
    setForm({
      ...form,
      [field]: value,
    });
    if (!!errors[field])
      setErrors({
        ...errors,
        [field]: null,
      });
  };

  const findFormErrors = () => {
    const { old_password, new_password, retype_password } = form;
    const newErrors = {};
    // name errors
    if (!old_password || old_password === "")
      newErrors.old_password = "Vui lòng nhập mật khẩu hiện tại!";
    if (!new_password || new_password === "")
      newErrors.new_password = "Vui lòng nhập mật khẩu mới!";
    if (!retype_password || retype_password === "")
      newErrors.retype_password = "Vui lòng nhập lại xác nhận mật khẩu";

    return newErrors;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newErrors = findFormErrors();
    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
    } else {
      setIsLoading(true)
      const { old_password, new_password, retype_password } = form;
      const json = {
        old_password: old_password,
        new_password: new_password,
        retype_password: retype_password,
      };
      await axios
        .put(api.API_CHANGE_PASSWORD, json)
        .then(async ({ data }) => {
          toast.success("Đổi mật khẩu thành công", {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "success",
          });
          handleNavigate()
        })
        .catch((error) => {
          toast.error(error?.response?.data?.msg, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "error",
          });
        })
        .finally(() => {
          setIsLoading(false)
        });
    }
  };

  const handleNavigate = () => {
    switch (currentRole) {
      case "duocsi":
        navigate("/admin/chung-chi-hanh-nghe")
        break;
      case "tochuc":
        navigate("/admin/giay-phep-kinh-doanh")
        break;
      default:
        navigate("/dashboard")
        break;
    }

  }


  return (
    <div>
      <LoadingOverlay spinner active={isLoading} text="Đang tải dữ liệu ...">
        <PageTitle breadcrumbs={usersBreadcrumbs}>Đổi mật khẩu</PageTitle>
        <div className="card-body w-100 w-md-75 p-3 m-auto ">
          <Form action="#" className="box-changepass-wrapper">
            <div className="form-group mt-10 mb-6 input-icon">
              <label className="label-input fw-bold">Họ và tên</label>
              <Form.Control
                type="text"
                placeholder="Họ và tên"
                className="input-register input-login"
                style={{ fontSize: 14, fontWeight: 400 }}
                disabled
                value={thongTinDuocSi?.assigned_role[0]?.ten ? thongTinDuocSi?.assigned_role[0]?.ten : ""}
              />
            </div>
            <div className="form-group mb-6">
              <label className="label-input fw-bold">Tên đăng nhập</label>
              <div className="input-group input-icon">
                <Form.Control
                  type="text"
                  placeholder="Tên đăng nhập"
                  className="input-register"
                  style={{ fontSize: 14, fontWeight: 400 }}
                  disabled
                  value={thongTinDuocSi?.tai_khoan ? thongTinDuocSi?.tai_khoan : ""}
                />
              </div>
            </div>

            <div className="form-group mb-6">
              <label className="label-input fw-bold">Mật khẩu cũ</label>
              <div className="input-group input-icon">
                <Form.Control
                  type="password"
                  placeholder="Nhập mật khẩu cũ"
                  className="input-register"
                  style={{ fontSize: 14, fontWeight: 400 }}
                  onChange={(e) => {
                    setField("old_password", e.target.value)
                  }}
                />
              </div>
            </div>

            <div className="form-group mb-10">
              <label className="label-input fw-bold">Nhập mật khẩu mới</label>
              <div className="input-group input-icon">
                <Form.Control
                  type="password"
                  placeholder="Nhập mật khẩu mới"
                  className="input-register"
                  style={{ fontSize: 14, fontWeight: 400 }}
                  onChange={(e) => {
                    setField("new_password", e.target.value)
                  }}
                />
              </div>
            </div>
            <div className="form-group mb-10">
              <label className="label-input fw-bold">
                Xác nhận mật khẩu mới
              </label>
              <div className="input-group input-icon">
                <Form.Control
                  type="password"
                  placeholder="Nhập lại mật khẩu mới"
                  className="input-register"
                  style={{ fontSize: 14, fontWeight: 400 }}
                  onChange={(e) => {
                    setField("retype_password", e.target.value)
                  }}
                />
              </div>
            </div>
            <div className="d-flex flex-row justify-content-center">
              <div className="form-group my-2 text-center m-5">
                <button
                  className="btn btn-secondary btn-block w-100 mb-8 btn-trolai"
                  type="button"
                  onClick={() => {
                    navigate(-1);
                  }}
                > Trở lại
                </button>
              </div>
              <div className="form-group my-2 text-center m-5">

                {isLoading ? (
                  <button
                    className="btn btn-primary btn-block btn-login w-100 mb-8"
                    type="submit"
                    id="btn-register"
                  >
                    <span
                      className="indicator-progress"
                      style={{ display: "block" }}
                    >
                      Vui lòng chờ...
                      <span className="spinner-border spinner-border-sm align-middle ms-2"></span>
                    </span>
                  </button>
                ) : (
                  <button
                    className="btn btn-primary btn-block btn-login w-100 mb-8"
                    type="submit"
                    id="btn-register"
                    onClick={async (e) => {
                      handleSubmit(e);
                    }}
                  >
                    Đổi mật khẩu
                  </button>
                )}
              </div>
            </div>
          </Form>
        </div>
      </LoadingOverlay>
    </div>
  );

};
