import axios from "axios";
import { toast } from "react-toastify";
import api from "../../../app/configs/api";

// GET LOCATIONS
const getTinhThanh = () => {
    let resultsTinhThanh = [];
    axios
        .get(api.API_TINH_THANH)
        .then(({ data }) => {
            resultsTinhThanh = data?.results;
            resultsTinhThanh.forEach((e) => {
                e.label = e.ten;
                e.value = e.id;
            })
            console.log(resultsTinhThanh)
            return resultsTinhThanh
        })
        .catch((error) => {
            if (error?.response?.data) {
                toast.error(error?.response?.data?.msg, {
                    position: "top-right",
                    autoClose: 2000,
                    hideProgressBar: false,
                    closeOnClick: true,
                    pauseOnHover: true,
                    draggable: true,
                    progress: undefined,
                    toastId: "success",
                });
            }
        })
        .finally(() => { });
}

export {
    getTinhThanh
}
