import {
  Checkbox,
  FormControl,
  FormControlLabel,
  Radio,
  RadioGroup,
} from "@mui/material";
import axios from "axios";
import { useEffect, useRef, useState } from "react";
import LoadingOverlay from "react-loading-overlay";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate, useParams } from "react-router-dom";
import { toast } from "react-toastify";
import api from "../../../../configs/api";
import { selectCurrentUser } from "../../../../redux-module/auth/authSlice";
import {
  commonActions,
  selectListIdChecked,
} from "../../../../redux-module/common/commonSlice";
import ThongTinCaNhan from "../../danh-sach-ho-so-thu-ly-duoc-si/components/thong-tin-ca-nhan/ThongTinCaNhan";
import { BangCap } from "../../duoc-si/bang-cap/BangCap";
import { LichSuDaoTao } from "../../duoc-si/lich-su-dao-tao/LichSuDaoTao";
import { LichSuThucHanh } from "../../duoc-si/lich-su-thuc-hanh/LichSuThucHanh";

LoadingOverlay.propTypes = undefined;
export function CapNhatHoSo() {
  const navigate = useNavigate();
  const params = useParams();
  const LoaiChungChi = useRef(null);
  const LoaiHinhThuc = useRef(null);
  const PhamViHNCM = useRef(null);

  const scrollToLoaiChungChi = () =>
    LoaiChungChi.current.scrollIntoView({
      behavior: "smooth",
      block: "center",
    });
  const scrollToLoaiHinhThuc = () =>
    LoaiHinhThuc.current.scrollIntoView({
      behavior: "smooth",
      block: "center",
    });
  const scrollToPhamViHNCM = () =>
    PhamViHNCM.current.scrollIntoView({ behavior: "smooth", block: "center" });

  useEffect(() => {
    if (params?.id) {
      getThongTinHoSo(params?.id);
    }
  }, []);

  const dispatch = useDispatch();
  const currentUser = useSelector(selectCurrentUser);
  const [isLoading, setIsLoading] = useState(false);
  const [thongTinHoSo, setThongTinHoSo] = useState({});
  const [DeNghiHinhThuc, setDeNghiHinhThuc] = useState(null);
  const [DeNghiLoaiDeNghi, setDeNghiLoaiDeNghi] = useState(null);
  const [DeNghiDMPVHN, setDeNghiDMPVHN] = useState(null);
  const [valueDeNghiLoai, setValueDeNghiLoai] = useState(null);
  const [valueDeNghiHinhThuc, setValueDeNghiHinhThuc] = useState(null);
  const [FileDeNghiCapLai, setFileDeNghiCapLai] = useState([]);
  const [FileAnhChanDung, setFileAnhChanDung] = useState([]);
  const [FileBanChinhHoacBanSau, setFileBanChinhHoacBanSau] = useState([]);
  const [FileBanChinhDaDuocCap, setFileBanChinhDaDuocCap] = useState([]);
  const [FileKhac, setFileKhac] = useState([]);
  const [FileVanBangChuyenMon, setFileVanBangChuyenMon] = useState([]);
  const [FileXacNhanSucKhoe, setFileXacNhanSucKhoe] = useState([]);
  const [FileXacNhanThucHanh, setFileXacNhanThucHanh] = useState([]);
  const [FileXacNhanDaoTao, setFileXacNhanDaoTao] = useState([]);
  const [FileXacNhanCongDan, setFileXacNhanCongDan] = useState([]);
  const [FileXacNhanLyLich, setFileXacNhanLyLich] = useState([]);
  const [FileXacNhanLePhi, setFileXacNhanLePhi] = useState([]);
  const [checkedCamKet, setCheckedCamKet] = useState(false);
  const [ValueNote, setValueNote] = useState("");
  const [checkSave, setCheckSave] = useState(false);
  const [valueTrangThaiHoSo, setValueTrangThaiHoSo] = useState("");
  const ListIdChecked = useSelector(selectListIdChecked);

  const handleChangeDeNghiLoai = (event) => {
    setValueDeNghiLoai(event.target.value);
  };

  const handleChangeDeNghiHinhThuc = (event) => {
    setValueDeNghiHinhThuc(event.target.value);
  };

  const getThongTinHoSo = async (id) => {
    await setIsLoading(true);
    axios
      .get(api.API_YEU_CAU_CHUNG_CHI_HANH_NGHE + "/" + id)
      .then(async ({ data }) => {
        const { result } = data;
        setValueNote(result?.noi_dung_dieu_chinh || result?.ly_do_tu_choi);
        if (result) {
          setThongTinHoSo(data?.result);
          if (result?.thu_tuc?.loai === "1") {
            setDeNghiHinhThuc(
              Object.entries(result?.de_nghi?.hinh_thuc_thi).map(
                ([key, value]) => ({ value: key, label: value })
              )
            );
            setDeNghiLoaiDeNghi(
              Object.entries(result?.de_nghi?.loai_de_nghi).map(
                ([key, value]) => ({ value: key, label: value })
              )
            );
            setDeNghiDMPVHN(result?.de_nghi?.danh_muc_pham_vi_hn);
            setValueDeNghiLoai(result?.checked_denghi?.loai_de_nghi);
            setValueDeNghiHinhThuc(result?.checked_denghi?.hinh_thuc_thi);
            setValueTrangThaiHoSo(result?.yeu_cau_cchnd?.trang_thai_ho_so);
            dispatch(
              commonActions.setListIdChecked(
                result?.checked_denghi?.danh_muc_pham_vi_hn?.id || []
              )
            );
          }
        }
      })
      .catch((error) => {
        toast.error("Lấy thông tin chứng chỉ không thành công", {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
        navigate("/error/404");
      })
      .finally(() => {
        setTimeout(() => {
          setIsLoading(false);
        }, 1000);
      });
  };

  const validateSubmit = () => {
    switch (true) {
      case thongTinHoSo?.thu_tuc?.loai === "2" && !ValueNote:
      case thongTinHoSo?.thu_tuc?.loai === "3" && !ValueNote:
        toast.error(
          thongTinHoSo?.thu_tuc?.loai === "2"
            ? "Vui lòng nhập lý do điều chỉnh"
            : "Vui lòng nhập lý do hỏng/mất",
          {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "error",
          }
        );
        return false;
      case thongTinHoSo?.thu_tuc?.loai === "3" && !checkedCamKet:
        toast.error("Vui lòng xác nhận cam kết", {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
        return false;
      case thongTinHoSo?.thu_tuc?.loai === "1" && !valueDeNghiLoai:
        scrollToLoaiChungChi();
        toast.error("Chưa chọn trường hợp cấp chứng chỉ", {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
        return false;
      case thongTinHoSo?.thu_tuc?.loai === "1" && !valueDeNghiHinhThuc:
        scrollToLoaiHinhThuc();
        toast.error("Chưa chọn hình thức cấp chứng chỉ", {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
        return false;
      case thongTinHoSo?.thu_tuc?.loai === "1" && ListIdChecked.length === 0:
        scrollToPhamViHNCM();
        toast.error("Chưa chọn phạm vi hành nghề", {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
        return false;
      default:
        return true;
    }
  };

  const handleSubmit = async (valueButton) => {
    setCheckSave(true);
    if (validateSubmit()) {
      await setIsLoading(true);

      // formData thutucdieuchinh
      const formDataUpdateThuTucDieuChinh = new FormData();
      formDataUpdateThuTucDieuChinh.append("noi_dung_dieu_chinh", ValueNote);
      for (let i = 0; i < FileDeNghiCapLai.length; i++) {
        formDataUpdateThuTucDieuChinh.append(
          "dinh_kem_don_de_nghi",
          FileDeNghiCapLai[i]
        );
      }
      for (let i = 0; i < FileAnhChanDung.length; i++) {
        formDataUpdateThuTucDieuChinh.append(
          "dinh_kem_anh_chan_dung",
          FileAnhChanDung[i]
        );
      }
      for (let i = 0; i < FileBanChinhHoacBanSau.length; i++) {
        formDataUpdateThuTucDieuChinh.append(
          "dinh_kem_xac_nhan_thay_doi",
          FileBanChinhHoacBanSau[i]
        );
      }
      for (let i = 0; i < FileBanChinhDaDuocCap.length; i++) {
        formDataUpdateThuTucDieuChinh.append(
          "dinh_kem_chung_chi",
          FileBanChinhDaDuocCap[i]
        );
      }
      for (let i = 0; i < FileKhac.length; i++) {
        formDataUpdateThuTucDieuChinh.append(
          "dinh_kem_xac_nhan_files",
          FileKhac[i]
        );
      }
      formDataUpdateThuTucDieuChinh.append("trang_thai_ho_so", valueButton);

      // formData thutuccaplai
      const formDataUpdateThuTucCapLai = new FormData();
      formDataUpdateThuTucCapLai.append("ly_do_mat_hong", ValueNote);
      for (let i = 0; i < FileDeNghiCapLai.length; i++) {
        formDataUpdateThuTucCapLai.append(
          "dinh_kem_don_de_nghi",
          FileDeNghiCapLai[i]
        );
      }
      for (let i = 0; i < FileAnhChanDung.length; i++) {
        formDataUpdateThuTucCapLai.append(
          "dinh_kem_anh_chan_dung",
          FileAnhChanDung[i]
        );
      }
      for (let i = 0; i < FileBanChinhDaDuocCap.length; i++) {
        formDataUpdateThuTucCapLai.append(
          "dinh_kem_chung_chi",
          FileBanChinhDaDuocCap[i]
        );
      }
      for (let i = 0; i < FileKhac.length; i++) {
        formDataUpdateThuTucCapLai.append(
          "dinh_kem_xac_nhan_files",
          FileKhac[i]
        );
      }
      formDataUpdateThuTucCapLai.append("trang_thai_ho_so", valueButton);

      // formData thutuccapmoi
      const formDataUpdateThuTucCapMoi = new FormData();
      formDataUpdateThuTucCapMoi.append("loai_cap_chung_chi", valueDeNghiLoai);

      formDataUpdateThuTucCapMoi.append("hinh_thuc", valueDeNghiHinhThuc);

      for (let i = 0; i < ListIdChecked.length; i++) {
        formDataUpdateThuTucCapMoi.append(
          "vi_tri_hanh_nghe_ids",
          ListIdChecked[i]
        );
      }
      for (let i = 0; i < FileDeNghiCapLai.length; i++) {
        formDataUpdateThuTucCapMoi.append(
          "dinh_kem_don_de_nghi",
          FileDeNghiCapLai[i]
        );
      }
      for (let i = 0; i < FileAnhChanDung.length; i++) {
        formDataUpdateThuTucCapMoi.append(
          "dinh_kem_anh_chan_dung",
          FileAnhChanDung[i]
        );
      }
      for (let i = 0; i < FileVanBangChuyenMon.length; i++) {
        formDataUpdateThuTucCapMoi.append(
          "dinh_kem_van_bang_chuyen_mon",
          FileVanBangChuyenMon[i]
        );
      }
      for (let i = 0; i < FileXacNhanSucKhoe.length; i++) {
        formDataUpdateThuTucCapMoi.append(
          "dinh_kem_xac_nhan_suc_khoe",
          FileXacNhanSucKhoe[i]
        );
      }
      for (let i = 0; i < FileXacNhanThucHanh.length; i++) {
        formDataUpdateThuTucCapMoi.append(
          "dinh_kem_xac_nhan_thuc_hanh",
          FileXacNhanThucHanh[i]
        );
      }
      for (let i = 0; i < FileXacNhanDaoTao.length; i++) {
        formDataUpdateThuTucCapMoi.append(
          "dinh_kem_xac_nhan_dao_tao",
          FileXacNhanDaoTao[i]
        );
      }
      for (let i = 0; i < FileXacNhanCongDan.length; i++) {
        formDataUpdateThuTucCapMoi.append(
          "dinh_kem_xac_nhan_cong_dan",
          FileXacNhanCongDan[i]
        );
      }
      for (let i = 0; i < FileXacNhanLyLich.length; i++) {
        formDataUpdateThuTucCapMoi.append(
          "dinh_kem_xac_nhan_ly_lich",
          FileXacNhanLyLich[i]
        );
      }
      for (let i = 0; i < FileXacNhanLePhi.length; i++) {
        formDataUpdateThuTucCapMoi.append(
          "dinh_kem_xac_nhan_le_phi",
          FileXacNhanLePhi[i]
        );
      }
      for (let i = 0; i < FileKhac.length; i++) {
        formDataUpdateThuTucCapMoi.append(
          "dinh_kem_xac_nhan_files",
          FileKhac[i]
        );
      }
      formDataUpdateThuTucCapMoi.append("trang_thai_ho_so", valueButton);

      axios
        .put(
          api.API_YEU_CAU_CHUNG_CHI_HANH_NGHE + "/" + params?.id,
          thongTinHoSo?.thu_tuc?.loai === "1"
            ? formDataUpdateThuTucCapMoi
            : thongTinHoSo?.thu_tuc?.loai === "2"
              ? formDataUpdateThuTucDieuChinh
              : formDataUpdateThuTucCapLai,
          {
            headers: {
              "content-type": "multipart/form-data",
            },
          }
        )
        .then(async ({ data }) => {
          await toast.success(data?.msg, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "success",
          });
          if (valueButton !== "1") {
            navigate("/admin/nop-ho-so/danh-sach-ho-so")
          } else {
            setTimeout(() => {
              navigate(0)
            }, 1000);
          }
        })
        .catch((error) => {
          toast.error(error?.response?.data?.msg, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "error",
          });
        })
        .finally(() => {
            setIsLoading(false);
        });
    }
  };

  const UploadControl = ({
    children,
    value,
    onChange,
    disabled,
    accept,
    name,
  }) => {
    return (
      <label htmlFor={`contained-button-${name}`} className="m-0 w-100">
        <input
          disabled={disabled}
          style={{ display: "none" }}
          id={`contained-button-${name}`}
          multiple
          type="file"
          accept="audio/*|video/*|image/*|MIME_type"
          onChange={(event) => {
            switch (name) {
              case "denghicaplai":
                setFileDeNghiCapLai(Object.values(event.target.files));
                break;
              case "banchinhhoacbansau":
                setFileBanChinhHoacBanSau(Object.values(event.target.files));
                break;
              case "anhchandung":
                setFileAnhChanDung(Object.values(event.target.files));
                break;
              case "banchinhdaduoccap":
                setFileBanChinhDaDuocCap(Object.values(event.target.files));
                break;
              case "vanbangchuyenmon":
                setFileVanBangChuyenMon(Object.values(event.target.files));
                break;
              case "xacnhansuckhoe":
                setFileXacNhanSucKhoe(Object.values(event.target.files));
                break;
              case "xacnhanthuchanh":
                setFileXacNhanThucHanh(Object.values(event.target.files));
                break;
              case "xacnhandaotao":
                setFileXacNhanDaoTao(Object.values(event.target.files));
                break;
              case "xacnhancongdan":
                setFileXacNhanCongDan(Object.values(event.target.files));
                break;
              case "xacnhanlylich":
                setFileXacNhanLyLich(Object.values(event.target.files));
                break;
              case "xacnhanlephi":
                setFileXacNhanLePhi(Object.values(event.target.files));
                break;
              case "khac":
                setFileKhac(Object.values(event.target.files));
                break;
              default:
                break;
            }
          }}
        />
        {children}
      </label>
    );
  };

  const renderChooseFile = ({ title, name, arrayFileMap, setFile }) => {
    return (
      <div className="col-6">
        <div className="row">
          <div className="col-10">{title}</div>
          <div className="col-2">
            <button className="btn btn-primary btn-sm font-weight-bold">
              <UploadControl name={name}>CHỌN</UploadControl>
            </button>
          </div>
          <div className="col-12">
            {arrayFileMap?.length > 0
              ? arrayFileMap.map((e, i) => {
                return (
                  <button
                    key={i}
                    className="btn ml-2 mb-2"
                    style={{
                      backgroundColor: "#e8e8e8",
                      padding: "12px 10px 12px 16px",
                      marginRight: "12px",
                    }}
                  >
                    <span>{e?.name}</span>
                    <i
                      style={{
                        display: "inline",
                        color: "red",
                        marginLeft: "8px",
                      }}
                      onClick={() => {
                        let arrFile = arrayFileMap;
                        arrFile = arrFile.filter(
                          (item) => item.name !== e?.name
                        );
                        setFile(arrFile);
                      }}
                      className="fas fa-times-circle"
                    ></i>
                  </button>
                );
              })
              : null}
          </div>
        </div>
      </div>
    );
  };

  const renderButton = () => {
    switch (thongTinHoSo?.yeu_cau_cchnd?.trang_thai_ho_so) {
      case "11":
        return (
          <>
            <div className="col-12 mt-4 mb-5 text-center">
              <div
                onClick={() => {
                  navigate(-1);
                }}
                className="btn bg-primary2 text-white px-4 py-2 mx-2 font-weight-bolder btn-back"
              >
                QUAY LẠI
              </div>
              <button
                className="btn btn-primary text-white px-4 py-2 mx-2 font-weight-bolder btn-print-options"
                data-toggle="modal"
                data-target="#print-options"
              >
                IN HỒ SƠ
              </button>
            </div>
          </>
        );
      case "10":
        return (
          <>
            <div className="col-12 mt-4 mb-5 text-center">
              <div
                onClick={() => {
                  navigate(-1);
                }}
                className="btn bg-primary2 text-white px-4 py-2 mx-2 font-weight-bolder btn-back"
              >
                QUAY LẠI
              </div>
              <button
                className="btn btn-primary text-white px-4 py-2 mx-2 font-weight-bolder btn-print-options"
                data-toggle="modal"
                data-target="#print-options"
              >
                IN HỒ SƠ
              </button>
            </div>
          </>
        );
      case "9":
        return (
          <>
            <div className="col-12 mt-4 mb-5 text-center">
              <div
                onClick={() => {
                  navigate(-1);
                }}
                className="btn bg-primary2 text-white px-4 py-2 mx-2 font-weight-bolder btn-back"
              >
                QUAY LẠI
              </div>
              <button
                className="btn btn-primary text-white px-4 py-2 mx-2 font-weight-bolder btn-print-options"
                data-toggle="modal"
                data-target="#print-options"
              >
                IN HỒ SƠ
              </button>
            </div>
          </>
        );
      case "8":
        return (
          <>
            <div className="col-12 mt-4 mb-5 text-center">
              <div
                onClick={() => {
                  navigate(-1);
                }}
                className="btn bg-primary2 text-white px-4 py-2 mx-2 font-weight-bolder btn-back"
              >
                QUAY LẠI
              </div>
              <div
                onClick={() => {
                  handleSubmit("1");
                }}
                className="btn btn-success text-white px-4 py-2 mx-2 font-weight-bolder btn-save"
              >
                LƯU
              </div>
              <button
                className="btn btn-primary text-white px-4 py-2 mx-2 font-weight-bolder btn-print-options"
                data-toggle="modal"
                data-target="#print-options"
              >
                IN HỒ SƠ
              </button>
              <div
                onClick={() => {
                  handleSubmit("2");
                }}
                className="btn btn-primary text-white px-4 py-2 mx-2 font-weight-bolder btn-send-hoso"
              >
                GỬI HỒ SƠ
              </div>
              <div className="btn btn-danger text-white px-4 py-2 mx-2 font-weight-bolder btn-huy-hoso">
                HỦY
              </div>
            </div>
          </>
        );
      case "7":
        return (
          <>
            <div className="col-12 mt-4 mb-5 text-center">
              <div
                onClick={() => {
                  navigate(-1);
                }}
                className="btn bg-primary2 text-white px-4 py-2 mx-2 font-weight-bolder btn-back"
              >
                QUAY LẠI
              </div>
              <button
                className="btn btn-primary text-white px-4 py-2 mx-2 font-weight-bolder btn-print-options"
                data-toggle="modal"
                data-target="#print-options"
              >
                IN HỒ SƠ
              </button>
            </div>
          </>
        );
      case "6":
        return (
          <>
            <div className="col-12 mt-4 mb-5 text-center">
              <div
                onClick={() => {
                  navigate(-1);
                }}
                className="btn bg-primary2 text-white px-4 py-2 mx-2 font-weight-bolder btn-back"
              >
                QUAY LẠI
              </div>
              <button
                className="btn btn-primary text-white px-4 py-2 mx-2 font-weight-bolder btn-print-options"
                data-toggle="modal"
                data-target="#print-options"
              >
                IN HỒ SƠ
              </button>
            </div>
          </>
        );
      case "5":
        return (
          <>
            <div className="col-12 mt-4 mb-5 text-center">
              <div
                onClick={() => {
                  navigate(-1);
                }}
                className="btn bg-primary2 text-white px-4 py-2 mx-2 font-weight-bolder btn-back"
              >
                QUAY LẠI
              </div>
              <div
                onClick={() => {
                  handleSubmit("1");
                }}
                className="btn btn-success text-white px-4 py-2 mx-2 font-weight-bolder btn-save"
              >
                LƯU
              </div>
              <button
