          <div className="row col-12">
            <div className="col-4 mb-4">
              <span className="required fw-bolder text-primary">CMND/CCCD</span>
              <Form.Control
                type="text"
                className="mt-2"
                placeholder="Nhập số CMND/CCCD"
                value={form?.ma_cong_dan != null ? form?.ma_cong_dan : ""}
                style={{ fontSize: 14, fontWeight: 400, height: "38px" }}
                onChange={(e) => {
                  setField("ma_cong_dan", e.target.value);
                }}
                isInvalid={!!errors?.ma_cong_dan}
              />
              <Form.Control.Feedback type="invalid">
                {errors?.ma_cong_dan}
              </Form.Control.Feedback>
            </div>
            <div className="col-4 mb-4">
              <span className="required fw-bolder d-block mb-2 text-primary">
                Ngày cấp CMND/CCCD
              </span>
              <DesktopDatePicker
                label=" "
                inputFormat="dd/MM/yyyy"
                value={form?.ngay_cap || null}
                onChange={(e) => {
                  setField("ngay_cap", new Date(e).getTime());
                }}
                renderInput={(params) => <TextField {...params} />}
                isInvalid={!!errors?.ngay_cap}
              />
              <span className="text-danger">
                {errors?.ngay_cap ? <span>{errors?.ngay_cap}</span> : ""}
              </span>
            </div>
            <div className="col-4 mb-4">
              <span className="required fw-bolder d-block mb-2 text-primary">
                Nơi cấp CMND/CCCD
              </span>
              <Select
                key={"id"}
                className="mt-2"
                options={listNoiCapCMND}
                style={{ height: "38px" }}
                onChange={(e) => {
                  setSelectedNoiCapCMND(e);
                  setField("noi_cap", e?.label);
                }}
                value={selectedNoiCapCMND}
              />
              <span className="text-danger">
                {errors?.noi_cap ? <span>{errors?.noi_cap}</span> : ""}
              </span>
            </div>
            <div className="col-4 mb-4">
              <span className="fw-bolder d-block mb-2 text-primary">
                Số điện thoại
              </span>
              <Form.Control
                type="text"
                className="mt-2"
                placeholder="Nhập số điện thoại"
                style={{ fontSize: 14, fontWeight: 400, height: "38px" }}
                onChange={(e) => {
                  setField("dien_thoai", e.target.value);
                }}
                value={form?.dien_thoai != null ? form?.dien_thoai : ""}
                isInvalid={!!errors?.dien_thoai}
              />
              <Form.Control.Feedback type="invalid">
                {errors?.dien_thoai}
              </Form.Control.Feedback>
            </div>
            <div className="col-4 mb-4">
              <span className="fw-bolder d-block mb-2 text-primary">Email</span>
              <Form.Control
                type="text"
                className="mt-2"
                placeholder="Nhập email"
                value={form?.email != null ? form?.email : ""}
                style={{ fontSize: 14, fontWeight: 400, height: "38px" }}
                onChange={(e) => {
                  setField("email", e.target.value);
                }}
                isInvalid={!!errors?.email}
              />
            </div>
          </div>
        </div>
      </div>
    </LocalizationProvider>
  );
}
