import axios from "axios";
import { useEffect, useState } from "react";
import { Form } from "react-bootstrap";
import LoadingOverlay from "react-loading-overlay";
import { useSelector } from "react-redux";
import { useNavigate, useParams } from "react-router-dom";
import { toast } from "react-toastify";
import { PageTitle } from "../../../../_metronic/layout/core";
import api from "../../../configs/api";
import { selectRoleUser } from "../../../redux-module/auth/authSlice";
import { LichSuThucHanh } from "../duoc-si/lich-su-thuc-hanh/LichSuThucHanh";
import ThongTinCaNhan from "./components/thong-tin-ca-nhan/ThongTinCaNhan";

LoadingOverlay.propTypes = undefined;
export function ChiTietHoSoThuLyTuChoi(props) {
  const roleUser = useSelector(selectRoleUser);
  const navigate = useNavigate();
  const { id } = useParams();

  useEffect(() => {
    if (id) {
      getThongTinHoSo(id);
    }
  }, []);

  const [isLoading, setIsLoading] = useState(false);
  const [thongTinHoSo, setThongTinHoSo] = useState({});
  const [FileDeNghiCapLai, setFileDeNghiCapLai] = useState([]);
  const [FileAnhChanDung, setFileAnhChanDung] = useState([]);
  const [FileKhac, setFileKhac] = useState([]);
  const [FileVanBangChuyenMon, setFileVanBangChuyenMon] = useState([]);
  const [FileXacNhanSucKhoe, setFileXacNhanSucKhoe] = useState([]);
  const [FileXacNhanThucHanh, setFileXacNhanThucHanh] = useState([]);
  const [FileXacNhanDaoTao, setFileXacNhanDaoTao] = useState([]);
  const [FileXacNhanCongDan, setFileXacNhanCongDan] = useState([]);
  const [FileXacNhanLyLich, setFileXacNhanLyLich] = useState([]);
  const [FileXacNhanLePhi, setFileXacNhanLePhi] = useState([]);
  const [DeNghi, setDeNghi] = useState("");
  const [TypeGiaoDien, setTypeGiaoDien] = useState("");

  const getThongTinHoSo = async (id) => {
    await setIsLoading(true);
    axios
      .get(api.API_DETAIL_HO_SO_THU_LY_DUOC_SI + "/" + id)
      .then(async ({ data }) => {
        const { result } = data;
        if (result) {
          setDeNghi(result?.de_nghi);
          setThongTinHoSo(result);
          setField(
            "ly_do_tu_choi",
            roleUser[0]?.ten_en === "lanhdao"
              ? result?.yeu_cau_cchnd?.ly_do_tu_choi_lanh_dao
              : roleUser[0]?.ten_en === "chuyenvien"
              ? result?.yeu_cau_cchnd?.ly_do_tu_choi_chuyen_vien
              : ""
          );
        }
      })
      .catch((error) => {
        toast.error("Lấy thông tin chứng chỉ không thành công", {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
        navigate("/error/404");
      })
      .finally(() => {
        setTimeout(() => {
          setIsLoading(false);
        }, 1000);
      });
  };

  //validate thêm vị trí hành nghề
  const [form, setForm] = useState({});
  const [errors, setErrors] = useState({});

  const setField = (field, value) => {
    setForm({
      ...form,
      [field]: value,
    });
    if (!!errors[field])
      setErrors({
        ...errors,
        [field]: null,
      });
  };

  const findFormErrors = () => {
    const { ly_do_tu_choi } = form;
    const newErrors = {};
    // name errors
    if (!ly_do_tu_choi || ly_do_tu_choi === "")
      newErrors.ly_do_tu_choi = "Lý do từ chối không được bỏ trống!";
    return newErrors;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newErrors = findFormErrors();
    // Conditional logic:
    if (Object.keys(newErrors).length > 0) {
      console.log(newErrors);
      // We got errors!
      setErrors(newErrors);
      for (const property in newErrors) {
        toast.error(newErrors[property], {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
        return;
      }
    } else {
      // No errors! Put any logic here for the form submission!
      updateHoSo();
    }
  };

  const updateHoSo = async () => {
    await setIsLoading(true);
    const { ly_do_tu_choi } = form;
    const jsonCancel = {
      trang_thai_ho_so: "3",
      ly_do_tu_choi,
    };
    axios
      .put(
        api.API_YEU_CAU_CHUNG_CHI_HANH_NGHE + `/update_inform/${id}`,
        jsonCancel
      )
      .then(async ({ data }) => {
        toast.success("Cập nhật thành công", {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "success",
        });
        navigate(-1);
      })
      .catch((error) => {
        toast.error(error?.data?.errors, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  const usersBreadcrumbs = [
    {
      title: "Trang chủ",
      path: "/",
      isSeparator: false,
      isActive: false,
    },
    {
      title: "",
      path: "",
      isSeparator: true,
      isActive: false,
    },
  ];

  return (
    <div>
      <PageTitle breadcrumbs={usersBreadcrumbs}>Chi tiết hồ sơ</PageTitle>
      <LoadingOverlay spinner active={isLoading} text="Đang tải dữ liệu ...">
        {/* Thủ tục */}
        <div className="card py-6 px-6 mx-3 my-3">
          <label className="fw-bold fs-5">THỦ TỤC</label>
          <div className="text py-2">
            {thongTinHoSo?.thu_tuc ? thongTinHoSo?.thu_tuc?.ten : ""}
          </div>
        </div>

        {/* THÔNG TIN CÁ NHÂN */}
        <ThongTinCaNhan thongTinHoSo={thongTinHoSo} />
        {/* THỰC HÀNH TẠI CƠ SỞ DƯỢC */}
        <LichSuThucHanh userId={thongTinHoSo?.user?.id} />

        <div className="card-box py-6 px-6 mx-3 my-3">
          <div className="fw-bold fs-5 mb-2">LÝ DO TỪ CHỐI</div>

          <div className="col-12 d-flex px-0">
            <Form.Control
              as="textarea"
              rows={3}
              style={{ fontSize: 14, fontWeight: 500 }}
              value={form.ly_do_tu_choi === null ? "" : form.ly_do_tu_choi}
              onChange={(e) => {
                setField("ly_do_tu_choi", e.target.value);
              }}
              isInvalid={!!errors.ly_do_tu_choi}
            />
            {/* <Form.Control.Feedback type="invalid">
              {errors.ly_do_tu_choi}
            </Form.Control.Feedback> */}
          </div>
        </div>

        <div className="row w-100">
          <div className="col-12 mt-4 mb-5 text-center">
            <div
              onClick={() => {
                navigate(-1);
              }}
              className="btn bg-primary2 text-white px-4 py-2 mx-2 font-weight-bold"
            >
              QUAY LẠI
            </div>

            <div
              onClick={(e) => {
                handleSubmit(e);
              }}
              className="btn btn-success text-white px-4 py-2 mx-2 font-weight-bold "
            >
              LƯU
            </div>
            <div
              onClick={() => {}}
              className="btn btn-primary text-white px-4 py-2 mx-2 font-weight-bold "
            >
              IN VĂN BẢN TỪ CHỐI
            </div>
            <div
              onClick={() => {}}
              className="btn btn-primary text-white px-4 py-2 mx-2 font-weight-bold "
            >
              IN PHIẾU THẨM XÉT
            </div>
          </div>
        </div>
      </LoadingOverlay>
    </div>
  );
}
