import React from "react";
import { useSelector } from "react-redux";
import { selectRoleUser } from "../../../../redux-module/auth/authSlice";

export default function LyDoHeader(props) {
  const roleUser = useSelector(selectRoleUser)[0].ten_en;
  switch (props?.thongTinHoSo?.trang_thai_ho_so) {
    case "3":
    case "7":
      return (
        <div class="card py-3 px-3 mx-3 my-3 alert-warning">
          <div className="col">
            <strong>Lý do từ chối: </strong>
            <span>
              {roleUser === "chuyenvien"
                ? props?.thongTinHoSo?.yeu_cau_cchnd?.ly_do_tu_choi_chuyen_vien
                : roleUser === "lanhdao" &&
                  props?.thongTinHoSo?.yeu_cau_cchnd?.ly_do_tu_choi_lanh_dao}
            </span>
          </div>
        </div>
      );
    case "5":
    case "8":
      return (
        <div className="card py-3 px-3 mx-3 my-3 alert-warning">
          <div className="col">
            <strong>Yêu cầu bổ sung: </strong>
            <span>
              {roleUser === "chuyenvien"
                ? props?.thongTinHoSo?.yeu_cau_cchnd
                    ?.noi_dung_yeu_cau_bo_sung_chuyen_vien
                : roleUser === "lanhdao" &&
                  props?.thongTinHoSo?.yeu_cau_cchnd
                    ?.noi_dung_yeu_cau_bo_sung_lanh_dao}
            </span>
          </div>
        </div>
      );

    default:
      return null;
  }
}
