import { AdapterDateFns } from "@mui/x-date-pickers/AdapterDateFns";
import { LocalizationProvider, DesktopDatePicker } from "@mui/x-date-pickers";
import { TextField } from "@mui/material";
import axios from "axios";
// import { vi } from "date-fns/locale";
import { enAU } from "date-fns/locale";
import { useEffect, useState } from "react";
import { Button, Form, Modal } from "react-bootstrap";
import Select from "react-select";
import { toast } from "react-toastify";
import api from "../../../configs/api";
// import { GioiTinh, HinhThuc } from "../../../data";
// import moment from "moment";

export default function PopupQuanLyNguoiDung(props) {
  const [edit, setEdit] = useState(false);
  const [showUpdate, setShowUpdate] = useState(false);
  const [form, setForm] = useState({});
  const [trang_thai, setTrang_thai] = useState(true);
  const [isLoading, setIsLoading] = useState(false);
  const [selectedgioitinh, setSelectedgioitinh] = useState({
    label: "Nam",
    value: 1,
    id: 1,
  });

  useEffect(() => {
    if (props?.show) {
      //   resetState();
      setForm({
        ...props?.data,
      });
      console.log(props?.data);
      //   setForm({ ...props?.data, ngay_cap: props?.data.ngay_cap * 1 });
    }
    console.log("propsssss", props);
  }, [props?.show]);

  // selected ngay
  const selectedNS = async (date) => {
    // await setNgaySinh(date);
    await setField("ngay_sinh", new Date(date).getTime);
  };
  const selectedNCCMND = async (date) => {
    // await setNgayCapCmnd(date);
    await setField("ngay_cap", new Date(date).getTime);
  };



  const [selectedDoiTuong, setSelectedDoiTuong] = useState({
    label: "Chuyên viên thụ lý",
    value: 1,
    id: 1,
  });
  const [errors, setErrors] = useState({});
  const setField = (field, value) => {
    setForm({
      ...form,
      [field]: value,
    });
    if (!!errors[field])
      setErrors({
        ...errors,
        [field]: null,
      });
  };
  const optionsDoiTuong = [
    {
      label: "Chuyên viên thụ lý",
      value: 1,
      id: 1,
    },
    {
      label: "Chuyên viên hội đồng",
      value: 2,
      id: 2,
    },
    {
      label: "Phó trưởng phòng",
      value: 3,
      id: 3,
    },
    {
      label: "Trưởng phòng",
      value: 4,
      id: 4,
    },
    {
      label: "Giám đốc",
      value: 5,
      id: 5,
    },
    {
      label: "Phó giám đốc",
      value: 6,
      id: 6,
    },
    {
      label: "Văn thư",
      value: 7,
      id: 7,
    },
  ];
  const formatGroupLabel = (listTemp) => (
    <div style={groupStyles}>
      <span>{listTemp.ten}</span>
    </div>
  );
  const findFormErrors = () => {
    const { old_password, new_password, retype_password } = form;
    const newErrors = {};
    // name errors
    if (!old_password || old_password === "")
      newErrors.old_password = "Vui lòng nhập mật khẩu hiện tại!";
    if (!new_password || new_password === "")
      newErrors.new_password = "Vui lòng nhập mật khẩu mới!";
    if (!retype_password || retype_password === "")
      newErrors.retype_password = "Vui lòng nhập lại xác nhận mật khẩu";

    return newErrors;
  };
  const handleSubmit = async (e) => {
    e.preventDefault();
    const newErrors = findFormErrors();
    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
    } else {
      setIsLoading(true)
      const { old_password, new_password, retype_password } = form;
      const json = {
        old_password: old_password,
        new_password: new_password,
        retype_password: retype_password,
      };
      await axios
        .put(api.API_QL_TAI_KHOAN_NGUOI_DUNG_ID, json)
        .then(async ({ data }) => {
          toast.success("Đổi mật khẩu thành công", {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "success",
          });
        })
        .catch((error) => {
          toast.error(error?.response?.data?.msg, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "error",
          });
        })
        .finally(() => {
          setIsLoading(false)
        });
    }
  };



  // GET LOCATIONs

  // cccd/cmnd
  const [datareset, setDatareset] = useState(props?.data);
  useEffect(() => {
    setDatareset(props?.data);
  }, [props?.data]);
  const resetState = () => {
    setForm({});
    setErrors({});
    setShowUpdate(false);
    const updatedData = props.data.map((item) => {
      return {
        ...item,
        toggleSelected: false,
      };
    });
    setDatareset(updatedData);
  };



  const groupStyles = {
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
  };
  const cardBox = {
    backgroundColor: "#fff",
    backgroundClip: "border-box",
    border: "1px solid #e7eaed",
    padding: "1.5rem",
    marginBottom: "24px",
    borderRadius: "0.5rem",
  };
  return (
    <Modal
      backdrop="static"
      keyboard={false}
      size="xl"
      show={props?.show}
      onHide={() => {
        props?.hide();
      }}
    >
      <LocalizationProvider dateAdapter={AdapterDateFns} locale={enAU}>
        <Modal.Header className="bg-blue2" closeButton>
          <Modal.Title className="text-white">
            {!edit ? "Thêm" : "Cập nhật"} người dùng
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div>
            <div className="row" style={cardBox}>
              <div className=" col-12 form-group">
                <div className=" form-group mt-2">
                  <label className="required fw-bold fs-6 mb-2">
                    Họ và tên
                  </label>
                  <Form.Control
                    type="text"
                    placeholder="Nhập họ và tên đệm"
                    style={{ height: 38 }}
                    value={form.ho === null ? "" : form.ho}
                    onChange={(e) => {
                      setField("ho", e.target.value);
                    }}
                    isInvalid={!!errors.ho}
                  />
                </div>
                <div className=" form-group mt-2">
                  <label className="required fw-bold fs-6 mb-2">
                    Tên tài khoản
                  </label>
                  <Form.Control
                    type="text"
                    placeholder="Tên tài khoản"
                    style={{ height: 38 }}
                    value={form.ten_tai_khoan === null ? "" : form.ten_tai_khoan}
                    disabled
                  />
                </div>
                <div className="form-group mt-2 mb-5">
                  <label className="required fw-bold fs-6 mb-2">Vai trò</label>
                  <Select
                    value={selectedDoiTuong}
                    key={"id"}
                    options={optionsDoiTuong}
                    formatGroupLabel={formatGroupLabel}
                    onChange={async (data) => {
                      setField("doi_tuong", data?.value);
                      setSelectedDoiTuong(data);
                    }}
                  />
                </div>
                <div className="form-group mt-2 mb-5">
                  <label className="label-input fw-bold">Mật khẩu</label>
                  <div className="input-group input-icon">
                    <Form.Control
                      type="password"
                      autoComplete="none"
                      placeholder="Nhập mật khẩu mới"
                      className="input-register"
                      style={{ fontSize: 14, fontWeight: 400 }}
                      onChange={(e) => {
