import axios from "axios";
import { useEffect, useState } from "react";
import BlockUi from "react-block-ui";
import { Form } from "react-bootstrap";
import DataTable from "react-data-table-component";
import { Oval } from "react-loader-spinner";
// import { useDispatch } from "react-redux";
import clsx from "clsx";
import { toast } from "react-toastify";
import ReactTooltip from "react-tooltip";
import { KTCard, KTSVG, useDebounce } from "../../../../../_metronic/helpers";
import Status from "../../../../components/Status";
import api from "../../../../configs/api";
import { PopupDelete } from "../../popup/PopupDelete";

export function PhamViHoatDongKinhDoanh() {
  const [listPhamViHoatDongKinhDoanh, setListPhamViHoatDongKinhDoanh] =
    useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [totalRows, setTotalRows] = useState(0);
  const [perPage, setPerPage] = useState(10);
  const [page, setPage] = useState(1);
  const [showUpdate, setShowUpdate] = useState(false);
  const [stt, setStt] = useState(0);
  const [trang_thai, setTrang_thai] = useState(true);

  //open modal delete
  const [isvisibleDelete, setIsvisibleDelete] = useState(false);
  const [idDelete, setIdDelete] = useState("");
  const closeModalDelete = () => setIsvisibleDelete(false);
  const showModalDelete = () => setIsvisibleDelete(true);
  const [searchTerm, setSearchTerm] = useState("");

  const debouncedSearchTerm = useDebounce(searchTerm, 1000);

  useEffect(
    () => {
      if (debouncedSearchTerm !== undefined && searchTerm !== undefined) {
        getList({ page_number: 1, search_key: debouncedSearchTerm });
      }
    },
    [debouncedSearchTerm] // Only call effect if debounced search term changes
  );

  const getList = ({
    page_number = page,
    size = perPage,
    search_key = searchTerm,
  }) => {
    setIsLoading(true);
    axios
      .post(
        api.API_LIST_PHAM_VI_HOAT_DONG_KINH_DOANH +
          `?page=${page_number}&per_page=${size}`,
        { search_ten: search_key }
      )
      .then(async ({ data }) => {
        setListPhamViHoatDongKinhDoanh(data?.results);
        setTotalRows(data?.total);
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  //validate thêm phạm vi hoạt động kinh doanh
  const [form, setForm] = useState({});
  const [errors, setErrors] = useState({});
  const setField = (field, value) => {
    setForm({
      ...form,
      [field]: value,
    });
    if (!!errors[field])
      setErrors({
        ...errors,
        [field]: null,
      });
  };
  const findFormErrors = () => {
    const { ten, chi_tiet, loai, stt, ten_in } = form;
    const newErrors = {};
    // name errors
    if (!ten || ten === "") newErrors.ten = "Tên không được bỏ trống!";
    if (!chi_tiet || chi_tiet === "")
      newErrors.chi_tiet = "Chi tiết được bỏ trống!";
    if (!loai || loai === "") newErrors.loai = "Loại không được bỏ trống!";
    if (!stt || stt === "") newErrors.stt = "Số thứ tự không được bỏ trống!";
    if (!ten_in || ten_in === "")
      newErrors.ten_in = "Tên in không được bỏ trống!";
    return newErrors;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // get our new errors
    const newErrors = findFormErrors();
    // Conditional logic:
    if (Object.keys(newErrors).length > 0) {
      console.log(newErrors);
      // We got errors!
      setErrors(newErrors);
    } else {
      // No errors! Put any logic here for the form submission!
      showUpdate
        ? updatePhamViHoatDongKinhDoanh()
        : addPhamViHoatDongKinhDoanh();
    }
  };

  // handle api
  const addPhamViHoatDongKinhDoanh = async () => {
    setIsLoading(true);
    const { ten, chi_tiet, loai, stt, ten_in } = form;
    const json = {
      ten,
      chi_tiet,
      loai,
      stt,
      ten_in,
      trang_thai: trang_thai,
    };
    await axios
      .post(api.API_PHAM_VI_HOAT_DONG_KINH_DOANH + "_add", json)
      .then(async ({ data }) => {
        resetState();
        toast.success("Tạo phạm vi hoạt động kinh doanh thành công", {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "success",
        });
        getList({});
        setIsLoading(false);
      })
      .catch((error) => {
        console.log("error", error?.response);
        toast.error(error?.data?.errors, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  const updatePhamViHoatDongKinhDoanh = async () => {
    setIsLoading(true);
    const { ten, chi_tiet, loai, stt, ten_in, id } = form;
    const json = {
      ten,
      chi_tiet,
      loai,
      stt,
      ten_in,
      trang_thai: trang_thai,
    };
    await axios
      .put(api.API_PHAM_VI_HOAT_DONG_KINH_DOANH + "/" + id, json)
      .then(async ({ data }) => {
        resetState();
        toast.success("Cập nhật phạm vi hoạt động kinh doanh thành công", {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "success",
        });
        getList({});
      })
      .catch((error) => {
        toast.error(error?.data?.errors, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  const resetState = () => {
    setForm({
      ten: "",
      chi_tiet: "",
      ten_in: "",
      loai: "",
      stt: "",
    });
    setErrors({});
    setShowUpdate(false);
    const updatedData = listPhamViHoatDongKinhDoanh?.map((item) => {
      return {
        ...item,
        toggleSelected: false,
      };
    });
    setData(updatedData);
  };

  // DETAIL
  const detail = async (data) => {
    setForm({
      ...data,
    });
    setTrang_thai(data?.trang_thai);
    setShowUpdate(true);
  };

  const handlePerRowsChange = async (newPerPage, page) => {
    await setIsLoading(true);
    axios
      .post(
        api.API_LIST_PHAM_VI_HOAT_DONG_KINH_DOANH +
          `?page=${page}&per_page=${newPerPage}`,
        {}
      )
      .then(({ data }) => {
        if (data?.results) {
          setListPhamViHoatDongKinhDoanh(data?.results);
          setPerPage(newPerPage);
        }
      })
      .catch(() => {})
      .finally(() => {
        setIsLoading(false);
      });
  };

  const deleteVBCM = () => {
    axios
      .delete(api.API_PHAM_VI_HOAT_DONG_KINH_DOANH + "/" + idDelete)
      .then(async ({ data }) => {
        await toast.success(data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "success",
        });
        closeModalDelete();
        resetState();
        if (page == 1) {
          getList({ page_number: 1 });
        } else if (listPhamViHoatDongKinhDoanh?.length == 1) {
          getList({ page_number: page - 1 });
        } else {
          getList({ page_number: page });
        }
      })
      .catch((error) => {
        console.log("error", error);
        toast.error(error?.data?.errors, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };
  const handlePageChange = (page) => {
    setPage(page);
    getList({ page_number: page });
  };

  //đổi màu khi selected
  const [data, setData] = useState(listPhamViHoatDongKinhDoanh);
  useEffect(() => {
    setData(listPhamViHoatDongKinhDoanh);
  }, [listPhamViHoatDongKinhDoanh]);

  const handleRowClicked = (row) => {
    setErrors({});
    const updatedData = listPhamViHoatDongKinhDoanh?.map((item) => {
      if (row.id !== item.id) {
        return item;
      }
      return {
        ...item,
        toggleSelected: !item.toggleSelected,
      };
    });
    setData(updatedData);
  };

  const paginationOptions = {
    rowsPerPageText: "Dòng hiển thị",
    rangeSeparatorText: "trên",
  };
  const customStyles = {
    headRow: {
      style: {
        borderTopStyle: "solid",
        borderTopWidth: "1px",
        borderTopColor: "#e0e0e0",
        borderBottomColor: "#e0e0e0",
      },
    },
    headCells: {
      style: {
        color: "#202124",
        fontSize: "16px",
      },
    },
    rows: {
      highlightOnHoverStyle: {
        backgroundColor: "rgb(230, 244, 244)",
        borderBottomColor: "#FFFFFF",
        outline: "1px solid #FFFFFF",
      },
    },
    pagination: {
      style: {
        border: "none",
      },
    },
  };
  const columns = [
    {
      name: "Mã",
      grow: 3,
      selector: (row) => (
        <div
          onClick={() => {
            detail(row);
            handleRowClicked(row);
          }}
        >
          <span>{row.ma_hoat_dong_kinh_doanh}</span>
        </div>
      ),
      style: {
        color: "#1251ff",
        fontSize: "14px",
        fontWeight: 500,
        width: 300,
        cursor: "pointer",
      },
    },
    {
      name: "Tên",
      selector: (row) => (
        <span data-tip={row?.ten}>
          {row.ten}
          <ReactTooltip place="top" type="dark" effect="float" />
        </span>
      ),
      grow: 8,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
    {
      name: "Trang thái",
      selector: (row) => <span>{row.trang_thai}</span>,
      grow: 3,
      cell: (row) => (
        <td>
          <Status status={row.trang_thai} />
        </td>
      ),
    },
    {
      name: "Thao tác",
      grow: 2,
      cell: (row) => (
        <div className="container my-3">
          <button
            onClick={async () => {
              await setIdDelete(row?.id);
              showModalDelete();
            }}
            className="btn btn-icon btn-bg-light btn-color-danger btn-sm"
          >
            <KTSVG
              path="/media/icons/duotune/general/gen027.svg"
              className="svg-icon-3"
            />
          </button>
        </div>
      ),
    },
  ];
  const conditionalRowStyles = [
    {
      when: (row) => row.toggleSelected,
      style: {
        backgroundColor: "#f5f5f5",
        userSelect: "none",
      },
    },
  ];
  const cardBox = {
    backgroundColor: "#fff",
    backgroundClip: "border-box",
    border: "1px solid #e7eaed",
    padding: "1.5rem",
    marginBottom: "24px",
    borderRadius: "0.5rem",
  };
  return (
    <KTCard>
      <div className="page">
        <div className="container-fluid">
          <div className="fv-row mb-7 mt-6">
            {/* begin::Label */}
            <label className="required fw-bold fs-6 mb-2">
              Tên phạm vi hoạt động kinh doanh
            </label>
            {/* end::Label */}

            {/* begin::Input */}
            <div className="col-md-6">
              <input
                placeholder="Nhập tên tìm kiếm..."
                type="text"
                name="name"
                className={clsx("form-control form-control-solid mb-3 mb-lg-0")}
                autoComplete="off"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>

            {/* end::Input */}
          </div>
          <BlockUi tag="div" blocking={isLoading} centerY={true} centerX={true}>
            <div className="row">
              <div className="col-md-8">
                <div className="" style={cardBox}>
                  <DataTable
                    noDataComponent={"Không có dữ liệu."}
                    sortServer
                    progressPending={isLoading}
                    columns={columns}
                    data={data}
                    customStyles={customStyles}
                    pagination
                    highlightOnHover
                    pointerOnHover
                    paginationServer
                    paginationTotalRows={totalRows}
                    onChangeRowsPerPage={handlePerRowsChange}
                    onChangePage={handlePageChange}
                    paginationComponentOptions={paginationOptions}
                    conditionalRowStyles={conditionalRowStyles}
                    // onRowClicked={(data) => {
                    //   detail(data);
                    //   handleRowClicked(data);
                    // }}
                    progressComponent={
                      <div
                        style={{
                          padding: "24px",
                        }}
                      >
                        <Oval
                          arialLabel="loading-indicator"
                          color="#007bff"
                          height={40}
                        />
                      </div>
                    }
                  />
                </div>
              </div>
              <div className="col-md-4">
                <div className="" style={cardBox}>
                  <div className="">
                    <p className="font-weight-bold">
                      {showUpdate
                        ? "Cập nhật phạm vi hoạt động kinh doanh"
                        : "Tạo mới phạm vi hoạt động kinh doanh"}
                    </p>
                  </div>
                  {showUpdate && (
                    <div className="row mt-3">
                      <div className="col-4">
                        <p className="font-weight-bold">Mã</p>
                      </div>
                      <div className="col-8">
                        <Form.Control
                          disabled={true}
                          type="text"
                          placeholder="Nhập tên mã hoạt động kinh doanh"
                          style={{ fontSize: 14, fontWeight: 500 }}
                          value={
                            form.ma_hoat_dong_kinh_doanh === null
                              ? ""
                              : form.ma_hoat_dong_kinh_doanh
                          }
                        />
                      </div>
                    </div>
                  )}

                  <div className="row mt-3">
                    <div className="col-4">
                      <p className="font-weight-bold">
                        Tên<span className="text-danger">*</span>
                      </p>
                    </div>
                    <div className="col-8">
                      <Form.Control
                        as="textarea"
                        rows={3}
                        placeholder="Nhập tên mã hoạt động kinh doanh"
                        style={{ fontSize: 14, fontWeight: 500 }}
                        value={form.ten === null ? "" : form.ten}
                        onChange={(e) => {
                          setField("ten", e.target.value);
                        }}
                        isInvalid={!!errors.ten}
                      />
                      <Form.Control.Feedback type="invalid">
                        {errors.ten}
                      </Form.Control.Feedback>
                    </div>
                  </div>
                  <div className="row mt-3">
                    <div className="col-4">
                      <p className="font-weight-bold">
                        Chi tiết<span className="text-danger">*</span>
                      </p>
                    </div>
                    <div className="col-8">
                      <Form.Control
                        as="textarea"
                        rows={3}
                        placeholder="Nhập chi tiết phạm vi hoạt động kinh doanh"
                        style={{ fontSize: 14, fontWeight: 500 }}
                        value={form.chi_tiet === null ? "" : form.chi_tiet}
                        onChange={async (e) => {
                          await setField("chi_tiet", e.target.value);
                        }}
                        isInvalid={!!errors.chi_tiet}
                      />
                      <Form.Control.Feedback type="invalid">
                        {errors.chi_tiet}
                      </Form.Control.Feedback>
                    </div>
                  </div>
                  <div className="row mt-3">
                    <div className="col-4">
                      <p className="font-weight-bold">
                        Loại<span className="text-danger">*</span>
                      </p>
                    </div>
                    <div className="col-8">
                      <Form.Control
                        type="text"
                        placeholder="Nhập tên mã hoạt động kinh doanh"
                        style={{ fontSize: 14, fontWeight: 500 }}
                        value={form.loai === null ? "" : form.loai}
                        onChange={async (e) => {
                          await setField("loai", e.target.value);
                        }}
                        isInvalid={!!errors.loai}
                      />
                      <Form.Control.Feedback type="invalid">
                        {errors.loai}
                      </Form.Control.Feedback>
                    </div>
                  </div>
                  <div className="row mt-3">
                    <div className="col-4">
                      <p className="font-weight-bold">
                        STT <span className="text-danger">*</span>
                      </p>
                    </div>
                    <div className="col-8">
                      <Form.Control
                        type="text"
                        placeholder="Nhập số thứ tự"
                        style={{ fontSize: 14, fontWeight: 500 }}
                        value={form.stt === null ? "" : form.stt}
                        onChange={async (e) => {
                          await setField("stt", e.target.value);
                        }}
                        isInvalid={!!errors.stt}
                      />
                      <Form.Control.Feedback type="invalid">
                        {errors.stt}
                      </Form.Control.Feedback>
                    </div>
                  </div>
                  <div className="row mt-3">
                    <div className="col-4">
                      <p className="font-weight-bold">
                        Tên in <span className="text-danger">*</span>
                      </p>
                    </div>
                    <div className="col-8">
                      <Form.Control
                        as="textarea"
                        rows={3}
                        placeholder="Nhập tên in"
                        style={{ fontSize: 14, fontWeight: 500 }}
                        value={form.ten_in === null ? "" : form.ten_in}
                        onChange={async (e) => {
                          await setField("ten_in", e.target.value);
                        }}
                        isInvalid={!!errors.ten_in}
                      />
                      <Form.Control.Feedback type="invalid">
                        {errors.ten_in}
                      </Form.Control.Feedback>
                    </div>
                  </div>
                  <div className="row mt-3">
                    <div className="col-4">
                      <label className="fw-bold fs-6 mb-2">Trạng thái</label>
                    </div>
                    <div className="col-8">
                      <div className="form-check form-check-solid form-switch fv-row">
                        <input
                          className="form-check-input w-45px h-30px"
                          type="checkbox"
                          id="trang_thai"
                          checked={trang_thai}
                          onChange={(e) => {
                            setTrang_thai(!trang_thai);
                          }}
                        />
                        <label className="form-check-label"></label>
                      </div>
                    </div>
                  </div>
                  <div className="row mt-3">
                    <div className="col-4">
                      {showUpdate ? (
                        <button
                          className="btn text-white bg-blue2"
                          style={{ fontSize: ".9rem" }}
                          onClick={() => resetState()}
                        >
                          <i className="fas fa-sync-alt text-white"></i> Làm mới
                        </button>
                      ) : null}
                    </div>

                    <div className="col-8">
                      <button
                        className="btn text-white bg-blue2 mr-3"
                        style={{ fontSize: ".9rem" }}
                        onClick={(e) => {
                          handleSubmit(e);
                        }}
                      >
                        {showUpdate ? (
                          <i className="fa fa-pencil-alt text-white"></i>
                        ) : (
                          <i className="fas fa-save text-white"></i>
                        )}{" "}
                        {showUpdate
                          ? "Cập nhật"
                          : "Tạo phạm vi hoạt động kinh doanh"}
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            </div>

            <PopupDelete
              title="Bạn có đồng ý xóa phạm vi hoạt động kinh doanh này không?"
              show={isvisibleDelete}
              onHide={closeModalDelete}
              onDetele={() => {
                deleteVBCM();
              }}
            />
          </BlockUi>
        </div>
      </div>
    </KTCard>
  );
