import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    loading: false,
    listTinhThanh: [],
    listQuanHuyen: [],
    listXaPhuong: [],
    isThuongTru: false
};

export const locationSlice = createSlice({
    name: "location",
     initialState,
     reducers: {
        getTinhThanh(state, action) {
            state.loading = true;
        },
        getTinhThanhSuccess(state, action) {
            state.loading = false;
            state.listTinhThanh = action.payload;
        },
        getTinhThanhFailed(state) {
            state.loading = false;
        },
        getQuanHuyenHN(state, action) {
             state.loading = true;
        },
        getQuanHuyenHNSuccess(state, action) {
            state.loading = false;
            state.listQuanHuyen = action.payload;
        },
        getQuanHuyenHNFailed(state) {
            state.loading = false;
        },
        getXaPhuongHN(state, action) {
            state.loading = true;
        },
        getXaPhuongHNSuccess(state, action) {
            state.loading = false;
            state.listXaPhuong = action.payload;
        },
        getXaPhuongHNFailed(state) {
            state.loading = false;
        },

        getQuanHuyenTT(state, action) {
            state.loading = true;
        },
        getQuanHuyenTTSuccess(state, action) {
            state.loading = false;
            state.listQuanHuyen = action.payload;
        },
        getQuanHuyenTTFailed(state) {
            state.loading = false;
        },
        getXaPhuongTT(state, action) {
            state.loading = true;
        },
        getXaPhuongTTSuccess(state, action) {
            state.loading = false;
            state.listXaPhuong = action.payload;
        },
        getXaPhuongTTFailed(state) {
            state.loading = false;
        }
     }
})

export const locationActions = locationSlice.actions

const locationReducer = locationSlice.reducer;
export default locationReducer;

export const selectLoading = (state) => state.location.loading;
export const selectListTinhThanh = (state) => state.location.listTinhThanh;
export const selectListQuanHuyen = (state) => state.location.listQuanHuyen;
export const selectListXaPhuong = (state) => state.location.listXaPhuong;
