import axios from "axios";
import { Form } from "react-bootstrap";
import { toast } from "react-toastify";
import api from "../../../../configs/api";
import { useDispatch } from "react-redux";
import { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import {
  commonActions,
  selectListIdCheckedPVCM,
  selectListIdCheckedVTHN,
} from "../../../../redux-module/common/commonSlice";
import { Checkbox, FormControlLabel } from "@mui/material";
import { selectRoleUser } from "../../../../../app/redux-module/auth/authSlice";

export function PhamViVaViTriChuyenMon(props) {
  const dispatch = useDispatch();
  const roleUser = useSelector(selectRoleUser)[0];
  const ListIdCheckedPVCM = useSelector(selectListIdCheckedPVCM);
  const listIdCheckedVTHN = useSelector(selectListIdCheckedVTHN);
  const [ListPVCM, setListPVCM] = useState([]);
  const [ListVTHN, setListVTHN] = useState([]);
  const [listIdPVCM, setListIdPVCM] = useState([]);
  const [listIdVTHN, setListIdVTHN] = useState([]);

  useEffect(() => {
    setListPVCM(props?.dataDMPVCM);
    setListVTHN(props?.dataVTHN);
    dispatch(commonActions.setListIdCheckedPVCM(props?.listCheckedDMPVCM));
    dispatch(commonActions.setListIdCheckedVTHN(props?.listCheckedVTHN));
  }, [props?.dataDMPVCM, props?.dataVTHN]);

  //   useEffect(() => {
  //     dispatch(commonActions.setListIdCheckedPVCM(props?.listCheckedDMPVCM));
  //     console.log("props?.listCheckedDMPVCM", props?.listCheckedDMPVCM);
  //   }, [props?.listCheckedDMPVCM]);

  //   useEffect(() => {
  //     dispatch(commonActions.setListIdCheckedVTHN(props?.listCheckedVTHN));
  //     console.log("props?.listCheckedVTHN", props?.listCheckedVTHN);
  //   }, [props?.listCheckedVTHN]);

  const renderCheckbox = (e) => {
    return (
      <div className="row col-12" key={e?.id}>
        <span key={e?.id} className="font-weight-bold mb-1">
          {e?.ten}
        </span>
        {e.value && e.value.length > 0
          ? e.value.map((a, i) => {
              return (
                <div className="col-6" key={i}>
                  <FormControlLabel
                    control={
                      <Checkbox
                        key={i}
                        checked={listIdCheckedVTHN.includes(String(a.id))}
                        onChange={() => {
                          let checkExist = listIdCheckedVTHN.includes(
                            String(a.id)
                          );
                          let listTemp = listIdCheckedVTHN;
                          if (checkExist) {
                            let x1 = listTemp.filter((x) => x !== String(a.id));
                            dispatch(commonActions.setListIdCheckedVTHN(x1));
                          } else {
                            let x2 = [...listTemp, a.id];
                            dispatch(commonActions.setListIdCheckedVTHN(x2));
                          }
                        }}
                        value={a?.id || ""}
                      />
                    }
                    label={a?.rut_gon}
                  />
                </div>
              );
            })
          : null}
      </div>
    );
  };
  return (
    <div className="row">
      {props?.hidePhamVi ? null : (
        <div className="col-12 mb-8">
          <label className="text-primary required fw-bold">
            Phạm vi chuyên môn
          </label>
          <div className="row">
            {/* <div className="row" style={{ pointerEvents: roleUser?.ten_en == "duocsi" ? 'auto' : 'none', opacity: roleUser?.ten_en == "duocsi" ? '1' : '0.7' }}> */}
            {ListPVCM && ListPVCM.length > 0 ? (
              <>
                {ListPVCM.map((e, i) => {
                  return (
                    <div className="col-6" key={i}>
                      <FormControlLabel
                        control={
                          <Checkbox
                            key={`chilđren${e.id}`}
                            checked={ListIdCheckedPVCM.includes(String(e.id))}
                            onChange={() => {
                              let checkExist = ListIdCheckedPVCM.includes(
                                String(e.id)
                              );
                              let listTemp = ListIdCheckedPVCM;
                              if (checkExist) {
                                let x1 = listTemp.filter(
                                  (x) => x !== String(e.id)
                                );
                                dispatch(
                                  commonActions.setListIdCheckedPVCM(x1)
                                );
                              } else {
                                let x2 = [...listTemp, e.id];
                                dispatch(
                                  commonActions.setListIdCheckedPVCM(x2)
                                );
                              }
                            }}
                            value={e?.id || ""}
                          />
                        }
                        label={e?.ten}
                      />
                    </div>
                  );
                })}
                {props?.hideGhiChu ? null : (
                  <>
                    <span className="font-weight-bold my-2">Ghi chú:</span>
                    <Form.Control
                      rows={3}
                      as="textarea"
                      className="mx-2"
                      placeholder="Nhập ghi chú"
                      style={{ fontSize: 14, fontWeight: 400 }}
                    />
                  </>
                )}
              </>
            ) : null}
          </div>
        </div>
      )}

      {props?.hideViTri ? null : (
        <div className="col-12 mb-4">
          <label className="text-primary required fw-bold mb-1">
            Vị trí hành nghề
          </label>
          <div className="row">
            {/* <div className="row" style={{ pointerEvents: roleUser?.ten_en == "duocsi" ? 'auto' : 'none', opacity: roleUser?.ten_en == "duocsi" ? '1' : '0.7' }}> */}
            {ListVTHN && ListVTHN.length > 0 ? (
              <>
                {ListVTHN?.map((e, i) => {
                  return <div key={i}>{renderCheckbox(e)}</div>;
                })}
                {props?.hideGhiChu ? null : (
                  <>
                    <span className="font-weight-bold my-2">Ghi chú:</span>
                    <Form.Control
                      rows={3}
                      as="textarea"
                      className="mx-2"
                      placeholder="Nhập ghi chú"
                      style={{ fontSize: 14, fontWeight: 400 }}
                    />
                  </>
                )}
              </>
            ) : null}
          </div>
        </div>
      )}
    </div>
  );
}
