/**
 * High level router.
 *
 * Note: It's recommended to compose related routes in internal router
 * components (e.g: `src/app/modules/Auth/pages/AuthPage`, `src/app/BasePage`).
 */

import { FC, useEffect, useState } from "react";
import "react-block-ui/style.css";
import { Toast } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { BrowserRouter, Navigate, Route, Routes } from "react-router-dom";
import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { fetchToken, onMessageListener } from "../../firebase";
import { App } from "../App";
import { Logout } from "../modules/apps/auth";
import { ForgotPassword } from "../modules/apps/auth/components/ForgotPassword";
import { Login } from "../modules/apps/auth/Login";
import { DangKy } from "../modules/apps/dang-ky/DangKy";
import { In } from "../modules/apps/In/In";
import { ErrorsPage } from "../modules/errors/ErrorsPage";
import { selectCurrentUser } from "../redux-module/auth/authSlice";
import { notiActions } from "../redux-module/noti/notiSlice";
import { PrivateRoutes } from "./PrivateRoutes";

/**
 * Base URL of the website.
 *
 * @see https://facebook.github.io/create-react-app/docs/using-the-public-folder
 */
const { PUBLIC_URL } = process.env;

const AppRoutes: FC = () => {
  // const { currentUser } = useAuth();
  const [show, setShow] = useState(false);
  const [notification, setNotification] = useState({ title: "", body: "" });
  const [isTokenFound, setTokenFound] = useState(false);
  const currentUser = useSelector(selectCurrentUser);
  const dispatch = useDispatch();

  useEffect(() => {
    fetchToken(setTokenFound);
    if (currentUser) {
      dispatch(notiActions.getNoti(currentUser?.id));
    }
  }, []);

  onMessageListener()
    .then((payload) => {
      //   setNotification({
      //     title: payload.notification.title,
      //     body: payload.notification.body,
      //   });

      console.log("onMessageListener", payload);
      toast.success(payload.notification.body, {
        position: "top-right",
        autoClose: 1000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        toastId: "success",
      });
    })
    .catch((err) => console.log("failed: ", err));

  const onShowNotificationClicked = () => {
    setNotification({
      title: "Notification",
      body: "This is a test notification",
    });
    setShow(true);
  };
  return (
    <BrowserRouter basename={PUBLIC_URL}>
      <Routes>
        <Route element={<App />}>
          <Route path="in-ho-so" element={<In />} />
          <Route path="dang-nhap/*" element={<Login />} />

          <Route path="error/*" element={<ErrorsPage />} />
          <Route path="logout" element={<Logout />} />
          <Route path="dang-ky" element={<DangKy />} />
          <Route path="dang-nhap" element={<Login />} />
          <Route path="forgot-password" element={<ForgotPassword />} />
          {currentUser ? (
            <>
              <Route path="/*" element={<PrivateRoutes />} />
            </>
          ) : (
            <>
              <Route path="dang-nhap/*" element={<Login />} />
              <Route path="*" element={<Navigate to="/dang-nhap" />} />
            </>
          )}
        </Route>
      </Routes>
      <ToastContainer
        hideProgressBar
        pauseOnFocusLoss
        draggable
        pauseOnHover
        limit={1}
      />
    </BrowserRouter>
  );
};

export { AppRoutes };
