export default function LyDoHeader(props) {
  var trangThai = "";
  if (props?.thongTinHoSo?.trang_thai_ho_so) {
    trangThai = props?.thongTinHoSo?.trang_thai_ho_so;
  } else if (props?.thongTinHoSo?.yeu_cau_cchnd?.trang_thai_ho_so) {
    trangThai = props?.thongTinHoSo?.yeu_cau_cchnd?.trang_thai_ho_so;
  }

  switch (trangThai) {
    case "3":
      return (
        <div
          className="card-box py-4 px-6 mx-3 my-3"
          style={{
            color: "#a94442",
            borderColor: "#ebccd1",
            backgroundColor: "#f2dede",
          }}
        >
          <div className="col">
            <strong>Lý do từ chối: </strong>
            <span>
              {props?.thongTinHoSo?.yeu_cau_cchnd?.ly_do_tu_choi_chuyen_vien}
            </span>
          </div>
        </div>
      );
    case "7":
      return (
        <div
          className="card-box py-4 px-6 mx-3 my-3"
          style={{
            color: "#a94442",
            borderColor: "#ebccd1",
            backgroundColor: "#f2dede",
          }}
        >
          <div className="col">
            <strong>Lý do từ chối: </strong>
            <span>
              {props?.thongTinHoSo?.yeu_cau_cchnd?.ly_do_tu_choi_lanh_dao}
            </span>
          </div>
        </div>
      );
    case "5":
      return (
        <div
          className="card-box py-4 px-6 mx-3 my-3"
          style={{
            color: "#806027",
            borderColor: "#fdebcd",
            backgroundColor: "#fdf1db",
          }}
        >
          <div className="col">
            <strong>Yêu cầu bổ sung: </strong>
            <div>
              {
                props?.thongTinHoSo?.yeu_cau_cchnd
                  ?.noi_dung_yeu_cau_bo_sung_chuyen_vien
              }
            </div>
          </div>
        </div>
      );
    case "8":
      return (
        <div
          className="card-box py-4 px-6 mx-3 my-3"
          style={{
            color: "#806027",
            borderColor: "#fdebcd",
            backgroundColor: "#fdf1db",
          }}
        >
          <div className="col">
            <strong>Yêu cầu bổ sung: </strong>
            <span>
              {
                props?.thongTinHoSo?.yeu_cau_cchnd
                  ?.noi_dung_yeu_cau_bo_sung_lanh_dao
              }
            </span>
          </div>
        </div>
      );

    default:
      return null;
  }
}
